import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import { Form, Card, Descriptions, Table, Modal, Rate, Tag, Row, Col, Icon, Input, Button } from 'antd';
import Highlighter from 'react-highlight-words';

class Dashboard extends Component {
    
    componentWillMount(){
        this.setState({
            data : [],
            count : []
        })
        axios.get('http://localhost:5000/leave/find/'+4, 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("res", res.data);
            this.setState({
                data : (res.data).reverse()
            })
        }) 

        axios.get('http://localhost:5000/leave_count/profile/'+4, 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("res", res.data);
            this.setState({
                count : res.data
            })
        }) 
    } 

    componentDidMount() {
        console.log(this.state.count.special)
    }

    state = {
        data : [],
        count : [],
        leave : []
    }

    showModal = (leave) => {
        this.setState({
          visible: true,
          leave: leave
        });
      };
    
      handleOk = e => {
        console.log(e);
        this.setState({
          visible: false,
        });
    };

    getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.state.searchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });

      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.setState({ searchText: selectedKeys[0] });
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.setState({ searchText: '' });
      };

    render() {

        const {leave} = this.state;

        const columns = [ 
            
            {
                title: 'Date of request',
                dataIndex: 'formatDateTime',
                ...this.getColumnSearchProps('formatDateTime'),
            },
            {
              title: 'Leave Type',
              dataIndex: 'leave_type',
              render: type => (
               type.type
              )
            },
            {
                title: 'Start Date',
                dataIndex: 'start_date'
            },
            {
                title: 'End Date',
                dataIndex: 'end_date'
            },
            {
                title: 'Number of days',
                dataIndex: 'number_of_leave_days'
            },
            {
                title: 'Status',
                dataIndex: 'status',
                render: tag => {
                    let color = tag.length > 5 ? 'geekblue' : 'green';
                    if (tag === 'Pending') {
                      color = 'green';
                    }else if (tag === 'Approved') {
                      color = 'geekblue';
                    }else {
                      color = 'red';
                    }
                    return (
                      <Tag color={color} key={tag}>
                        {tag}
                      </Tag>
                    );
                  }
                // ...this.getColumnSearchProps('status')
            },
            {
              dataIndex: '',
              key: 'z',
              width: '1%',
              render: (emp) => <Icon type="info-circle" onClick={this.showModal.bind(this, emp)}  theme="twoTone" />,
            }
        ];

        return (
            <div className='center'>
                <Card hoverable='true'>
                <Row>
                    <Col span={8}><Tag color="geekblue">Casual</Tag> &nbsp; <Tag color="volcano">{this.state.count.casual}/7</Tag></Col>
                    <Col span={8}><Tag color="geekblue">Medical</Tag> &nbsp; <Tag color="volcano">{this.state.count.medical}/7</Tag></Col>
                    <Col span={8}><Tag color="geekblue">Annual</Tag> &nbsp; <Tag color="volcano">{this.state.count.annual}/14</Tag></Col>
                </Row>
                
                    {this.state.count.length !== 0 ? <Descriptions>
                        <Descriptions.Item><Rate allowHalf defaultValue={this.state.count.casual} count={7} disabled={true} style={{color:'#FC7F03'}} character={<Icon type="bulb" theme="filled" />}/></Descriptions.Item>
                        <Descriptions.Item><Rate allowHalf defaultValue={this.state.count.medical} count={7} disabled={true} style={{color:'#FC7F03'}} character={<Icon type="bulb" theme="filled" />}/></Descriptions.Item>
                        <Descriptions.Item><Rate allowHalf defaultValue={this.state.count.annual} count={14} disabled={true} style={{color:'#FC7F03'}} character={<Icon type="bulb" theme="filled" />}/></Descriptions.Item>
                    </Descriptions> : null}
                </Card>
                <br/>

                <Card hoverable='true' >
                    <Table rowKey={record => record.leave_id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 7 }} size="middle" />
                </Card>
                {leave.length!==0? <Modal
                  title="Full Leave Record"
                  visible={this.state.visible}
                  onOk={this.handleOk}
                  onCancel={this.handleOk}
                  footer={null}
                >
                  <p>Date of Request: <Tag color="volcano">{leave.formatDateTime}</Tag> Leave Type: <Tag color="volcano">{leave.leave_type.type}</Tag></p>
                  <p>Leave Period: <Tag color="volcano">{leave.start_date} to {leave.end_date}</Tag> Number of Days: <Tag color="volcano">{leave.number_of_leave_days}</Tag></p>
                  <p>Status: <Tag color="volcano">{leave.status}</Tag> </p>
                  <p>Duty cover by: <Tag color="volcano">{leave.duty.first_name} {leave.duty.second_name}</Tag></p>
                  <p>Special note: <Tag color="volcano">{leave.special_notes}</Tag></p>
                  <p>If reject, reason: <Tag color="volcano">{leave.reject}</Tag></p>
                </Modal>: null}
            </div>
        )
    }
}    

const Dash = Form.create({name: "dashboard"})(Dashboard);

export default Dash;

/* <Row gutter={16}>
                <Col span={8}>    
                <Progress type="circle" percent={75} format={percent => `${percent} Days`}/>
                <br/>
                <span>Annual Leave</span>
                </Col>
                <Col span={8}>
                <Progress type="circle" percent={50} />
                <br/>
                <span>Casual Leave</span>
                </Col>
                <Col span={8}>
                <Progress type="circle" percent={100} />
                <br/>
                <span>Medical Leave</span>
                </Col>
                </Row> */
