import React from 'react';
import { Link, Switch, Route } from 'react-router-dom';
import 'antd/dist/antd.css';
import './index.css';
import { Layout, Menu, Icon, Form, Card } from 'antd';
import Dashboard from './Dashboard';
import LeavesHistory from '../components/Leave/ReadLeaves';
import AddEmployee from '../components/Employee/AddEmployee';
import RequestLeave from '../components/Leave/RequestLeave';
import PendingLeaves from '../components/Leave/PendingLeaves';
import ViewEmployee from '../components/Employee/ViewEmployee';
import ViewProfile from '../components/Leave/ViewProfile';
import History from '../components/Leave/History';
import ChangePassword from '../components/Settings/ChangePassword';
import LeaveCalender from '../components/Settings/LeaveCalender';
import ContactsNumber from '../components/Settings/ContactsNumber';
import Header from './Header';
import axios from 'axios';
import ViewOne from './../components/Employee/ViewOne';
import Attendence from './../components/Attendence/Attendence';
import OneAttendence from './../components/Attendence/OneAttendence';
import {Redirect} from 'react-router-dom';

const { Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

class SiderDemo extends React.Component {
  
  componentWillMount () {
    axios.get('http://localhost:5000/api/auth/correct', 
      {
          headers: {
              Authorization: 'Bearer ' + localStorage.getItem("header")
          }
      })
      .then(res => {
          if(res.data === true){                      
            this.setState({redirectToReferrer: 1}); 
          }else{
            this.setState({redirectToReferrer: 0});
          }
      }).catch(e => {
          this.setState({redirectToReferrer: 0});
      })

    if(localStorage.getItem("header") !== null){ 

      axios.get('http://localhost:5000/api/auth/info',
        {
          headers: {
            Authorization: 'Bearer ' + localStorage.getItem("header")
          }
        })
        .then(res => {
            this.setState({
                user_name: res.data.firstName+ " " + res.data.secondName,
                user_role: res.data.role,
                user_department: res.data.department 
            })
        }).catch(e => {
          console.log(e);
        });
    }

  }

  state = {
    redirectToReferrer: null,
    visible: false,
    confirmDirty: false,
    user_name: null,
    user_role: null,
    user_department: null
  };

  render() {
    const {redirectToReferrer, user_name, user_role, user_department} = this.state;

    if (redirectToReferrer === 0) {
      return <Redirect to='/login'/>
    }else if (redirectToReferrer === 1){
    return (
      <Layout style={{ minHeight: '100vh' }}>
        <Sider 
          style={{
            overflow: 'auto',
            height: '100vh',
            position: 'fixed',
            left: 0,
            width: '250'
          }}
        >
          <div className="logo" >&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
          <img height= '35px' alt="example" src="https://rakiya.lk/files/recruiters/1805/logo.png" />
          </div>
          <br/>
          <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
            
            <Menu.Item key="1">
              <Link to='/'/>
              <Icon type="home" />
              <span>Home</span>
            </Menu.Item>
          { user_role === "Admin"?
            <Menu.Item key="2">
                <Link to='/manage_employee'/>
                <Icon type="team" />
                <span>Manage Employee</span>
            </Menu.Item>
            :null
          }
            <SubMenu
              key="sub1"
              title={
                <span>
                  <Icon type="export" />
                  <span>Leave</span>
                </span>
              }
            >
            <Menu.Item key="3">
                <Link to='/request_leave'/>
                <Icon type="paper-clip" />
                <span>Request Leave</span>
            </Menu.Item>
          { user_role === "Admin" || user_role === "Supervisor"?
            
              <Menu.Item key="4">
                  <Link to='/leave_history'/>
                  <Icon type="folder" />
                  <span>Review Leaves</span>
              </Menu.Item>
            :null
          }
          { user_role === "Admin" || user_role === "Supervisor"?
              <Menu.Item key="5">
                  <Link to='/pending_leaves'/>
                  <Icon type="question-circle" />
                  <span>Pending Leaves</span>
              </Menu.Item>
          :null
          }
            <Menu.Item key="14">
                <a href='http://leaves.vizuamatix.com:6077/Docs/LeavePolicy.pdf' target='_blank' rel="noopener noreferrer">
                <Icon type="file-protect" />
                <span>Leave Policy</span></a>
            </Menu.Item>
            </SubMenu>
            { user_role === "Admin" ?
              <Menu.Item key="6">
                  <Link to='/view_profile'/>
                  <Icon type="team" />
                  <span>View Profiles</span>
              </Menu.Item>
            :null
            }
            { user_role === "Admin" ?
              <Menu.Item key="7">
                  <Link to='/history'/>
                  <Icon type="clock-circle" />
                  <span>History</span>
              </Menu.Item>
            :null
            }
            { user_role === "Admin" ?
              <Menu.Item key="12">
                  <Link to='/attendence'/>
                  <Icon type="solution" />
                  <span>Attendence Sheet</span>
              </Menu.Item>
            :null
            } 
            <SubMenu
              key="sub2"
              title={
                <span>
                  <Icon type="setting" />
                  <span>Settings</span>
                </span>
              }
            >
              <Menu.Item key="8">
                <Link to='/contact_number'/>
                <Icon type="phone" />
                <span>Contact Numbers</span>
              </Menu.Item>
              <Menu.Item key="9">
                <Link to='/change_password'/>
                <Icon type="lock" />
                <span>Change Password</span>
              </Menu.Item>
              { user_role === "Admin" ?
                <Menu.Item key="11">
                  <Link to='/leave_calender'/>
                  <Icon type="calendar" />
                  <span>Leave Calendar</span>
                </Menu.Item>
              :null
              }
            </SubMenu>
          </Menu>
        </Sider>
        <Layout style={{ marginLeft: 200 }}>
          
          <Layout style={{ position: 'fixed', zIndex: 1, width: '87%', height: '50px' }}>
            <Card hoverable='true'> 
              <Header name={user_name} role={user_role} department={user_department}/>
            </Card>
          </Layout>

          <Layout style={{marginTop:'165px'}} >
          <Content style={{ margin: '20px', paddingTop: '20PX' }}>
              <Switch>
                <Route exact path='/' component={Dashboard} />
                <Route path='/leave_history' component={LeavesHistory} />
                <Route path='/history' component={History} />
                <Route path='/dashboard' component={Dashboard} />
                <Route path='/add_employee' component={AddEmployee} />
                <Route path='/request_leave' component={RequestLeave} />
                <Route path='/pending_leaves' component={PendingLeaves} />
                <Route path='/manage_employee' component={ViewEmployee} />
                <Route path='/change_password' component={ChangePassword} />
                <Route path='/view_profile' component={ViewProfile} />
                <Route path='/get_employee' component={ViewOne} /> 
                <Route path='/leave_calender' component={LeaveCalender}/>
                <Route path='/contact_number' component={ContactsNumber}/>
                <Route path='/attendence' component={Attendence}/>
                <Route path='/one_attendence' component={OneAttendence}/>
              </Switch>
          </Content>
          
          <Footer style={{ textAlign: 'center', height: '0px'}}>VX Leave Management System ©2019 Created by Pramuditha</Footer>
          </Layout>
        </Layout>
      </Layout>
    );
  }else {
    return null
  }
  }
}

