import React, { Component } from 'react';
import axios from 'axios';
import Table from 'react-bootstrap/Table';
import 'antd/dist/antd.css';
import { AutoComplete } from 'antd';

const loginStyle = {
    width: "100%",
    maxWidth: "1300px",
    margin: "30px auto",
    border: "5px solid #ddd",
    borderRadius: "3px",
    padding: "10px"
}

function AllUsers(props) {
    return(
        props.data.map(item => (
        <React.Fragment key={item.id}>
        <tr>
        <td> {item.id} </td>
        <td> {item.name} </td>
        <td> {item.salary} </td>
        </tr>
        </React.Fragment>
        ))
    )
}

function SelectUser(props) {
    return(
        <tr>
        <td> {pr.filter.id} </td>
        <td> {this.state.filter.name} </td>
        <td> {this.state.filter.salary} </td>
        </tr>
    )
}

function Greeting(props) {
    const isUser = props.user;
    if (isUser) {
      return <SelectUser />;
    }
    return <AllUsers />;
  }

export default class Dashboard extends Component {
    
    componentDidMount(){
        axios.get('http://localhost:5000/all')
        .then(res => {
            console.log("res", res.data);
            this.setState({
                data : res.data
            })
        }) 
    } 

    state = {
        user : false,
        value : '',
        dataSource : [],
        data : [],
        filter : []
    }
   
    onSelect = (value) => {
        const data = this.state.data;
        let len = data.length;
        for(var i=0; i<len; i++){
          var string = data[i]["name"];
          if(value===string){
            console.log('onSelect',data[i]);
            this.setState({
                user : true,
                filter : data[i]
              });
          }}
        
      }
    
      onSearch = searchText => {
        const data = this.state.data;
        let len = data.length;
        var arr = [];
        for(var i=0; i<len; i++){
          var re = new RegExp(searchText, 'gi');
          var string = data[i]["name"];
          if(string.match(re)){
            arr.push(string);
          }}
        this.setState({
          dataSource: arr
        });
        console.log("data source",this.state.dataSource)
      };
    
      onChange = value => {
        this.setState({ value });
      };

    render() {
        const { dataSource, value } = this.state;            
        return (
            
            <div style={loginStyle} className="white card z-depth-0 card-content center">
                
                    <div>
                        <AutoComplete
                        value={value}
                        dataSource={dataSource}
                        style={{ width: 200 }}
                        onSelect={this.onSelect}
                        onSearch={this.onSearch}
                        onChange={this.onChange}
                        placeholder="Filter Here"
                        />
                    </div>
                    <Table striped bordered hover className='notification-list msgact notf2'>
                        <thead>
                           
                        <tr>
                            <td><h6>Id</h6></td>
                            <td><h6>Name</h6></td>
                            <td><h6>Salary</h6></td>
                        </tr>
                        </thead>
                        <tbody>
                        <Greeting/>
                        
                        </tbody>
                    </Table>
                </div>
        )
    }
}
