import React from 'react';
import { Link, Switch, Route } from 'react-router-dom';
import 'antd/dist/antd.css';
import './index.css';
import { Layout, Menu, Icon, Dropdown, Avatar, Drawer, Form, Button, Row, Input } from 'antd';
import Signup from '../auth/Signup';
import Login from '../auth/Login';
import Dashboard from './Dashboard';
import Profile from './Profile';
import Details from '../components/Employee/Details';
import LeavesHistory from '../components/Leave/ReadLeaves';
import AddEmployee from '../components/Employee/AddEmployee';
import RequetLeave from '../components/Leave/RequetLeave';
import PendingLeaves from '../components/Leave/PendingLeaves';
import ViewEmployee from '../components/Employee/ViewEmployee';
import Pdf from './Pdfs';
import axios from 'axios';
import swal from 'sweetalert';

const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

class SiderDemo extends React.Component {
  
  state = {
    collapsed: false,
    redirectToReferrer: false,
    visible: false
  };

  onCollapse = collapsed => {
    console.log(collapsed);
    this.setState({ collapsed });
  };
  
  handleClick = () => {
    this.setState({redirectToReferrer: true});
    localStorage.removeItem("user");
  }

  showDrawer = () => {
    this.setState({
      visible: true
    });
  };

  onClose = () => {
    this.setState({
      visible: false
    });
  };

  changePassword = e => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        console.log("Received values of form: ", values);

        var passwordHash = require('password-hash');
        var hashedPassword = passwordHash.generate(values.newpassword);
        const user_id = localStorage.getItem("id");

        axios.get('http://localhost:5000/change/'+user_id)
        .then(res => {
            if(passwordHash.verify(values.password, res.data.password)){
              const user = {
                user_id : user_id || undefined,
                password: hashedPassword || undefined
              }
              axios.post('http://localhost:5000/changepassword',user)
              .then(res => {
                if(res.data){
                  swal("Yeah!","Password Updated !!!","success");
                  this.setState({
                    visible: false
                  });
                }
                else{
                  swal("Oops!","Something Went Wrong!!!","error"); 
                }
              }).catch(e => console.log("eeeeeeeeeeee",e))
            }
            else{
              swal("Oops!","Invalid Current Password !!!","warning");
            }
        }).catch(e => {
            console.log("eeeeeeeeeeee",e)
            swal("Oops!","Something Went Wrong!!!","error");          
          });
      }
    });
  }

  handleConfirmBlur = e => {
    const { value } = e.target;
    this.setState({ confirmDirty: this.state.confirmDirty || !!value });
  };

  compareToFirstPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && value !== form.getFieldValue("newpassword")) {
      callback("Two passwords that you enter is inconsistent!");
    } else {
      callback();
    }
  };

  validateToNextPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && this.state.confirmDirty) {
      form.validateFields(["confirm"], { force: true });
    }
    callback();
  };

  render() {
    const {redirectToReferrer, visible} = this.state;
    const { getFieldDecorator } = this.props.form;
    const menu = (
      <Menu>
        <Menu.Item onClick={this.showDrawer}>
          Change Password
        </Menu.Item>
        <Menu.Item onClick={this.handleClick}>
          Log Out
        </Menu.Item>
      </Menu>
    );
    const user = localStorage.getItem("user");
    if (redirectToReferrer) {
      return (<Login/>)
    }
    else{
    return (
      <Layout style={{ minHeight: '100vh' }}>
        <Sider 
          collapsible collapsed={this.state.collapsed} 
          onCollapse={this.onCollapse}
        >
          <div className="logo" >&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
          <img height= '35px' alt="example" src="https://rakiya.lk/files/recruiters/1805/logo.png" />
          </div>
          <br/>
          <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
            
            <Menu.Item key="1">
              <Link to='/dashboard'/>
              <Icon type="home" />
              <span>Home</span>
            </Menu.Item>
            <Menu.Item key="2">
                <Link to='/add_employee'/>
                <Icon type="user-add" />
                <span>Add Employee</span>
            </Menu.Item>
            
            <SubMenu
              key="sub1"
              title={
                <span>
                  <Icon type="export" />
                  <span>Leave</span>
                </span>
              }
            >
            <Menu.Item key="3">
                <Link to='/request_leave'/>
                <Icon type="paper-clip" />
                <span>Request Leave</span>
            </Menu.Item>
            <Menu.Item key="4">
                <Link to='/leave_history'/>
                <Icon type="folder" />
                <span>Leaves History</span>
            </Menu.Item>
            <Menu.Item key="5">
                <Link to='/pending_leaves'/>
                <Icon type="question-circle" />
                <span>Pending Leaves</span>
            </Menu.Item>
            <Menu.Item key="5">
                <Link to='/pending_leaves'/>
                <Icon type="file-protect" />
                <span>Leave Policy</span>
            </Menu.Item>
            </SubMenu>
            <Menu.Item key="6">
                <Link to='/view_employee'/>
                <Icon type="team" />
                <span>View Profiles</span>
            </Menu.Item>
            <Menu.Item key="7">
              <Link to='/pdf'/>
              <Icon type="download" />
              <span>Download Details</span>
            </Menu.Item>
            <Menu.Item key="10">
                <Link to='/calendar'/>
                <Icon type="schedule" />
                <span>Calendar</span>
            </Menu.Item>
            <SubMenu
              key="sub2"
              title={
                <span>
                  <Icon type="setting" />
                  <span>Settings</span>
                </span>
              }
            >
              <Menu.Item key="8">
                <Link to='/update'/>
                <Icon type="phone" />
                <span>Contact Numbers</span>
              </Menu.Item>
              <Menu.Item key="9">
                <Link to='/delete'/>
                <Icon type="lock" />
                <span>Change Password</span>
              </Menu.Item>
              <Menu.Item key="11">
                <Link to='/delete'/>
                <Icon type="calendar" />
                <span>Leave Calendar</span>
              </Menu.Item>
              <Menu.Item key="12">
                <Link to='/delete'/>
                <Icon type="solution" />
                <span>Attendence Sheet</span>
              </Menu.Item>
            </SubMenu>
          </Menu>
        </Sider>
        <Layout>
          <Header style={{ background: 'gray', padding: '0px'}} >
            <nav className="" style={{backgroundColor:"#00183A", height: '80px'}}>
            <div>
              
            <h5 className="brand-logo center" style={{paddingTop:'20px'}}>VX Leave Management System</h5>
            </div>
            <ul className="right" style={{paddingTop:'10px'}}>
              <li>
              <Avatar style={{ backgroundColor: '#FD6A02', verticalAlign: 'middle' }} size="large">
                {user}
              </Avatar>
              </li>
              <li>
              &nbsp;&nbsp;&nbsp;&nbsp;
              </li>
              <li>
                <Dropdown overlay={menu} placement="bottomRight">
                  <Icon type="menu" />  
                </Dropdown>&nbsp;&nbsp;&nbsp;&nbsp;
              </li>
            </ul>
            </nav>
          </Header>
          <Content style={{ margin: '20px', paddingTop: '20PX' }}>
              <Switch>
                <Route path='/signup' component={Signup} />
                <Route path='/profile' component={Profile} />
                <Route path='/details' component={Details} />
                <Route path='/leave_history' component={LeavesHistory} />
                <Route path='/pdf' component={Pdf} />
                <Route exact path='/' component={Dashboard} />
                <Route path='/dashboard' component={Dashboard} />
                <Route path='/add_employee' component={AddEmployee} />
                <Route path='/request_leave' component={RequetLeave} />
                <Route path='/pending_leaves' component={PendingLeaves} />
                <Route path='/view_employee' component={ViewEmployee} />
              </Switch>
            <div>
          <Drawer
          title="Change Password"
          width={300}
          onClose={this.onClose}
          visible={visible}
          >
          <Form layout="vertical" hideRequiredMark>
            
            <Row gutter={16}>
              <Form.Item hasFeedback>
                {getFieldDecorator("password", {
                  rules: [
                    {
                      required: true,
                      message: "Please input your current password!"
                    },
                    {
                      validator: this.validateToNextPassword
                    }
                  ]
                })(<Input.Password 
                  id= '1'
                  prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                  placeholder="Current Password"
                  />)}
              </Form.Item>
            </Row>

            <Row gutter={16}>
              <Form.Item hasFeedback>
                {getFieldDecorator("newpassword", {
                  rules: [
                    {
                      required: true,
                      message: "Please input your new password!"
                    },
                    {
                      validator: this.validateToNextPassword
                    }
                  ]
                })(<Input.Password 
                  id= '2'
                  prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                  placeholder="New Password"
                  />)}
              </Form.Item>
            </Row>

            <Row gutter={16}>
            
              <Form.Item hasFeedback>
                {getFieldDecorator("confirm", {
                  rules: [
                    {
                      required: true,
                      message: "Please confirm your new password!"
                    },
                    {
                      validator: this.compareToFirstPassword
                    }
                  ]
                })(<Input.Password 
                  id= '3'
                  onBlur={this.handleConfirmBlur}
                  prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                  placeholder="Confirm Password"
                />)}
              </Form.Item>
            </Row>
          </Form>
          <div
            style={{
              position: 'absolute',
              left: 0,
              bottom: 0,
              width: '100%',
              borderTop: '1px solid #e9e9e9',
              padding: '10px 16px',
              background: '#fff',
              textAlign: 'right',
            }}
          >
            <Button onClick={this.onClose} style={{ marginRight: 8 }}>
              Cancel
            </Button>
            <Button onClick={this.changePassword} type="primary">
              Submit
            </Button>
          </div>
        </Drawer>
        </div>
          </Content>
          
          <Footer style={{ textAlign: 'center', height: '0px'}}>VX Leave Management System ©2019 Created by Pramuditha</Footer>
        </Layout>
      </Layout>
    );}
  }
}

const Main = Form.create()(SiderDemo);

export default Main;

