import React from 'react';
import 'antd/dist/antd.css';
import {withRouter} from 'react-router-dom';
import { PageHeader, Icon, Button, Modal } from 'antd';

const { confirm } = Modal;

class Header extends React.Component { 

    handleLogout = (e) => {
        e.preventDefault();
        console.log(this.props)
        confirm({
          title: 'Do you want to logout?',
          onOk: () => {
            localStorage.removeItem("user_id");
            localStorage.removeItem("user_name");
            localStorage.removeItem("user_role");
            localStorage.removeItem("header");
            this.props.history.push('/login')
          },
          onCancel() {
            console.log('Cancel');
          },
        });
    }

    render () {
        const routes = [
            {
                path: '/',
                breadcrumbName: "Home",
            },
            {
                path: 'first',
                breadcrumbName: 'Second-level Menu',
            },
            {
                path: 'second',
                breadcrumbName: 'Third-level Menu',
            },
        ];

    return (
        <PageHeader
            title={localStorage.getItem()}
            style={{
            background: 'white',    
            border: '1px solid rgb(235, 237, 240)',
            }}
            subTitle="Intern"
            extra={[
            <Button key="1" type="primary" onClick={this.handleLogout} shape="circle">
               <Icon type="logout" />
            </Button>
            ]}
            
            avatar={{ src: 'https://avatars1.githubusercontent.com/u/8186664?s=460&v=4', size: '100px'}}
            breadcrumb={{ routes }}
        >
        </PageHeader>
        );
    }
}          

export default withRouter(Header);
