import React from 'react';
import axios from 'axios';
import swal from 'sweetalert';
import 'antd/dist/antd.css';
import {
  Form,
  Input,
  Tooltip,
  Icon,
  Select,
  Button,
  AutoComplete,
  DatePicker,
  Radio,
  Card,
  Row,
  Col
} from 'antd';

const { Option } = Select;
const AutoCompleteOption = AutoComplete.Option;

class AddEmployee extends React.Component {
  state = {
    confirmDirty: false,
    autoCompleteResult: []
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        console.log('Received values of form: ', values);

        var passwordHash = require('password-hash');
        var hashedPassword = passwordHash.generate("123");

        const employee = {
          first_name: values.first_name || undefined,
          second_name: values.second_name || undefined,
          initials: values.initials || undefined,
          gender: values.gender || undefined,
          email: values.email || undefined,
          residence: values.residence || undefined,
          contact: values.contact || undefined,
          role: {role_id: values.role} || undefined,
          department: {department_id: values.department} || undefined,
          supervisor1: values.supervisor1 || undefined,
          supervisor2: values.supervisor2 || undefined,
          join_date: values.join_date._d.toDateString() || undefined,
          confirm_date: values.confirm_date._d.toDateString() || undefined,
          password: hashedPassword || undefined
        }

        console.log('Values of object: ', employee);

        axios.post(
          'http://localhost:5000/employee/add', 
          employee, 
          { 
              headers: {
                  Authorization: 'Bearer ' + localStorage.getItem("header")
              }
          }
        )
        .then(res => {
          console.log("res", res.data);
          swal("Yeah!","Employee Added!!!","success"); 
          // this.props.history.push('/manage_employee');
        })
      this.props.close();
      }
    });
  };

  handleConfirmBlur = e => {
    const { value } = e.target;
    this.setState({ confirmDirty: this.state.confirmDirty || !!value });
  };

  compareToFirstPassword = (value, callback) => {
    const { form } = this.props;
    if (value && value !== form.getFieldValue('password')) {
      callback('Two passwords that you enter is inconsistent!');
    } else {
      callback();
    }
  };

  validateToNextPassword = (value, callback) => {
    const { form } = this.props;
    if (value && this.state.confirmDirty) {
      form.validateFields(['confirm'], { force: true });
    }
    callback();
  };

  handleWebsiteChange = value => {
    let autoCompleteResult;
    if (!value) {
      autoCompleteResult = [];
    } else {
      autoCompleteResult = ['.com', '.org', '.net'].map(domain => `${value}${domain}`);
    }
    this.setState({ autoCompleteResult });
  };

  render() {
    const { getFieldDecorator } = this.props.form;
    const { autoCompleteResult } = this.state;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
    };
    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 24,
          offset: 0,
        },
        sm: {
          span: 16,
          offset: 8,
        },
      },
    };

    const websiteOptions = autoCompleteResult.map(website => (
      <AutoCompleteOption key={website}>{website}</AutoCompleteOption>
    ));

    return (
        <div>
        <Card bordered={false} style={{ height: '1000px'}}>  
        <Form {...formItemLayout}>
        
        <Form.Item label="First Name">
          {getFieldDecorator('first_name', {
            rules: [{ required: true, message: 'Please input first name!' }],
          })(<Input />)}
        </Form.Item>
        <Form.Item label="Second Name">
          {getFieldDecorator('second_name', {
            rules: [{ required: true, message: 'Please input second name!' }],
          })(<Input />)}
        </Form.Item>
        <Form.Item label="Initials">
          {getFieldDecorator('initials', {
            rules: [{ required: true, message: 'Please input initials!' }],
          })(<Input />)}
        </Form.Item>
        <Form.Item label="Gender">
          {getFieldDecorator('gender', {
            rules: [{ required: true, message: 'Please input initials!' }],
          })(
          <Radio.Group name="radiogroup">
          <Radio value={1}>Male</Radio>
          <Radio value={2}>Female</Radio>
          </Radio.Group>
        )}
        </Form.Item>       
        <Form.Item label="E-mail">
          {getFieldDecorator('email', {
            rules: [
              {
                type: 'email',
                message: 'The input is not valid E-mail!',
              },
              {
                required: true,
                message: 'Please input your E-mail!',
              },
            ],
          })(<Input />)}
        </Form.Item>
        <Form.Item
          label={
            <span>
              Residence Address&nbsp;
              <Tooltip title="No/ Street/ City.">
                <Icon type="question-circle-o" />
              </Tooltip>
            </span>
          }
        >
          {getFieldDecorator('residence', {
            rules: [{ message: 'Please input your nickname!', whitespace: true }],
          })(<Input />)}
        </Form.Item>
       
        <Form.Item label="Contact Number">
          {getFieldDecorator('contact', {
            rules: [{ required: true, message: 'Please input your phone number!' }],
          })(<Input addonBefore="+94" style={{ width: '100%' }} />)}
        </Form.Item>
        <Form.Item label="Role">
          {getFieldDecorator('role', {
            rules: [{ required: true, message: 'Please input employee role!' }],
          })(
            <Select placeholder="Please select a role">
              <Option value={1}>Supervisor</Option>
              <Option value={2}>Permenent</Option>
              <Option value={3}>Probation</Option>
              <Option value={4}>Intern</Option>
              <Option value={5}>Contract</Option>
            </Select>
          )}
        </Form.Item>  
        <Form.Item label="Department">
          {getFieldDecorator('department', {
            rules: [{ required: true, message: 'Please input employee department!' }],
          })(
          <Select placeholder="Please select a department">
            <Option value={0}>Human Resource</Option> 
            <Option value={1}>Finance</Option>
            <Option value={2}>Enterprise Support</Option>
            <Option value={3}>Java Development</Option>
            <Option value={4}>Web Development</Option>
            <Option value={5}>Mobile Development</Option>  
            <Option value={6}>Quality Assurance</Option>
            <Option value={7}>Bussiness Development</Option> 
            <Option value={8}>Bussiness Analysis</Option> 
            <Option value={9}>Big Data</Option>
          </Select>
            )}
        </Form.Item>  
        <Form.Item label="Supervisor 01">
          {getFieldDecorator('supervisor1', {
            rules: [{ required: true, message: 'Please input first supervisor!' }],
          })(
            <AutoComplete
              dataSource={websiteOptions}
              onChange={this.handleWebsiteChange}
              placeholder="Enter supervisor's name"
            >
              <Input />
            </AutoComplete>,
          )}
        </Form.Item>
        <Form.Item label="Supervisor 02">
          {getFieldDecorator('supervisor2', {
            rules: [{ message: 'Please input second supervisor!' }],
          })(
            <AutoComplete
              dataSource={websiteOptions}
              onChange={this.handleWebsiteChange}
              placeholder="Enter supervisor's name"
            >
              <Input />
            </AutoComplete>,
          )}
        </Form.Item>
        <Form.Item label="Joined date">
          {getFieldDecorator('join_date', {
            rules: [{ required: true, message: 'Please input join date!' }],
          })(<DatePicker format="YYYY-MM-DD"/>)}
        </Form.Item>
        <Form.Item label="Confirm date">
          {getFieldDecorator('confirm_date', {
            rules: [{ required: true, message: 'Please input confirm date!' }],
          })(<DatePicker format="YYYY-MM-DD"/>)}
        </Form.Item> 
        <br/><br/>  
        <Row>
        <Col span={17}>  
        <Form.Item {...tailFormItemLayout}>
          <Button type="primary" htmlType="submit" onClick={this.handleSubmit} style={{width:'100px', float:'right'}}>
          <Icon type="check-circle" /> 
            Submit
          </Button>
        </Form.Item>
        </Col> 
        <Col span={7}> 
        <Form.Item {...tailFormItemLayout}>
          <Button type="danger" onClick={this.props.close} style={{width:'100px', float:'left'}}>
          <Icon type="close-circle" /> 
            Cancel
          </Button>
        </Form.Item>
        </Col> 
        </Row>
      </Form>
      </Card>
      </div>
    );
  }
}

