import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import {Table, Button, Icon, Input, Card } from 'antd';
import Highlighter from 'react-highlight-words';
// import swal from 'sweetalert';

class LeaveCount extends Component {
    
    componentWillMount(){
        this.setState({
            data : []
        })
        axios.get('http://localhost:5000/leave_count/profile', 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("res", res.data);
            // this.setState({
            //     data : res.data
            // })
        }) 
    } 

    state = {
        searchText: '',
        data : [],
        visibleUpdate: false,
        visibleAdd: false
    };
    
      getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.state.searchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });
    
      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.setState({ searchText: selectedKeys[0] });
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.setState({ searchText: '' });
      };   
    
    
    render() {
       
        const columns = [
          {
            title: 'Name',
            key: '0',
            dataIndex: 'employee'
          },
          {
            title: 'Casual',
            key: '1',
            dataIndex: 'casual'
          },    
          {
            title: 'Medical',
            key: '2',
            dataIndex: 'medical',
            // ...this.getColumnSearchProps('supervisor1'),
          },
          {
            title: 'Maternity',
            key: '3',
            dataIndex: 'maternity'
          },
          {
            title: 'Paternity',
            key: '4',
            dataIndex: 'paternity'
          },
          {
            title: 'Annual',
            key: '5',
            dataIndex: 'annual'
          },
          {
            title: 'Lieu',
            key: '6',
            dataIndex: 'lieu'
          },
          {
            title: 'Special',
            key: '7',
            dataIndex: 'special'
          },
          {
            title: 'Cover Up',
            key: '8',
            dataIndex: 'coverup'
          },
          {
            title: 'No Pay',
            key: '9',
            dataIndex: 'nopay'
          },
          {
            dataIndex: '',
            key: '10',
            render: () => <Button type="default" shape="circle" icon="file-search" />,
          },
          ];

        return (
            <div>
                <Card>
                <Table rowKey={record => record.id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 2 }} size="middle" />
                </Card>
            </div>
        )
    }
}    


export default LeaveCount;
