import React from 'react';
import ReactDOM from 'react-dom';
import 'antd/dist/antd.css';
import './AddBasicEmployeeDetails.css';
import {
  Form,
  Input,
  Tooltip,
  Icon,
  Cascader,
  Select,
  Row,
  Col,
  Checkbox,
  Button,
  AutoComplete,
  Radio, 
  DatePicker, 
  TimePicker
} from 'antd';
import { history } from '../../_helpers/history';        
import { employeeActions } from '../../redux/actions/employee.action';
import { connect } from 'react-redux';

const { Option } = Select;
const AutoCompleteOption = AutoComplete.Option;

const residences = [
  {
    value: 'zhejiang',
    label: 'Zhejiang',
    children: [
      {
        value: 'hangzhou',
        label: 'Hangzhou',
        children: [
          {
            value: 'xihu',
            label: 'West Lake',
          },
        ],
      },
    ],
  },
  {
    value: 'jiangsu',
    label: 'Jiangsu',
    children: [
      {
        value: 'nanjing',
        label: 'Nanjing',
        children: [
          {
            value: 'zhonghuamen',
            label: 'Zhong Hua Men',
          },
        ],
      },
    ],
  },
];



class AddBasicEmployeeDetails extends React.Component {
  constructor(props){
    super(props);
    this.state =  {
      // employeeAddRequest:{

      
        employeeNo: '',
        address: '',
        nic: '',
        basicSalary: '',
        birthday : '',
        callingName: '',
        fullName: '',
        gender: '',
        joiningDate:'',
        mariudStatus:'',
        nameWithInicial: '',
        nationality : '',
        picture: '',
        religious: '',
        status: '',
        roleId:'',
        supervisorId:'',
        departmentId: '',
        designationId:'',
        employeeTypeId:''
        
      // }
    }
   
}
 
 
  state = {
    confirmDirty: false,
    autoCompleteResult: [],
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        console.log('Received values of form: ', values);
      }
    });
  };

  handleConfirmBlur = e => {
    const { value } = e.target;
    this.setState({ confirmDirty: this.state.confirmDirty || !!value });
  };

  compareToFirstPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && value !== form.getFieldValue('password')) {
      callback('Two passwords that you enter is inconsistent!');
    } else {
      callback();
    }
  };

  validateToNextPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && this.state.confirmDirty) {
      form.validateFields(['confirm'], { force: true });
    }
    callback();
  };



  handleWebsiteChange = value => {
    let autoCompleteResult;
    if (!value) {
      autoCompleteResult = [];
    } else {
      autoCompleteResult = ['.com', '.org', '.net'].map(domain => `${value}${domain}`);
    }
    this.setState({ autoCompleteResult });
  };

   handleChange(key) {
      return function (e) {
        var state = {};
        state[key] = e.target.value;
        this.setState(state);
      }.bind(this);
      
    }


  handleClickMenu = e => {
    e.preventDefault();
  
    
   
    //  const { employeeAddRequest } = this.state;
    const { dispatch } = this.props;
    
    var employeeAddRequest= {

      employeeNo: this.state.employeeNo,
      address: this.state.address,
      nic: this.state.nic,
      basicSalary: this.state.basicSalary,
      birthday : this.state.birthday,
      callingName: this.state.callingName,
      fullName: this.state.fullName,
      gender: this.state.gender,
      joiningDate:this.state.joiningDate,
      mariudStatus:this.state.mariudStatus,
      nameWithInicial: this.state.nameWithInicial,
      nationality : this.state.nationality,
      picture: this.state.picture,
      religious: this.state.religious,
      status: this.state.status,
      roleId:1,
      supervisorId:1,
      departmentId: 1,
      designationId:1,
