import React from 'react';
import 'antd/dist/antd.css';
import axios from 'axios';
import swal from 'sweetalert';
import {
  Form,
  Input,
  Button,
  DatePicker,
  Radio,
  Switch,
  Row,
  Col,
  Card,
  Icon,
  Select
} from 'antd';

const { RangePicker } = DatePicker;
const { TextArea } = Input;
const { Option } = Select;

class RequestLeave extends React.Component {
  
  componentWillMount() {
    this.setState({
      employees : []
    })

    axios.get('http://localhost:5000/employee/all', 
    {
        headers: {
            Authorization: 'Bearer ' + localStorage.getItem("header")
        }
    })
    .then(res => {
        console.log("Employees:  ", res.data);
        this.setState({
            employees : res.data
        })
    })
  }

  state = {
    employees : [],
    period : undefined,
    disable : false,
    startValue: null,
    endValue: null,
    endOpen: false,
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {

        const leave = {
          leave_type : { leave_type_id : values.leave_type },
          employee : { emp_id : 4 },
          start_date : this.state.start_date,
          end_date : this.state.end_date,
          half_day : values.halfDay,
          number_of_leave_days : parseFloat(values.days),
          duty : { emp_id : parseInt(values.duty) },
          special_notes : values.note,
          status : "Pending"
        }
        
        console.log('Received values of form: ', leave);

        axios.post(
          'http://localhost:5000/leave/request', 
          leave, 
          { 
              headers: {
                  Authorization: 'Bearer ' + localStorage.getItem("header")
              }
          }
        )
        .then(res => {
          console.log("res", res.data);
          swal("Yeah!",res.data,"success"); 
          window.location.reload();
        })
        .catch(e => {
          console.log("Error", e);
          swal("Oops!","Something Went Wrong !!!","error"); 
        })
      }
    });
  };

  // handlePeriod = (date, dateString) => {

  //   var s = new Date(date[0]._d)
  //   var e = new Date(date[1]._d)
  //   var dif = parseInt(e-s)/(1000*60*60*24)

  //   console.log("Range", dif );

  //   this.setState({
  //     start_date : dateString[0],
  //     end_date : dateString[1],
  //     period : dif+1
  //   })
  // }

  onChange = (field, value) => {
    this.setState({
      [field]: value,
    });
  };

  onStartChange = value => {
    this.onChange('startValue', value);
  };

  onEndChange = value => {
    this.onChange('endValue', value);
  };

  handleStartChange = open => {
    if (!open) {
      this.setState({ endOpen: true });
    }
  };

  handleEndChange = open => {
    this.setState({ endOpen: open });
  };

  halfDay = () => {
    this.setState({
      period : 0.5,
      disable : true
    })
  } 

  render() {
    const { getFieldDecorator } = this.props.form;
    const { startValue, endValue, endOpen } = this.state;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
    };
    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 24,
          offset: 0,
        },
        sm: {
          span: 16,
          offset: 8,
        },
      },
    };

    return (
        <div>
        <Row gutter={0}>
        <Col span={15}>    
        <div className="scrollable-container-requestLeave">       
        <div className="background-requestLeave">
        <Card title='Request Leave' bordered={false}>  
        <Form {...formItemLayout} onSubmit={this.handleSubmit}>

        <Form.Item label="Leave Type">
          {getFieldDecorator('leave_type', {
            rules: [{ required: true, message: 'Please input leave type!' }],
          })(
          <Radio.Group name="radiogroup">
          <Radio.Button value={1}>Casual</Radio.Button>
          <Radio.Button value={2}>Medical</Radio.Button>
          <Radio.Button value={3}>Maternity</Radio.Button>
          <Radio.Button value={4}>Paternity</Radio.Button>
          <Radio.Button value={5}>Annual</Radio.Button>
          <Radio.Button value={6}>Lieu</Radio.Button> 
          <Radio.Button value={7}>Special</Radio.Button>
          <Radio.Button value={8}>Cover Up</Radio.Button>
          <Radio.Button value={9}>No Pay</Radio.Button>  
          </Radio.Group>
        )}
        </Form.Item>  
       
        <Form.Item label="Leave Period">
        {getFieldDecorator('period', {
            rules: [{ required: true, message: 'Please input leave period!' }],
          })(
          <React.Fragment>  
          <DatePicker
            format="YYYY-MM-DD"
            value={startValue}
            placeholder="Start"
            onChange={this.onStartChange}
            onOpenChange={this.handleStartChange}
          />
          <DatePicker
            format="YYYY-MM-DD"
            value={endValue}
            placeholder="End"
            onChange={this.onEndChange}
            open={endOpen}
            onOpenChange={this.handleEndChange}
            disabled = {this.state.disable}
          />
          </React.Fragment>
          )}
        </Form.Item>

        <Form.Item label="Half Day">
          {getFieldDecorator('halfDay', {
            valuePropName: 'checked'
          })(
          <Switch
            checkedChildren={<Icon type="check" />}
            unCheckedChildren={<Icon type="close" />}
            onChange= {this.halfDay} 
          />
          )}
          <span>&nbsp;&nbsp; If you willing to get half day.</span>
        </Form.Item>

        <Form.Item label="Number of leave days">
          {getFieldDecorator('days', {
            initialValue : this.state.period
          })(<Input style={{width: '50px'}} disabled={true}/>)}
        </Form.Item>

        <Form.Item label="Duty Cover By">
          {getFieldDecorator('duty')(
          <Select placeholder="Please select a employee">
            {this.state.employees.map(item => (
                  <Option key={item.emp_id}>{item.first_name} {item.second_name}</Option>
            ))}
          </Select>
          )}
        </Form.Item>
              
        <Form.Item label="Special Notes">
          {getFieldDecorator('note', {
            rules: [{
                message: 'Please input your note',
              }],
          })(<TextArea rows={4} />)}
        </Form.Item>

        <Row>
        <Col span={20}>  
        <Form.Item {...tailFormItemLayout}>
          <Button type="primary" htmlType="submit" style={{width:'100px'}}>
          <Icon type="check-circle" /> 
            Submit
          </Button>
        </Form.Item>
        </Col> 

        <Col span={4}> 
        <Form.Item {...tailFormItemLayout}>
          <Button type="danger" htmlType="submit" style={{width:'100px', float:'left'}}>
          <Icon type="close-circle" /> 
            Cancel
          </Button>
        </Form.Item>
        </Col> 
        </Row>
      </Form>
      </Card>
      </div>
      </div>
      </Col>
      <Col span={8}>
      <img height= '580px' width= '480px' alt="example" src="https://ec.europa.eu/jrc/sites/jrcsh/files/styles/normal-responsive/public/fotolia-92027264european-day-forest-green-forest.jpg?itok=MwdU0DK-" />
      </Col> 
        </Row>
    </div>
    );
  }
}

const WrappedRequestLeave = Form.create({ name: 'register' })(RequestLeave);

export default WrappedRequestLeave;
