import React from 'react';
import 'antd/dist/antd.css';
import axios from 'axios';
import swal from 'sweetalert';
import {
  Form,
  Input,
  Button,
  AutoComplete,
  DatePicker,
  Radio,
  Switch,
  Row,
  Col,
  Card,
  Icon
} from 'antd';
// import { request } from 'http';

const AutoCompleteOption = AutoComplete.Option;
const { RangePicker } = DatePicker;
const { TextArea } = Input;

class RequestLeave extends React.Component {
  state = {
    confirmDirty: false,
    autoCompleteResult: [],
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        console.log('Received values of form: ', values);

        const leave = {
          leave_type : values,
          start_date : values,
          end_date : values,
          number_of_leave_days : values.days,
          special_notes : valuenote
        }

        axios.post(
          'http://localhost:5000/employee/update/', 
          leave, 
          { 
              headers: {
                  Authorization: 'Bearer ' + localStorage.getItem("header")
              }
          }
        )
        .then(res => {
          console.log("res", res.data);
          swal("Yeah!","Employee Added!!!","success"); 
          
        })
      }
    });
  };

  handleConfirmBlur = e => {
    const { value } = e.target;
    this.setState({ confirmDirty: this.state.confirmDirty || !!value });
  };

  compareToFirstPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && value !== form.getFieldValue('password')) {
      callback('Two passwords that you enter is inconsistent!');
    } else {
      callback();
    }
  };

  validateToNextPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && this.state.confirmDirty) {
      form.validateFields(['confirm'], { force: true });
    }
    callback();
  };

  handleWebsiteChange = value => {
    let autoCompleteResult;
    if (!value) {
      autoCompleteResult = [];
    } else {
      autoCompleteResult = ['.com', '.org', '.net'].map(domain => `${value}${domain}`);
    }
    this.setState({ autoCompleteResult });
  };

  render() {
    const { getFieldDecorator } = this.props.form;
    const { autoCompleteResult } = this.state;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
    };
    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 24,
          offset: 0,
        },
        sm: {
          span: 16,
          offset: 8,
        },
      },
    };

    const websiteOptions = autoCompleteResult.map(website => (
      <AutoCompleteOption key={website}>{website}</AutoCompleteOption>
    ));

    return (
        <div>
        <Row gutter={0}>
        <Col span={15}>    
        <div className="scrollable-container-requestLeave">       
        <div className="background-requestLeave">
        <Card title='Request Leave' bordered={false}>  
        <Form {...formItemLayout} onSubmit={this.handleSubmit}>

        <Form.Item label="Leave Type">
          {getFieldDecorator('leave_type', {
            rules: [{ required: true, message: 'Please input initials!' }],
          })(
          <Radio.Group name="radiogroup">
          <Radio.Button value={1}>Casual</Radio.Button>
          <Radio.Button value={2}>Medical</Radio.Button>
          <Radio.Button value={3}>Maternity</Radio.Button>
          <Radio.Button value={4}>Paternity</Radio.Button>
          <Radio.Button value={5}>Annual</Radio.Button>
          <Radio.Button value={6}>Lieu</Radio.Button> 
          <Radio.Button value={7}>Special</Radio.Button>
          <Radio.Button value={8}>Cover Up</Radio.Button>
          <Radio.Button value={9}>No Pay</Radio.Button>  
          </Radio.Group>
        )}
        </Form.Item>  
       
        <Form.Item label="Leave Period">
          {getFieldDecorator('leavePeriod', {
            rules: [{ required: true, message: 'Please input first name!' }],
          })(<RangePicker />)}
        </Form.Item>

        <Form.Item label="Half Day">
          {getFieldDecorator('halfDay', {
            valuePropName: 'checked'
          })(
          <Switch
            checkedChildren={<Icon type="check" />}
            unCheckedChildren={<Icon type="close" />}
           
          />
          )}
          <span>&nbsp;&nbsp; If you willing to get leave with half day.</span>
        </Form.Item>

        <Form.Item label="Number of leave days">
          {getFieldDecorator('days', {
            rules: [{ message: 'Please input second name!' }],
          })(<Input style={{width: '50px'}}/>)}
        </Form.Item>

        <Form.Item label="Duties coverd by">
          {getFieldDecorator('dutiesCover', {
            rules: [{ message: 'Please input initials!' }],
          })(
            <AutoComplete
              dataSource={websiteOptions}
              onChange={this.handleWebsiteChange}
              placeholder="Enter valid employee name"
            >
              <Input />
            </AutoComplete>,
          )}
        </Form.Item>
              
        <Form.Item label="Special Notes">
          {getFieldDecorator('note', {
            rules: [{
                message: 'Please input your note',
              }],
          })(<TextArea rows={4} />)}
        </Form.Item>

        <Row>
        <Col span={20}>  
        <Form.Item {...tailFormItemLayout}>
          <Button type="primary" htmlType="submit" style={{width:'100px'}}>
          <Icon type="check-circle" /> 
            Submit
          </Button>
        </Form.Item>
        </Col> 

        <Col span={4}> 
        <Form.Item {...tailFormItemLayout}>
          <Button type="danger" htmlType="submit" style={{width:'100px', float:'left'}}>
          <Icon type="close-circle" /> 
            Cancel
          </Button>
        </Form.Item>
        </Col> 
        </Row>
      </Form>
      </Card>
      </div>
      </div>
      </Col>
      <Col span={8}>
      <img height= '580px' width= '480px' alt="example" src="https://ec.europa.eu/jrc/sites/jrcsh/files/styles/normal-responsive/public/fotolia-92027264european-day-forest-green-forest.jpg?itok=MwdU0DK-" />
      </Col> 
        </Row>
    </div>
    );
  }
}

const WrappedRequestLeave = Form.create({ name: 'register' })(RequestLeave);

export default WrappedRequestLeave;
