import React from "react";
import axios from 'axios';
import swal from 'sweetalert';
import "antd/dist/antd.css";
import "../../container/index.css";
import { Link, Switch, Route } from 'react-router-dom';
import BasicDetails from './BasicDetails';
import ContactDetails from './ContactDetails';
import {
  Form,
  Icon,
  Card,
  Menu
} from "antd";

class Details extends React.Component {
  state = {
    confirmDirty: false,
    basic: false,
    contact: false,
    educational: false,
    Experions: false
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        
        const favorite = {
            user_id: localStorage.getItem("id"),
            user_name: localStorage.getItem("user"),
            food: values.food || undefined,
            drink: values.drink || undefined,
            animal: values.animal || undefined,
            bird: values.bird || undefined,
            hobby: values.hobby || undefined,
            place: values.place || undefined
        }
        axios.post('http://localhost:5000/savefavo', favorite)
        .then(res => {
            console.log("res", res.data);
            swal("OMG!","User Favorite Submited!!!","success");
        }).catch(e => console.log("eeeeeeeeeeee",e));
        this.props.history.push('/read'); 
      }
    });
  };



  render() {
    

    return (
      <div style={{ background: '#ECECEC', paddingLeft: '130px', paddingRight: '150px', height: '420px' }}>
      
      <Menu onClick={this.handleClick} selectedKeys={[this.state.current]} mode="horizontal">
        <Menu.Item key="1">
          <Link to='/favorite/basic'/>
          <Icon type="mail" />
          Basic Details
        </Menu.Item>
        <Menu.Item key="2" >
          <Link to='/favorite/contact'/>
          <Icon type="appstore" />
          Contact Details
        </Menu.Item>
        <Menu.Item key="3" >
          <Link to='/favorite/educational'/>
          <Icon type="appstore" />
          Educational Details
        </Menu.Item>
        <Menu.Item key="4" >
          <Link to='/favorite/experions'/>
          <Icon type="appstore" />
          Experions Details
        </Menu.Item>
      </Menu> 
      <div className="scrollable-container-fav">       
      <div className="background">
      <Card bordered={false} style={{ width: 950, height: '600px'}}>
      <Switch>
        <Route path='/favorite/basic' component={BasicDetails} />
        <Route path='/favorite/contact' component={ContactDetails} />
      </Switch>
      </Card>
      </div>
      </div>
      </div>
    );
  }
}

const WrappedRegistrationForm = Form.create({ name: "favorite" })(
  Details
);


export default WrappedRegistrationForm;

