import React from 'react';
import 'antd/dist/antd.css';
import { Calendar, Alert, Card, Modal, Form, Input } from 'antd';
import moment from 'moment';

const ReasonForm = Form.create({ name: 'form_in_modal' })(

  class extends React.Component {
    render() {
      const { visible, onCancel, onCreate, form } = this.props;
      const { getFieldDecorator } = form;
      return (
        <Modal
          visible={visible}
          title="Reject the request"
          okText="Reject"
          onCancel={onCancel}
          onOk={onCreate}
        >
          <Form layout="vertical">
            <Form.Item label="Reason">
              {getFieldDecorator('reason', {
                rules: [{ required: true, message: 'Please input the reason for reject!' }],
              })(<Input type="textarea"/>)}
            </Form.Item>
          </Form>
        </Modal>
      );
    }
  },
);

export default class LeaveCalender extends React.Component {
  state = {
    value: moment('2017-01-25'),
    selectedValue: moment('2017-01-25'),
    visible: false
  };

  onSelect = value => {
    this.setState({
      value,
      selectedValue: value,
    });
  };

  onPanelChange = value => {
    this.setState({ value });
  };

  showModal = () => {
    this.setState({ 
      visible: true
    });
  };

  handleCancel = () => {
    this.setState({ 
      visible: false
    });
  };

  handleSet = () => {
    console.log("Set")
  }

  render() {
    const { value, selectedValue } = this.state;
    return (
      <div>
        <Card>
          <Alert
            message={`You selected date: ${selectedValue && selectedValue.format('YYYY-MM-DD')}`}
          />
          <Calendar value={value} onSelect={this.onSelect} onPanelChange={this.onPanelChange} />
        </Card>

        <ReasonForm
          wrappedComponentRef={this.saveFormRef}
          visible={this.state.visible}
          onCancel={this.handleCancel}
          onCreate={this.handleReject}
        />
      </div>
    );
  }
}
