import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import {Table, Button, Icon, Input, Drawer } from 'antd';
import Highlighter from 'react-highlight-words';
import swal from 'sweetalert';
import UpdateEmployee from '../Employee/UpdateEmployee';
import AddEmployee from '../Employee/AddEmployee';

class ViewEmployee extends Component {
    
    componentWillMount(){
        this.setState({
            data : []
        })

        axios.get('http://localhost:5000/employee/all', 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("res", res.data);
            this.setState({
                data : res.data
            })
        }) 
    } 

    state = {
        searchText: '',
        data : [],
        visibleUpdate: false,
        visibleAdd: false,
        emp_id: undefined,
        updateEmployee: [] 
    };
    
      getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.state.searchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });
    
      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.setState({ searchText: selectedKeys[0] });
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.setState({ searchText: '' });
      };   
    
      showAddDrawer = () => {
        this.setState({
            visibleAdd: true
          });
    }

    onAddClose = () => {
        this.setState({
          visibleAdd: false
        });
        window.location.reload();

    };

      showUpdateDrawer = (emp) => {
        console.log("Update Employee's Id: ",this.props.update);
    
        axios.get('http://localhost:5000/employee/'+ this.props.update, 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("res", res.data);
            this.setState({
                visibleUpdate: true,
                updateEmployee : res.data
            })
        })
        
        // this.setState({
        //   visibleUpdate: true,
        //   emp_id: emp.emp_id
        // });

      };
    
    onUpdateClose = (e) => {
        this.setState({
          visibleUpdate: false
        });
        window.location.reload();
    };

    handleInfo = (emp) => {
      swal(
        `
        Id: ${emp.emp_id} 
        Name: ${emp.first_name} ${emp.second_name}
        Initials: ${emp.initials}
        Email: ${emp.email}
        Contact: 0${emp.contact}
        Address: ${emp.residence}
        Supervisor 01: ${emp.supervisor1}
        Supervisor 02: ${emp.supervisor2}
        Join Date: ${emp.join_date}
        Confirm Date: ${emp.confirm_date}
         `,
        
      
      );
      console.log(AddEmployee.state)
    }

    handleDelete(emp,e) {
        e.preventDefault();
        console.log("sssssss", emp.emp_id)
        swal({
            text: "Wanna delete that user?",
            buttons: {
                confirm: "Delete",
                cancel: true,
            }
        })
        .then(willSearch => {
            if (willSearch) {
                axios.get('http://localhost:5000/employee/delete/'+emp.emp_id, 
                {
                    headers: {
                        Authorization: 'Bearer ' + localStorage.getItem("header")
                    }
                })
                .then(res => {
                    swal("Yeah!",res.data,"success"); 
                    this.props.history.push('/manage_employee'); 
                }).catch( err => {
                    swal("Oops!","Something Went Wrong!!!","error");
                })
            }
        })
    }
    
    render() {
       
        const columns = [
            {
              title: 'Id',
              dataIndex: 'emp_id'
            },
            {
              title: 'Name',
              dataIndex: 'first_name',
              ...this.getColumnSearchProps('first_name'),
            },
            {
              title: 'Email',
              dataIndex: 'email',
            },
            {
                title: 'Contact',
                dataIndex: 'contact',
            },
            {
              title: 'Role',
              dataIndex: 'role',
              render: role => (
               role.role_name
              ),
            },
            {
              title: 'Department',
              dataIndex: 'department',
              render: role => (
               role.department_name
              )
            },
            {
                title: 'Supervisor 01',
                dataIndex: 'supervisor1',
                ...this.getColumnSearchProps('supervisor1'),
            },
            {
              dataIndex: '',
              key: 'z',
              width: '3%',
              render: (emp) => <Button type="default" onClick={this.handleInfo.bind(this, emp)} shape="circle" icon="info" />,
            },
            {
                dataIndex: '',
                key: 'x',
                width: '3%',
                render: (emp) => <Button type="primary" onClick={this.showUpdateDrawer.bind(this, emp)} shape="circle" icon="edit" />,
            },
            {
                dataIndex: '',
                key: 'y',
                width: '3%',
                render: (emp) => <Button type="danger" onClick={this.handleDelete.bind(this, emp)} shape="circle" icon="delete" />,
            },

          ];

        return (
            <div>
                <Button style={{float:'right'}} type="primary" onClick={this.showAddDrawer} shape="circle" icon="user-add" />
                <br/><br/>
                <Table rowKey={record => record.emp_id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 8 }} size="middle" />

                <Drawer
                title="Update Existing Employee"
                width={570}
                onClose={this.onUpdateClose}
                visible={this.state.visibleUpdate}
                >
                    <UpdateEmployee update={this.state.updateEmployee} close={this.onUpdateClose} />
                </Drawer>

                <Drawer
                title="Add New Employee"
                width={570}
                onClose={this.onAddClose}
                visible={this.state.visibleAdd}
                >
                    <AddEmployee close={this.onAddClose}/>
                </Drawer>
            </div>
        )
    }
}    


export default ViewEmployee;
