import React, { Component } from 'react';
import {Redirect} from 'react-router-dom';

const loginStyle = {
  width: "90%",
  maxWidth: "500px",
  margin: "100px auto",
  border: "5px solid #ddd",
  borderRadius: "5px",
  padding: "30px"
}

class signup extends Component {
  state = {
    name: '',
    salary: '',
    redirectToReferrer: false
  }
 
  handleChange = (e) => {
    this.setState({
      [e.target.id] : e.target.value
    })
  }
  
  handleSubmit = (e) => {
    e.preventDefault();
    if (this.state.password !== this.state.cpassword) {
      this.setState({error:'Passwords did not matching!!!'})
      alert('Passwords did not matching!!!')
    } 
    else if (this.state.name === ''|| this.state.email === '') {
      this.setState({error:'Fields are empty'})
      alert('All fields are required!!!')
    } 
    else{
      //this.props.register(this.state);
      this.setState({redirectToReferrer: true}) 
      alert("Create User Successfully!!!")
    } 
    this.props.history.push('/');   
  }
  render() {
    const { authError } = this.props;
    const {from} = this.props.location.state || {
      from: {
        pathname: '/SignUp'
      }
    }
    const {redirectToReferrer} = this.state
    if (redirectToReferrer && {authError}==null) {
      return (<Redirect to={from}/>)
    }
    const {auth} = this.props;
    if(!auth.uid) return <Redirect to='/SignIn'/>
    return (

      <div style={loginStyle} className="white">
       
        <form onSubmit={this.handleSubmit}>

        <h5>Register the Users</h5>
        
        <br/>

        <div className="input-field">  
          <label htmlFor="name"> Name </label>  
          <input id="name" type="text" onChange={this.handleChange}/>       
        </div>

        <div className="input-field">  
          <label htmlFor="salary"> Salary </label>  
          <input id="salary" type="text" onChange={this.handleChange}/>       
        </div>

        <div className="input-field"> 
          <label htmlFor="password"> Password </label>  
          <input id="password" type="password" onChange={this.handleChange}/>
        </div> 
        
        <div className="input-field">
          <button className="btn blue lighten-1 z-depth-0">Sign Up</button>  
        </div>
        <div className="red-text center">
            { authError ? <p>{authError}</p>: null}
          </div>
        </form>
         
      </div>
    );
  }
}

export default (signup);
