import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import {Table, Button, Icon, Input, Drawer, Card, Tag, Modal, message, Form, Row, Col } from 'antd';
import Highlighter from 'react-highlight-words';
import UpdateEmployee from '../Employee/UpdateEmployee';
import AddEmployee from '../Employee/AddEmployee';

const ResignForm = Form.create({ name: 'viewEmployee' })(

  class extends React.Component {
    render() {
      const { visible, onCancel, onCreate, form } = this.props;
      const { getFieldDecorator } = form;
      return (
        <Modal
          visible={visible}
          title="Resign the employee"
          okText="Resign"
          onCancel={onCancel}
          onOk={onCreate}
        >
          <Form layout="vertical">
            <Form.Item label="Resign Date">
              {getFieldDecorator('date', {
                rules: [
                  { 
                    required: true, 
                    message: 'Please input the resign date!' 
                  },
                  {
                    message: 'Input valid date as example!',
                    len : 10
                  }
                ],
              })(<Input type="textarea" placeholder="ex: 2020-01-01"/>)}
            </Form.Item>
          </Form>
        </Modal>
      );
    }
  },
);

class ViewEmployee extends Component {
    
    componentWillMount(){
       this.reload();
    } 

    reload() {
      this.setState({
        data : []
      })

    axios.get('http://localhost:5000/user/all', 
    {
        headers: {
            Authorization: 'Bearer ' + localStorage.getItem("header")
        }
    })
    .then(res => {
        this.setState({
            data : res.data
        })
    }) 
    }

    state = {
        searchText: '',
        data : [],
        visibleUpdate: false,
        visibleAdd: false,
        emp_id: undefined,
        updateEmployee: [],
        emp : [],
        visible: false,
        visibleResign : false 
    };
    
    info = (emp) => {
      this.setState({
        visible: true,
        emp: emp
      });
    };
  
    handleOk = e => {
      console.log(e);
      this.setState({
        visible: false,
      });
    };

      getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.state.searchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });
    
      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.setState({ searchText: selectedKeys[0] });
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.setState({ searchText: '' });
      };   
    
      showAddDrawer = () => {
        this.setState({
            visibleAdd: true
          });
      }

      onAddClose = () => {
          this.setState({
            visibleAdd: false
          });
          this.reload();
      };

      showUpdateDrawer = (emp) => {
    
        axios.get('http://localhost:5000/user/get/'+ emp.id, 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            this.setState({
                visibleUpdate: true,
                updateEmployee : res.data
            })
        })
      };
    
    onUpdateClose = () => {
        this.setState({
          visibleUpdate: false,
          updateEmployee: []
        });
        window.location.reload();
    };
    
    showModal = (emp) => {
      this.setState({ 
        visibleResign : true,
        emp_id : emp.id
      });
    };

    saveFormRef = formRef => {
      this.formRef = formRef;
    };

    handleResign = () => {
      const { form } = this.formRef.props;
      form.validateFields((err, values) => {
        console.log(values)
        if (!err) {
          axios.post('http://localhost:5000/user/resign', 
                {
                  id: this.state.emp_id,
                  resignDate:values.date
                },
                {
                    headers: {
                        Authorization: 'Bearer ' + localStorage.getItem("header")
                    }
                })
                .then(res => {
                    message.success(res.data); 
                    form.resetFields();
                    this.reload(); 
                }).catch( err => {
                    message.error(err.response.data.error);
                })
          this.setState({ visibleResign : false });
        }
        
      });
    };
    
    handleCancel = () => {
      const { form } = this.formRef.props;
      form.resetFields();  
      this.setState({ 
        visibleResign: false
      });
    };

    render() {
       
        const {emp} = this.state;      
        const columns = [
            {
              title: 'Id',
              dataIndex: 'userId',
              ...this.getColumnSearchProps('userId'),
            },
            {
              title: 'Name',
              dataIndex: 'firstName',
              ...this.getColumnSearchProps('firstName'),
            },
            {
              title: 'Email',
              dataIndex: 'email',
            },
            {
                title: 'Contact',
                dataIndex: 'contact',
            },
            {
              title: 'Role',
              dataIndex: 'role',
              ...this.getColumnSearchProps('role')
            },
            {
              title: 'Department',
              dataIndex: 'department',
              ...this.getColumnSearchProps('department'),
              // render: role => (
              //  role.department_name
              // )
            },
            {
                title: 'Supervisor 01',
                dataIndex: 'supervisor1',
                ...this.getColumnSearchProps('supervisor1'),
            },
            {
              title: 'Status',
              dataIndex: 'status',
              ...this.getColumnSearchProps('status'),
              render: tag => {
                let color;
                if (tag === 'Resign') {
                  color = 'red';
                }else {
                  color = 'geekblue';
                }
                return (
                  <Tag color={color} key={tag}>
                    {tag}
                  </Tag>
                );
              }
          },
            {
              dataIndex: '',
              key: 'z',
              width: '3%',
              render: (emp) => <Icon type="info-circle" onClick={this.info.bind(this, emp)}/>,
            },
            {
                dataIndex: '',
                key: 'x',
                width: '3%',
                render: (emp) => <Icon type="edit" onClick={this.showUpdateDrawer.bind(this, emp)}  theme="twoTone" />,
            },
            {
                dataIndex: '',
                key: 'y',
                width: '3%',
                render: (emp) => <Icon type="close-circle" onClick={this.showModal.bind(this, emp)}  theme="twoTone" twoToneColor='#EE204D'/>,
            },

          ];

        return (
            <div>
              <Card hoverable='true'>
                <Button style={{float:'right'}} type="primary" onClick={this.showAddDrawer} shape="circle" icon="user-add" />
                <br/><br/>
                <Table rowKey={record => record.id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 10 }} size="middle" />

                <Drawer
                title="Update Existing Employee"
                width={570}
                onClose={this.onUpdateClose}
                visible={this.state.visibleUpdate}
                >
                    <UpdateEmployee update={this.state.updateEmployee} close={this.onUpdateClose} />
                </Drawer>

                <Drawer
                title="Add New Employee"
                width={570}
                onClose={this.onAddClose}
                visible={this.state.visibleAdd}
                >
                    <AddEmployee close={this.onAddClose}/>
                </Drawer>
                </Card> 
                
                {emp.length!==0? <Modal
                  title="Full Employee Detail"
                  visible={this.state.visible}
                  onOk={this.handleOk}
                  onCancel={this.handleOk}
                  footer={null}
                >
                  {/* <Row>
                    <Col span={10}>
                      ID: <Tag color="volcano">{emp.userId}</Tag>
                    </Col>
                    <Col span={14}>
                      Name: <Tag color="volcano">{emp.firstName} {emp.secondName}</Tag>
                    </Col>
                  </Row> */}
                  {emp.status === ""?
                  <Alert message="Success Text" type="success" />
                  :null
                  }
                  <p>Emp No: <Tag color="volcano">{emp.userId}</Tag> &nbsp;&nbsp;&nbsp;&nbsp; Name: <Tag color="volcano">{emp.firstName} {emp.secondName}</Tag> &nbsp;&nbsp;&nbsp;&nbsp; Role: <Tag color="volcano">{emp.role}</Tag></p>
                  <p>Designation: <Tag color="volcano">{emp.designation}</Tag> &nbsp;&nbsp;&nbsp;&nbsp; Department: <Tag color="volcano">{emp.department}</Tag></p>
                  <p>Email: <Tag color="volcano">{emp.email}</Tag> Contact: <Tag color="volcano">{emp.contact}</Tag></p>
                  <p>Address: <Tag color="volcano">{emp.residence}</Tag></p>
                  <p>  Status: <Tag color="volcano">{emp.status}</Tag> </p>
                  <p>Supervisor 01: <Tag color="volcano">{emp.supervisor1}</Tag>   Supervisor 02: <Tag color="volcano">{emp.supervisor2}</Tag></p>
                  <p>Join Date: <Tag color="volcano">{emp.joinDate}</Tag>   Confirm Date: <Tag color="volcano">{emp.confirmDate}</Tag></p>
                  <p>Annual: <Tag color="volcano">{emp.annual}</Tag>   Casual: <Tag color="volcano">{emp.casual}</Tag> Medical: <Tag color="volcano">{emp.medical}</Tag></p>
                </Modal>: null}

                  
                <ResignForm
                  wrappedComponentRef={this.saveFormRef}
                  visible={this.state.visibleResign}
                  onCancel={this.handleCancel}
                  onCreate={this.handleResign}
                />

            </div>
        )
    }
}    

const WrappedViewEmployee = Form.create({ name: 'viewEmployee' })(ViewEmployee);

export default WrappedViewEmployee;
