import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import {Table, Button, Icon, Input, Card, Modal, Form, Tag, message } from 'antd';
import Highlighter from 'react-highlight-words';

const CollectionCreateForm = Form.create({ name: 'form_in_modal' })(

  class extends React.Component {
    render() {
      const { visible, onCancel, onCreate, form } = this.props;
      const { getFieldDecorator } = form;
      return (
        <Modal
          visible={visible}
          title="Reject the request"
          okText="Reject"
          onCancel={onCancel}
          onOk={onCreate}
        >
          <Form layout="vertical">
            <Form.Item label="Reason">
              {getFieldDecorator('reason', {
                rules: [{ required: true, message: 'Please input the reason for reject!' }],
              })(<Input type="textarea"/>)}
            </Form.Item>
          </Form>
        </Modal>
      );
    }
  },
);

class PendingLeaves extends Component {
    
    componentWillMount(){
        this.reload();
    } 

    reload(){
      this.setState({
        data : []
      })

      axios.get('http://localhost:5000/leave/pending', 
      {
          headers: {
              Authorization: 'Bearer ' + localStorage.getItem("header")
          }
      })
      .then(res => {
        console.log(res.data)
          this.setState({
              data : (res.data).reverse()
          })
      }) 

      axios.get('http://localhost:5000/leave_type/all', 
      {
          headers: {
              Authorization: 'Bearer ' + localStorage.getItem("header")
          }
      })
      .then(res => {
          this.setState({
              types : res.data
          })
      })

      axios.get('http://localhost:5000/leave_count/profile', 
      {
          headers: {
              Authorization: 'Bearer ' + localStorage.getItem("header")
          }
      })
      .then(res => {
          this.setState({
              count : res.data
          })
      }) 
    }

    state = {
        visible: false,
        searchText: '',
        data : [],
        reject: null,
        visibleAccept: false,
        confirmLoading: false,
        leave: [],
        types: [],
        count: [],
        countOfOne: []
    };
    
      getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.state.searchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });
    
      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.setState({ searchText: selectedKeys[0] });
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.setState({ searchText: '' });
      };   

    show = (leave) => {

      this.state.count.map(item => (
        item.id===leave.user.id ? this.setState({ countOfOne : item }) : null
      ))
      console.log(leave)
      this.setState({
        leave: leave,
        visibleAccept: true
      });
    };
  
    handleOk = () => {
      
      this.setState({
        confirmLoading: true,
      });
      
      setTimeout(() => {
        this.setState({
          visibleAccept: false,
          confirmLoading: false,
        });
      }, 500);

      console.log("type", this.state.leave.type);
      console.log("number", this.state.leave.number_of_leave_days);
      console.log()

      if(this.state.leave.type === 'Casual' && this.state.leave.number_of_leave_days > (this.state.types[0].count-this.state.countOfOne.casual)){
        message.error("Exceed casual limit");
      }else if(this.state.leave.type === 'Medical' && this.state.leave.number_of_leave_days > (this.state.types[1].count-this.state.countOfOne.medical)){
        message.error("Exceed medical limit");
      }else if(this.state.leave.type === 'Maternity' && this.state.leave.number_of_leave_days > (this.state.types[2].count-this.state.countOfOne.maternity)){
        message.error("Exceed maternity limit");
      }else if(this.state.leave.type === 'Paternity' && this.state.leave.number_of_leave_days > (this.state.types[3].count-this.state.countOfOne.paternity)){
        message.error("Exceed paternity limit");
      }else if(this.state.leave.type === 'Annual' && this.state.leave.number_of_leave_days > (this.state.types[4].count-this.state.countOfOne.annual)){
        message.error("Exceed annual limit");  
      }else { 
        // axios.get('http://localhost:5000/leave/approve/'+this.state.leave.id, 
        //           {
        //               headers: {
        //                   Authorization: 'Bearer ' + localStorage.getItem("header")
        //               }
        //           })
        //           .then(res => {
        //               message.success(res.data); 
        //               this.reload(); 
        //           }).catch( err => {
        //               message.error(err.response.data.error);
        //           })
      }
    };

    showModal = (leave) => {
      this.setState({ 
        visible: true,
        reject: leave.leave_id
      });
    };
  
    handleCancel = () => {
      this.setState({ 
        visible: false,
        visibleAccept: false 
      });
      // this.reload();
    };
  
    handleReject = () => {
      const { form } = this.formRef.props;
      form.validateFields((err, values) => {
        if (!err) {
          axios.post('http://localhost:5000/leave/reject/'+this.state.reject, 
                {
                  reject:values.reason
                },
                {
                    headers: {
                        Authorization: 'Bearer ' + localStorage.getItem("header")
                    }
                })
                .then(res => {
                    message.success(res.data); 
                    this.reload(); 
                }).catch( err => {
                    message.error("Something Went Wrong!");
                })
          this.setState({ visible: false });
        }
        form.resetFields();
        
      });
    };
  
    saveFormRef = formRef => {
      this.formRef = formRef;
    };

    render() {
      const { visibleAccept, confirmLoading, leave } = this.state;
        const columns = [
            {
              title: 'Name',
              key: '0',
              dataIndex: 'user',
              ...this.getColumnSearchProps('user'),
            },  
            {
              title: 'Type',
              key: '1',
              dataIndex: 'type',
              ...this.getColumnSearchProps('type'),
            },
            {
              title: 'Start Date',
              key: '2',
              dataIndex: 'startDate'
            },
            {
              title: 'End Date',
              dataIndex: 'endDate'
            },
            {
                title: 'Number of days',
                dataIndex: 'number_of_leave_days'
            },
            {
                title: 'Date of request',
                dataIndex: 'formatDateTime',
            },
            {
              title: 'Supervisor 01',
              dataIndex: 'supervisor1',
              ...this.getColumnSearchProps('supervisor1'),
            },
            {
              title: 'Supervisor 02',
              dataIndex: 'supervisor2',
              ...this.getColumnSearchProps('supervisor2'),
            },
            {
                dataIndex: '',
                key: 'b',
                width: '1%',
                render: (emp) => <Icon type="like" onClick={this.show.bind(this, emp)} theme="twoTone" />,
            },
            {
              dataIndex: '',
              key: 'c',
              width: '1%',
              render: (emp) => <Icon type="dislike" onClick={this.showModal.bind(this, emp)} theme="twoTone" />,
          },

          ];

        return (
            <div>
              <Card title="Pending Leaves" hoverable='true'>
                <Table rowKey={record => record.id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 7 }} size="middle" />
              </Card> 
                <CollectionCreateForm
                  wrappedComponentRef={this.saveFormRef}
                  visible={this.state.visible}
                  onCancel={this.handleCancel}
                  onCreate={this.handleReject}
                />

                { (leave.length!==0) ? <Modal
                title="Accept the leave request."
                visible={visibleAccept}
                onOk={this.handleOk}
                confirmLoading={confirmLoading}
                onCancel={this.handleCancel}
                okText="Approve"
                >
                  <div>
                      <br/>
                      <p>Employee: <Tag color="volcano">{leave.user}</Tag> Date of Request: <Tag color="volcano">{leave.formatDateTime}</Tag></p>
                      <p>Leave Type: <Tag color="volcano">{leave.type}</Tag>  Number of Leave Days: <Tag color="volcano">{leave.number_of_leave_days}</Tag></p>
                      <p>Leave Period: <Tag color="volcano">{leave.startDate} {leave.startHalf}</Tag>to <Tag color="volcano">{leave.endDate}  {leave.endHalf}</Tag></p>
                      <p>Supervisor 01: <Tag color="volcano">{leave.supervisor1}</Tag>  Supervisor 02: <Tag color="volcano">{leave.supervisor2}</Tag></p>
                      <p>Duty cover by: <Tag color="volcano">{leave.duty}</Tag></p>
                      <p>Special note: <Tag color="volcano">{leave.specialNotes}</Tag></p>
                  </div>
                </Modal> : null}
            </div>
        )
    }
}    


export default PendingLeaves;
