import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import {Table, Button, Icon, Input, Card, Modal, Tag, message, Alert } from 'antd';
import Highlighter from 'react-highlight-words';

const { confirm } = Modal;

class ReadLeaves extends Component {
    
    componentWillMount(){
       this.reload();
    } 

    state = {
        searchText: '',
        data : [],
        visible: false,
        leave : [] 
    };
    
    reload () {
      this.setState({
        data : []
      })
    
      axios.get('http://localhost:5000/leave/all', 
      {
          headers: {
              Authorization: 'Bearer ' + localStorage.getItem("header")
          }
      })
      .then(res => {
          if (res.data.success === true) {              
            this.setState({
              data : (res.data.list).reverse()
            })
          } else {
              message.error(res.data.message);
          }
      })
      .catch(e => {
        message.error("Something went wrong");
        console.log(e.response.data.error);
      })  
    }

      getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.state.searchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });
    
      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.setState({ searchText: selectedKeys[0] });
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.setState({ searchText: '' });
      };   

    

    showModal = (leave) => {
      this.setState({
        visible: true,
        leave: leave
      });
    };
  
    handleOk = e => {
      console.log(e);
      this.setState({
        visible: false,
      });
    };

    showConfirm = (leave) =>{
      confirm({
        title: 'Do you want to remove this request ?',
        content: 'Make sure you can\'t delete pending leave request.',
        okText: 'Remove',
        okType: 'danger',
        onOk: () => {
          axios.get('http://localhost:5000/leave/delete/'+leave.id, 
          {
              headers: {
                  Authorization: 'Bearer ' + localStorage.getItem("header")
              }
          })
          .then(res => {
              if (res.data.success === true) {
                message.success(res.data.message);
                this.reload();
              } else {
                message.error(res.data.message);
              }
          }).catch( err => {
              console.log(err);
              message.error("Something Went Wrong!");
          })
        },
        onCancel() {
        },
      });
    }

    render() {
       
        const {leave} = this.state;

        const columns = [
            {
              title: 'Name',
              dataIndex: 'user',
              ...this.getColumnSearchProps('user')
            },  
            {
              title: 'Leave Type',
              dataIndex: 'type',
              // render: type => (
              //  type.type
              // ),
              ...this.getColumnSearchProps('type')
            },
            {
                title: 'Start Date',
                dataIndex: 'startDate',
                ...this.getColumnSearchProps('startDate')
            },
            {
                title: 'End Date',
                dataIndex: 'endDate',
                ...this.getColumnSearchProps('endDate')
            },
            {
              title: 'Supervisor 01',
              dataIndex: 'supervisor1',
              ...this.getColumnSearchProps('supervisor1'),
            },
            {
                title: 'Date of request',
                dataIndex: 'formatDateTime',
                ...this.getColumnSearchProps('formatDateTime'),
            },
            {
              title: 'Checked By',
              dataIndex: 'checkBy',
              ...this.getColumnSearchProps('checkBy'),
            },
            {
              title: 'Status',
              dataIndex: 'status',
              ...this.getColumnSearchProps('status'),
              render: tag => {
                        let color;
                        if (tag === 'Pending') {
                          color = 'geekblue';
                        }else if (tag === 'Approved') {
                          color = 'green';
                        }else {
                          color = 'red';
                        }
                        return (
                          <Tag color={color} key={tag}>
                            {tag}
                          </Tag>
                        );
                      },
            },
            {
              dataIndex: '',
              key: 'z',
              width: '1%',
              render: (emp) => <Icon type="info-circle" onClick={this.showModal.bind(this, emp)}/>,
            },
            {
              dataIndex: '',
              key: 'y',
              width: '3%',
              render: (emp) => <Icon type="delete" onClick={this.showConfirm.bind(this, emp)}  theme="twoTone" twoToneColor='#EE204D'/>,
            },

          ];

        return (
            <div>
              <Card title="Leave Requests" hoverable='true'>
                <Table rowKey={record => record.id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 7 }} size="middle" />
              </Card> 

                {leave.length!==0? <Modal
                  title="Full Leave Record"
                  visible={this.state.visible}
                  onOk={this.handleOk}
                  onCancel={this.handleOk}
                  footer={null}
                >
                  { 
                    leave.status === "Approved"?
                      <Alert message="Approved request" type="success" style={{width:'456px'}}/>
                    : leave.status === "Pending"?
                      <Alert message="Pending request" type="info" style={{width:'456px'}}/>
                    : <Alert message="RE request" type="error" style={{width:'456px'}}/>

                  }
                  <br/>
                  <p>Date of Request: <Tag color="volcano">{leave.formatDateTime}</Tag> Leave Type: <Tag color="volcano">{leave.type}</Tag></p>
                  <p>Leave Period: <Tag color="volcano">{leave.startDate} {leave.startHalf }</Tag>To  <Tag color="volcano">{leave.endDate} {leave.endHalf}</Tag></p>
                  <p>Number of Days: <Tag color="volcano">{leave.number_of_leave_days}</Tag> Status: <Tag color="volcano">{leave.status}</Tag></p>
                      <p>Supervisor 01: <Tag color="volcano">{leave.supervisor1}</Tag>  Supervisor 02: <Tag color="volcano">{leave.supervisor2}</Tag></p>
                  <p> Checked By: <Tag color="volcano">{leave.checkBy}</Tag> Checked On: <Tag color="volcano">{leave.checkTime}</Tag></p>
                  <p>Duty cover by: <Tag color="volcano">{leave.duty} </Tag></p>
                  <p>Special note: <Tag color="volcano">{leave.specialNotes}</Tag></p>
                  <p>If reject, reason: <Tag color="volcano">{leave.reject}</Tag></p>
                </Modal>: null}
            </div>
        )
    }
}    


export default ReadLeaves;
