import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import {Table, Button, Icon, Input } from 'antd';
import Highlighter from 'react-highlight-words';

class LeaveCount extends Component {
    
    componentWillMount(){
        this.setState({
            data : []
        })
        axios.get('http://localhost:5000/employee/all', 
        {
            headers: {
                Authorization: 'Bearer eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJ1c2VybmFtZSIsImV4cCI6MTU3MjI2MjMwMiwiaWF0IjoxNTcyMjQ0MzAyfQ.xeStub_a7esd_huszd4R3g5b-lYR_uJOMT24qgiv8czRnL5pQNhSGS7xdfel64P631xx_uyignG8eenrMSP0VA'
            }
        })
        .then(res => {
            console.log("res", res.data);
            this.setState({
                data : res.data
            })
        }) 
    } 

    state = {
        searchText: '',
        data : []
    };
    
      getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.state.searchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });
    
      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.setState({ searchText: selectedKeys[0] });
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.setState({ searchText: '' });
      };    
    render() {
       
        const columns = [
            {
              title: 'Id',
              dataIndex: 'emp_id',
              ...this.getColumnSearchProps('emp_id'),
            },
            {
              title: 'First Name',
              dataIndex: 'first_name',
              ...this.getColumnSearchProps('first_name'),
            },
            {
                title: 'Second Name',
                dataIndex: 'second_name',
                ...this.getColumnSearchProps('second_name'),
              },
            {
              title: 'Email',
              dataIndex: 'email',
              ...this.getColumnSearchProps('email'),
            },
            {
                title: 'Contact',
                dataIndex: 'contact',
                ...this.getColumnSearchProps('contact'),
            },
            {
                title: 'Supervisor 01',
                dataIndex: 'supervisor1',
                ...this.getColumnSearchProps('supervisor1'),
            },
            {
                title: 'Action',
                dataIndex: '',
                key: 'x',
                render: () => <Button type="danger" htmlType="submit" style={{width:'100px', float:'left'}}>
                                <Icon type="close-circle" /> 
                                Cancel
                            </Button>,
            },
          ];

        return (
            <div>
                <Table rowKey={record => record.emp_id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 2 }} size="middle" />

              
            </div>
        )
    }
}    


export default LeaveCount;
