import React from 'react';
import 'antd/dist/antd.css';
import {
  Form,
  Input,
  Select,
  Button,
  AutoComplete,
  DatePicker,
  Radio,
  Switch
} from 'antd';

const { Option } = Select;
const AutoCompleteOption = AutoComplete.Option;
const { RangePicker } = DatePicker;
const { TextArea } = Input;

class RequetLeave extends React.Component {
  state = {
    confirmDirty: false,
    autoCompleteResult: [],
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        console.log('Received values of form: ', values);
      }
    });
  };

  handleConfirmBlur = e => {
    const { value } = e.target;
    this.setState({ confirmDirty: this.state.confirmDirty || !!value });
  };

  compareToFirstPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && value !== form.getFieldValue('password')) {
      callback('Two passwords that you enter is inconsistent!');
    } else {
      callback();
    }
  };

  validateToNextPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && this.state.confirmDirty) {
      form.validateFields(['confirm'], { force: true });
    }
    callback();
  };

  handleWebsiteChange = value => {
    let autoCompleteResult;
    if (!value) {
      autoCompleteResult = [];
    } else {
      autoCompleteResult = ['.com', '.org', '.net'].map(domain => `${value}${domain}`);
    }
    this.setState({ autoCompleteResult });
  };

  render() {
    const { getFieldDecorator } = this.props.form;
    const { autoCompleteResult } = this.state;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
    };
    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 24,
          offset: 0,
        },
        sm: {
          span: 16,
          offset: 8,
        },
      },
    };
    const prefixSelector = getFieldDecorator('prefix', {
      initialValue: '86',
    })(
      <Select style={{ width: 70 }}>
        <Option value="86">+86</Option>
        <Option value="87">+87</Option>
      </Select>,
    );

    const websiteOptions = autoCompleteResult.map(website => (
      <AutoCompleteOption key={website}>{website}</AutoCompleteOption>
    ));

    return (
      <Form {...formItemLayout} onSubmit={this.handleSubmit}>

        <Form.Item label="Leave Type">
          {getFieldDecorator('gender', {
            rules: [{ required: true, message: 'Please input initials!' }],
          })(
          <Radio.Group name="radiogroup" defaultValue={1}>
          <Radio.Button value={1}>Annual</Radio.Button>
          <Radio.Button value={2}>Casual</Radio.Button>
          <Radio.Button value={3}>Medical</Radio.Button>
          <Radio.Button value={4}>Lieu</Radio.Button> 
          <Radio.Button value={5}>Special</Radio.Button>
          <Radio.Button value={6}>Maternity</Radio.Button>
          <Radio.Button value={7}>Paternity</Radio.Button>
          <Radio.Button value={8}>No Pay</Radio.Button>  
          </Radio.Group>
        )}
        </Form.Item>  
       <Form.Item label="Half Day">
          {getFieldDecorator('halfDay', {
            rules: [{ message: 'Half Day!' }],
          })(<Switch />)}
        </Form.Item>
        <Form.Item label="Leave Period">
          {getFieldDecorator('leavePeriod', {
            rules: [{ required: true, message: 'Please input first name!' }],
          })(<RangePicker />)}
        </Form.Item>
        <Form.Item label="Number of leave days">
          {getFieldDecorator('days', {
            rules: [{ message: 'Please input second name!' }],
          })(<Input style={{width: '50px'}}/>)}
        </Form.Item>
        <Form.Item label="Duties coverd by">
          {getFieldDecorator('dutiesCover', {
            rules: [{ message: 'Please input initials!' }],
          })(
            <AutoComplete
              dataSource={websiteOptions}
              onChange={this.handleWebsiteChange}
              placeholder="Enter valid employee name"
            >
              <Input />
            </AutoComplete>,
          )}
        </Form.Item>
              
        <Form.Item label="Special Notes">
          {getFieldDecorator('email', {
            rules: [{
                required: true,
                message: 'Please input your E-mail!',
              }],
          })(<TextArea rows={4} />)}
        </Form.Item>
           
        <Form.Item {...tailFormItemLayout}>
          <Button type="primary" htmlType="submit">
            Register
          </Button>
        </Form.Item>
      </Form>
    );
  }
}

const WrappedRequestLeave = Form.create({ name: 'register' })(RequetLeave);

export default WrappedRequestLeave;
