import React, { Component } from 'react';
import axios from 'axios';
import swal from 'sweetalert'; 
//import bounce from 'react-animations';
//import styled, {keyframes} from 'styled-components';
//import { Card } from 'antd'; 
import { Form, Icon, Input, Button } from 'antd';
import 'antd/dist/antd.css';
//import '/.index.css';
//import { height } from '@material-ui/system';

// const loginStyle = {
//     width: "90%",
//     maxWidth: "500px",
//     margin: "100px auto",
//     border: "5px solid #ddd",
//     borderRadius: "5px",
//     padding: "30px"
// }

//const Bounce = styled.div`animation: 2s ${keyframes`${bounce}`}infinite`;

//const { Meta } = Card;

class Delete extends Component {
    state = {
        data: undefined,
        id: ''
    }

    componentDidMount(){
        this.setState({
            id: ''
        })
    }

    handleChange = (e) => {
        this.setState({
          [e.target.id] : e.target.value
        })
    }

    handleSubmit = (e) => {
        if(this.state.id!==''){
            axios.get('http://localhost:5000/delete/'+this.state.id)
            .then(res => {
                swal("Omg!",res.data,"success"); 
                console.log("ccccccc",this.state.id,res.data) 
            }).catch( err => {
                console.log("eeeeeeee",this.state.id,err)
                swal("Oops!","User does not exist!!!","error");
            })
        }else{
            swal("Ohh!","Id Number is empty!!!","warning");
        }
        console.log("faaaaaak",this.state.id)
        //this.props.history.push('/');
    }

    render() {
        const { getFieldDecorator } = this.props.form;
        return (
            <div>
                
                <Form className="login-form" onSubmit={this.handleSubmit}>
                <Form.Item>
                    {getFieldDecorator('Id', {
                    rules: [{ required: true, message: 'Please input Id number!' }],
                    })(
                    <Input
                    id="id"
                    prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                    placeholder="Id Number"
                    onChange={this.handleChange}
                    />
                    )}
                </Form.Item>
                
                <Form.Item>
                <Button type="primary" htmlType="submit">
                    Delete
                </Button>
                </Form.Item>
                </Form>
                   
        </div>
        )
    }
}

const DeleteForm = Form.create({ name: 'normal_login' })(Delete);
export default DeleteForm


