import React from 'react';
import 'antd/dist/antd.css';
import {
  Form,
  Input,
  Tooltip,
  Icon,
  Select,
  Button,
  AutoComplete,
  DatePicker,
  Radio,
  Card,
  Row,
  Col
} from 'antd';

const { Option } = Select;
const AutoCompleteOption = AutoComplete.Option;

class AddEmployee extends React.Component {
  state = {
    confirmDirty: false,
    autoCompleteResult: [],
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        console.log('Received values of form: ', values);

        var passwordHash = require('password-hash');
        var hashedPassword = passwordHash.generate("viz");

        const employee = {
          first_name: values.firsName || undefined,
          second_name: values.secondName || undefined,
          initials: values.initials || undefined,
          gender: values.gender || undefined,
          email: values.email || undefined,
          residence: values.ressidence || undefined,
          contact: values.contact || undefined,
          role: values.role || undefined,
          department: values.department || undefined,
          supervisor1: values.supervisor1 || undefined,
          supervisor2: values.supervisor2 || undefined,
          join_date: values.join || undefined,
          confirm_date: values.confirm || undefined,

        }
        console.log('Values of object: ', employee);
      }
    });
  };

  handleConfirmBlur = e => {
    const { value } = e.target;
    this.setState({ confirmDirty: this.state.confirmDirty || !!value });
  };

  compareToFirstPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && value !== form.getFieldValue('password')) {
      callback('Two passwords that you enter is inconsistent!');
    } else {
      callback();
    }
  };

  validateToNextPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && this.state.confirmDirty) {
      form.validateFields(['confirm'], { force: true });
    }
    callback();
  };

  handleWebsiteChange = value => {
    let autoCompleteResult;
    if (!value) {
      autoCompleteResult = [];
    } else {
      autoCompleteResult = ['.com', '.org', '.net'].map(domain => `${value}${domain}`);
    }
    this.setState({ autoCompleteResult });
  };

  render() {
    const { getFieldDecorator } = this.props.form;
    const { autoCompleteResult } = this.state;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
    };
    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 24,
          offset: 0,
        },
        sm: {
          span: 16,
          offset: 8,
        },
      },
    };
    const prefixSelector = getFieldDecorator('prefix', {
      initialValue: '86',
    })(
      <Select style={{ width: 70 }}>
        <Option value="86">+86</Option>
        <Option value="87">+87</Option>
      </Select>,
    );

    const websiteOptions = autoCompleteResult.map(website => (
      <AutoCompleteOption key={website}>{website}</AutoCompleteOption>
    ));

    return (
        <div>
        <Row gutter={0}>
        <Col span={15}>    
        <div className="scrollable-container-addEmployee">       
        <div className="background-addEmployee">
        <Card title='Add Employee' bordered={false} style={{ height: '1200px'}}>  
        <Form {...formItemLayout} onSubmit={this.handleSubmit}>
        <Form.Item label="Employee ID">
          {getFieldDecorator('id', {
            rules: [{ required: true, message: 'Please input employee id!' }],
          })(<Input />)}
        </Form.Item>
        <Form.Item label="First Name">
          {getFieldDecorator('firstName', {
            rules: [{ required: true, message: 'Please input first name!' }],
          })(<Input />)}
        </Form.Item>
        <Form.Item label="Second Name">
          {getFieldDecorator('secondName', {
            rules: [{ required: true, message: 'Please input second name!' }],
          })(<Input />)}
        </Form.Item>
        <Form.Item label="Initials">
          {getFieldDecorator('initials', {
            rules: [{ required: true, message: 'Please input initials!' }],
          })(<Input />)}
        </Form.Item>
        <Form.Item label="Gender">
          {getFieldDecorator('gender', {
            rules: [{ required: true, message: 'Please input initials!' }],
          })(
          <Radio.Group name="radiogroup" defaultValue={1}>
          <Radio value={1}>Male</Radio>
          <Radio value={2}>Female</Radio>
          </Radio.Group>
        )}
        </Form.Item>       
        <Form.Item label="E-mail">
          {getFieldDecorator('email', {
            rules: [
              {
                type: 'email',
                message: 'The input is not valid E-mail!',
              },
              {
                required: true,
                message: 'Please input your E-mail!',
              },
            ],
          })(<Input />)}
        </Form.Item>
        <Form.Item
          label={
            <span>
              Residence Address&nbsp;
              <Tooltip title="No/ Street/ City.">
                <Icon type="question-circle-o" />
              </Tooltip>
            </span>
          }
        >
          {getFieldDecorator('residence', {
            rules: [{ message: 'Please input your nickname!', whitespace: true }],
          })(<Input />)}
        </Form.Item>
       
        <Form.Item label="Contact Number">
          {getFieldDecorator('phone', {
            rules: [{ required: true, message: 'Please input your phone number!' }],
          })(<Input addonBefore={prefixSelector} style={{ width: '100%' }} />)}
        </Form.Item>
        <Form.Item label="Role">
          {getFieldDecorator('role', {
            rules: [{ required: true, message: 'Please input employee role!' }],
          })(
            <Select placeholder="Please select a role">
              <Option value="supervisor">Supervisor</Option>
              <Option value="permenent">Permenent</Option>
              <Option value="probation">Probation</Option>
              <Option value="intern">Intern</Option>
              <Option value="contract">Contract</Option>
            </Select>
          )}
        </Form.Item>  
        <Form.Item label="Department">
          {getFieldDecorator('department', {
            rules: [{ required: true, message: 'Please input employee department!' }],
          })(
          <Select placeholder="Please select a department">
            <Option value="javaDev">Java Development</Option>
            <Option value="webDev">Web Development</Option>
            <Option value="mobileDev">Mobile Development</Option>
            <Option value="bussinessDev">Bussiness Development</Option> 
            <Option value="ba">Bussiness Analysis</Option>   
            <Option value="qa">Quality Assurance</Option>
            <Option value="bigData">Big Data</Option>
            <Option value="support">Enterprise Support</Option>
            <Option value="finance">Finance</Option>
            <Option value="hr">Human Resource</Option> 
          </Select>
            )}
        </Form.Item>  
        <Form.Item label="Supervisor 01">
          {getFieldDecorator('supervisor1', {
            rules: [{ required: true, message: 'Please input first supervisor!' }],
          })(
            <AutoComplete
              dataSource={websiteOptions}
              onChange={this.handleWebsiteChange}
              placeholder="Enter supervisor's name"
            >
              <Input />
            </AutoComplete>,
          )}
        </Form.Item>
        <Form.Item label="Supervisor 02">
          {getFieldDecorator('supervisor2', {
            rules: [{ message: 'Please input second supervisor!' }],
          })(
            <AutoComplete
              dataSource={websiteOptions}
              onChange={this.handleWebsiteChange}
              placeholder="Enter supervisor's name"
            >
              <Input />
            </AutoComplete>,
          )}
        </Form.Item>
        <Form.Item label="Joined date">
          {getFieldDecorator('join', {
            rules: [{ required: true, message: 'Please input join date!' }],
          })(<DatePicker/>)}
        </Form.Item>
        <Form.Item label="Confirm date">
          {getFieldDecorator('confirm', {
            rules: [{ message: 'Please input confirm date!' }],
          })(<DatePicker/>)}
        </Form.Item>    
        <Row>
        <Col span={20}>  
        <Form.Item {...tailFormItemLayout}>
          <Button type="primary" htmlType="submit" style={{width:'100px'}}>
          <Icon type="check-circle" /> 
            Submit
          </Button>
        </Form.Item>
        </Col> 
        <Col span={4}> 
        <Form.Item {...tailFormItemLayout}>
          <Button type="danger" htmlType="submit" style={{width:'100px', float:'left'}}>
          <Icon type="close-circle" /> 
            Cancel
          </Button>
        </Form.Item>
        </Col> 
        </Row>
      </Form>
      </Card>
      </div>
      </div>
      </Col>
      <Col span={8}>
      <img height= '580px' width= '480px' alt="example" src="https://packagetoursturkey.com/wp-content/uploads/2018/06/beach-calm-clouds-457882.jpg" />
      </Col> 
        </Row>
    </div>
    );
  }
}

const WrappedAddEmployee = Form.create({ name: 'register' })(AddEmployee);

export default WrappedAddEmployee;
