import React, { Component } from 'react';
import Pdf from "react-to-pdf";
import "./index.css";
import {Button, Descriptions} from 'antd';
import axios from 'axios'; 
import swal from 'sweetalert';

const ref = React.createRef();
const loginStyle = {
    maxHeight: '440px',
    width: "90%",
    maxWidth: "900px",
    margin: "30px auto",
    border: "1px solid #ddd",
    borderRadius: "5px",
    paddingLeft: "60px",
    float: 'center'
}

export default class Pdfs extends Component {

    componentDidMount(){
        axios.get('http://localhost:5000/search/'+this.state.id)
        .then(res => {
            this.setState({
                data : [res.data]
            })
            console.log("aaa", this.state.data);
        }).catch( err => {
            console.log(err)
            swal("Oops!","Something Went Wrong!!!","error");
        }) 
        
        // axios.get('http://localhost:5000/searchfavo/'+this.state.id)
        // .then(res => {
        //     this.setState({
        //         favorite : res.data
        //     })
        // }).catch( err => {
        //     console.log(err)
        //     swal("Oops!","Something Went Wrong!!!","error");
        // }) 
    }

    state = {
        data: [],
        favorite: [],
        id: localStorage.getItem("id")
    } 

    render() {

        return (
            <div>
                <div style={{float: 'right'}}>
                <Pdf pageSize="100px" targetRef={ref} filename="User Details.pdf">
                    {({ toPdf }) => <Button  type="primary" onClick={toPdf} shape="circle" icon="download" />}
                </Pdf>
                </div>
                <br/>
                <div style={loginStyle} ref={ref} className="white">
                <div className="scrollable-container-pdf">
                <div className="background">
                    <br/>
                    <img float= 'right' height= '50px' alt="example" src="https://rakiya.lk/files/recruiters/1805/logo.png" />
                    <br/>
                    <br/>
                    <Descriptions title="User Info" bordered>
                        <Descriptions.Item label="Product" span={2}>
                            Cloud Database
                        </Descriptions.Item>
                        <Descriptions.Item label="Usage Time" span={3}>
                            2019-04-24 18:00:00
                        </Descriptions.Item>
                        <Descriptions.Item label="Status" span={4}>bla
                        </Descriptions.Item>
                        <Descriptions.Item label="Status" span={5}>bla
                        </Descriptions.Item>
                        <Descriptions.Item label="Status" span={6}>bla
                        </Descriptions.Item>
                        <Descriptions.Item label="Negotiated Amount">$80.00</Descriptions.Item>
                    </Descriptions>
                </div>
                </div>
                </div>
            </div>
        )
    }
}




