import React, { Component } from 'react';
import './App.css';

class App extends Component {
    
    constructor() {
        super();
        this.state = {
            turn : 'X',
            winner : undefined,
            winnerLine: undefined,
            gameEnded : false,
            board : Array(9).fill(''),
            totalMoves : 0 
        }
    }
    clicked(e) {
        if(this.state.board[e.target.dataset.square] === ''){
            //  this.state.board[e.target.dataset.square] = this.state.turn;

            let newState = Object.assign({}, this.state);
            newState.board[e.target.dataset.square] = this.state.turn;
            this.setState(newState);

            e.target.innerText = this.state.turn;
            let totalMoves=this.state.totalMoves;
            this.setState({
                turn: this.state.turn === 'X' ? 'O' : 'X',
                board: this.state.board,
                totalMoves: totalMoves+1,
                
            })
        }
        var result = this.check();
        if(result === 'X'){
            this.setState({
                gameEnded : true,
                winner : 'X',
                winnerLine : "X won the match!!!"
            })
        }else if(result === 'O'){
            this.setState({
                gameEnded : true,
                winner : 'O',
                winnerLine : "O won the match!!!"
            })
        }else if(result === 'draw'){
            this.setState({
                gameEnded : true,
                winner : 'draw',
                winnerLine : "Match is drawn!!!"
            })
        }
        console.log(this.state.totalMoves)
    }

    check() {
        
        var moves = [[0,3,6],[1,4,7],[2,5,8],[0,4,8],[2,4,6],[0,1,2],[3,4,5],[6,7,8]];
        var board = this.state.board;
        for(let i=0; i<moves.length; i++){
            if(board[moves[i][0]]===board[moves[i][1]] && board[moves[i][1]] ===board[moves[i][2]]){
                return board[moves[i][0]];
            }
        }
        if(this.state.totalMoves === 8){
            return 'draw';
        }
    }

    handleSubmit = (e) => {
        window.location.reload();
    }

    render() {
        return (
            <div style={loginStyle} className="white">
       
        <form onSubmit={this.handleSubmit}>

        <h5>Register the Users</h5>
        
        <br/>

        <div className="input-field">  
          <label htmlFor="name"> Name </label>  
          <input id="name" type="text" onChange={this.handleChange}/>       
        </div>

        <div className="input-field">  
          <label htmlFor="salary"> Salary </label>  
          <input id="salary" type="text" onChange={this.handleChange}/>       
        </div>

        <div className="input-field"> 
          <label htmlFor="password"> Password </label>  
          <input id="password" type="password" onChange={this.handleChange}/>
        </div> 
        
        <div className="input-field">
          <button className="btn blue lighten-1 z-depth-0">Sign Up</button>  
        </div>
        <div className="red-text center">
            { authError ? <p>{authError}</p>: null}
          </div>
        </form>
         
      </div>
        )
    }
}

export default App;
