import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import { Form, Card, Descriptions, Table, Modal, Rate, Tag, Row, Col, Icon, Input, Button, Badge } from 'antd';
import Highlighter from 'react-highlight-words';

class Dashboard extends Component {
    
    componentWillMount(){
        this.setState({
            data : [],
            count : []
        })
        axios.get('http://localhost:5000/leave/find/'+4, 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("ALl Leaves", res.data);
            this.setState({
                data : (res.data).reverse()
            })
        }) 

        axios.get('http://localhost:5000/leave_count/profile/'+4, 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("Count", res.data);
            this.setState({
                count : res.data
            })
        })
        
        axios.get('http://localhost:5000/employee/'+4, 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("Employee", res.data);
            this.setState({
                employee : res.data
            })
        }) 
    } 

    state = {
        data : [],
        count : [],
        leave : [],
        employee : []
    }

    showModal = (leave) => {
        this.setState({
          visible: true,
          leave: leave
        });
      };
    
      handleOk = e => {
        console.log(e);
        this.setState({
          visible: false,
        });
    };

    getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.state.searchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });

      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.setState({ searchText: selectedKeys[0] });
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.setState({ searchText: '' });
      };

    render() {

        const {leave, employee, count} = this.state;

        const columns = [ 
            
            {
                title: 'Date of request',
                dataIndex: 'formatDateTime',
                ...this.getColumnSearchProps('formatDateTime'),
            },
            {
              title: 'Leave Type',
              dataIndex: 'leave_type',
              render: type => (
               type.type
              )
            },
            {
                title: 'Start Date',
                dataIndex: 'start_date'
            },
            {
                title: 'End Date',
                dataIndex: 'end_date'
            },
            {
                title: 'Number of days',
                dataIndex: 'number_of_leave_days'
            },
            {
                title: 'Status',
                dataIndex: 'status',
                render: tag => {
                    let color;
                    if (tag === 'Pending') {
                      color = 'green';
                    }else if (tag === 'Approved') {
                      color = 'geekblue';
                    }else {
                      color = 'red';
                    }
                    return (
                      <Tag color={color} key={tag}>
                        {tag}
                      </Tag>
                    );
                  }
                // ...this.getColumnSearchProps('status')
            },
            {
              dataIndex: '',
              key: 'z',
              width: '1%',
              render: (emp) => <Icon type="info-circle" onClick={this.showModal.bind(this, emp)}  theme="twoTone" />,
            }
        ];

        return (
            <div className='center'>
              <Row gutter={16}>
                <Col span={10} > 
                  <Card hoverable='true'>
                  <Row>
                      <Col span={8}><Tag color="geekblue">Casual</Tag> &nbsp; <Tag color="volcano">{count.casual}/{employee.casual}</Tag></Col>
                      <Col span={8}><Tag color="geekblue">Medical</Tag> &nbsp; <Tag color="volcano">{count.medical}/{employee.medical}</Tag></Col>
                      <Col span={8}><Tag color="geekblue">Annual</Tag> &nbsp; <Tag color="volcano">{count.annual}/{employee.annual}</Tag></Col>
                  </Row>
                  
                      {this.state.count.length !== 0 ? 
                      <Descriptions>
                          <Descriptions.Item><Rate allowHalf defaultValue={count.casual} count={employee.casual} disabled={true} style={{color:'#FC7F03'}} character={<Icon type="bulb" theme="filled" />}/></Descriptions.Item>
                          <Descriptions.Item><Rate allowHalf defaultValue={count.medical} count={employee.medical} disabled={true} style={{color:'#FC7F03'}} character={<Icon type="bulb" theme="filled" />}/></Descriptions.Item>
                          <Descriptions.Item><Rate allowHalf defaultValue={count.annual} count={employee.annual} disabled={true} style={{color:'#FC7F03'}} character={<Icon type="bulb" theme="filled" />}/></Descriptions.Item>
                      </Descriptions> : null}
                  </Card>
                </Col>
                <Col span={14}>  
                  <Card hoverable='true'>
                    {employee.length !== 0 ?
                    <Descriptions title="User Infomation" size="small" bordered>
                      <Descriptions.Item label="Name">{employee.first_name} {employee.second_name}</Descriptions.Item>
                      <Descriptions.Item label="Email" span={2}>{employee.email}</Descriptions.Item>
                      <Descriptions.Item label="Residence" >{employee.residence}</Descriptions.Item>
                      <Descriptions.Item label="Contact No" span={3}> 0{employee.contact}</Descriptions.Item>
                      <Descriptions.Item label="Role">{employee.role.role_name}</Descriptions.Item>
                      <Descriptions.Item label="Department" span={2}>{employee.department.department_name}</Descriptions.Item>
                      <Descriptions.Item label="Status" span={3}>
                        <Badge status="processing" text="Running" />
                      </Descriptions.Item>
                      <Descriptions.Item label="Supervisor 01">{employee.supervisor1}</Descriptions.Item>
                      <Descriptions.Item label="Supervisor 02" span={2}>{employee.supervisor2}</Descriptions.Item>
                      <Descriptions.Item label="Join Date">{employee.join_date}</Descriptions.Item>
                      <Descriptions.Item label="Confirm Date">{employee.confirm_date}</Descriptions.Item>
                    </Descriptions> : null}
                  </Card>
                </Col>
            </Row>
                <br/>
              
                <Card hoverable='true' >
                    <Table rowKey={record => record.leave_id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 7 }} size="middle" />
                </Card>
                {leave.length!==0? <Modal
                  title="Full Leave Record"
                  visible={this.state.visible}
                  onOk={this.handleOk}
                  onCancel={this.handleOk}
                  footer={null}
                >
                  <p>Date of Request: <Tag color="volcano">{leave.formatDateTime}</Tag> Leave Type: <Tag color="volcano">{leave.leave_type.type}</Tag></p>
                  <p>Leave Period: <Tag color="volcano">{leave.start_date} {leave.startHalf }</Tag>To  <Tag color="volcano">{leave.end_date} {leave.endHalf}</Tag></p>
                  <p>Number of Days: <Tag color="volcano">{leave.number_of_leave_days}</Tag></p>
                  <p>Status: <Tag color="volcano">{leave.status}</Tag> </p>
                  <p>Duty cover by: <Tag color="volcano">{leave.duty.first_name} {leave.duty.second_name}</Tag></p>
                  <p>Special note: <Tag color="volcano">{leave.special_notes}</Tag></p>
                  <p>If reject, reason: <Tag color="volcano">{leave.reject}</Tag></p>
                </Modal>: null}
            </div>
        )
    }
}    

