import React, { Component } from "react";
import { Icon, Form, Button, Row, Col, Input } from 'antd';

export default class ChangePassword extends Component {

    changePassword = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
          if (!err) {
            console.log("Received values of form: ", values);
    
            var passwordHash = require('password-hash');
            var hashedPassword = passwordHash.generate(values.newpassword);
            const user_id = localStorage.getItem("id");
    
            axios.get('http://localhost:5000/change/'+user_id)
            .then(res => {
                if(passwordHash.verify(values.password, res.data.password)){
                  const user = {
                    user_id : user_id || undefined,
                    password: hashedPassword || undefined
                  }
                  axios.post('http://localhost:5000/changepassword',user)
                  .then(res => {
                    if(res.data){
                      swal("Yeah!","Password Updated !!!","success");
                      this.setState({
                        visible: false
                      });
                    }
                    else{
                      swal("Oops!","Something Went Wrong!!!","error"); 
                    }
                  }).catch(e => console.log("eeeeeeeeeeee",e))
                }
                else{
                  swal("Oops!","Invalid Current Password !!!","warning");
                }
            }).catch(e => {
                console.log("eeeeeeeeeeee",e)
                swal("Oops!","Something Went Wrong!!!","error");          
              });
          }
        });
      }
    
      handleConfirmBlur = e => {
        const { value } = e.target;
        this.setState({ confirmDirty: this.state.confirmDirty || !!value });
      };
    
      compareToFirstPassword = (rule, value, callback) => {
        const { form } = this.props;
        if (value && value !== form.getFieldValue("newpassword")) {
          callback("Two passwords that you enter is inconsistent!");
        } else {
          callback();
        }
      };
    
      validateToNextPassword = (rule, value, callback) => {
        const { form } = this.props;
        if (value && this.state.confirmDirty) {
          form.validateFields(["confirm"], { force: true });
        }
        callback();
      };
    render () {
        return(
            <div>
                 <Form layout="vertical" hideRequiredMark>
            
                    <Row gutter={16}>
                    <Form.Item hasFeedback>
                        {getFieldDecorator("password", {
                        rules: [
                            {
                            required: true,
                            message: "Please input your current password!"
                            },
                            {
                            validator: this.validateToNextPassword
                            }
                        ]
                        })(<Input.Password 
                        id= '1'
                        prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                        placeholder="Current Password"
                        />)}
                    </Form.Item>
                    </Row>

                    <Row gutter={16}>
                    <Form.Item hasFeedback>
                        {getFieldDecorator("newpassword", {
                        rules: [
                            {
                            required: true,
                            message: "Please input your new password!"
                            },
                            {
                            validator: this.validateToNextPassword
                            }
                        ]
                        })(<Input.Password 
                        id= '2'
                        prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                        placeholder="New Password"
                        />)}
                    </Form.Item>
                    </Row>

                    <Row gutter={16}>
                    
                    <Form.Item hasFeedback>
                        {getFieldDecorator("confirm", {
                        rules: [
                            {
                            required: true,
                            message: "Please confirm your new password!"
                            },
                            {
                            validator: this.compareToFirstPassword
                            }
                        ]
                        })(<Input.Password 
                        id= '3'
                        onBlur={this.handleConfirmBlur}
                        prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                        placeholder="Confirm Password"
                        />)}
                    </Form.Item>
                    </Row>
                    <Row>
                        <Col span={17}>  
                        <Form.Item {...tailFormItemLayout}>
                        <Button type="primary" htmlType="submit" style={{width:'100px', float:'right'}}>
                        <Icon type="check-circle" /> 
                            Submit
                        </Button>
                        </Form.Item>
                        </Col> 

                        <Col span={7}> 
                        <Form.Item {...tailFormItemLayout}>
                        <Button type="danger" onClick={this.props.close} style={{width:'100px', float:'left'}}>
                        <Icon type="close-circle" /> 
                            Cancel
                        </Button>
                        </Form.Item>
                        </Col> 
                        </Row>
                </Form>
            </div>
        );
    }
}
