import React from "react";
import axios from 'axios';
import swal from 'sweetalert';
import "antd/dist/antd.css";
import "../../container/index.css";

import {
  Form,
  Input,
  Icon,
  Button,
  Card,
  Menu
} from "antd";

class Details extends React.Component {
  state = {
    confirmDirty: false,
    basic: false,
    contact: false,
    educational: false,
    Experions: false
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        
        const favorite = {
            user_id: localStorage.getItem("id"),
            user_name: localStorage.getItem("user"),
            food: values.food || undefined,
            drink: values.drink || undefined,
            animal: values.animal || undefined,
            bird: values.bird || undefined,
            hobby: values.hobby || undefined,
            place: values.place || undefined
        }
        axios.post('http://localhost:5000/savefavo', favorite)
        .then(res => {
            console.log("res", res.data);
            swal("OMG!","User Favorite Submited!!!","success");
        }).catch(e => console.log("eeeeeeeeeeee",e));
        this.props.history.push('/read'); 
      }
    });
  };

  handleConfirmBlur = e => {
    const { value } = e.target;
    this.setState({ confirmDirty: this.state.confirmDirty || !!value });
  };

  compareToFirstPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && value !== form.getFieldValue("password")) {
      callback("Two passwords that you enter is inconsistent!");
    } else {
      callback();
    }
  };

  validateToNextPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && this.state.confirmDirty) {
      form.validateFields(["confirm"], { force: true });
    }
    callback();
  };

  render() {
    const { getFieldDecorator } = this.props.form;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 }
      }
    };

    return (
      <div style={{ background: '#ECECEC', paddingLeft: '130px', paddingRight: '135px', height: '420px' }}>
      
      <Menu onClick={this.handleClick} selectedKeys={[this.state.current]} mode="horizontal">
        <Menu.Item key="1">
          <Icon type="mail" />
          Basic Details
        </Menu.Item>
        <Menu.Item key="2" >
          <Icon type="appstore" />
          Contact Details
        </Menu.Item>
        <Menu.Item key="3" >
          <Icon type="appstore" />
          Educational Details
        </Menu.Item>
        <Menu.Item key="4" >
          <Icon type="appstore" />
          Experions Details
        </Menu.Item>
      </Menu> 
      <div className="scrollable-container-fav">       
      <div className="background">
      <Card bordered={false} style={{ width: 950, height: '680px'}}>
      <Switch>
                <Route path='/signup' component={Signup} />
      </Switch>
      </Card>
      </div>
      </div>
      </div>
    );
  }
}

const WrappedRegistrationForm = Form.create({ name: "favorite" })(
  Details
);


export default WrappedRegistrationForm;

