import React from 'react';
import 'antd/dist/antd.css';
import {withRouter} from 'react-router-dom';
import moment from 'moment';
import axios from 'axios';
import { PageHeader, Icon, Button, Modal, Row, Col, Avatar, Tag, Drawer, List, message } from 'antd';

const { confirm } = Modal;

class Header extends React.Component { 

    handleLogout = (e) => {
        e.preventDefault();
        confirm({
          title: 'Do you want to logout?',
          onOk: () => {
            localStorage.removeItem("header");
            this.props.history.push('/login')
          },
          onCancel() {
            console.log('Cancel');
          },
        });
    }

    state = {
        visible: false,
        data: []
    };

    toggle = () => {
        this.setState({
            data : [],
            visible: true
        })
        axios.get('http://localhost:5000/leave/absent', 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            if (res.data.success === true) {
                this.setState({
                    data : res.data.list
                })
            } else {
                message.error(res.data.message);
            }
        }).catch( err => {
            console.log(err.response.data.error);
            message.error("Something went wrong");
        })
    };

    onClose = () => {
        this.setState({
          visible: false
        });
    };

    render () {

    return (
        <div>
        <PageHeader
            style={{   
            border: '1px solid rgb(235, 237, 240)',
            backgroundImage: 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSQhsbd4x-xRzJrNDxCCL0qT7mQa_tk4jS9WvFALCBAbKFSWwqw',
            }}
        >
            <Row>
                <Col span={2}>
                    <Avatar size={50} icon="user"  src= 'https://avatars1.githubusercontent.com/u/8186664?s=460&v=4'/>
                </Col>
                <Col span={18}>
                    <span style={{fontSize:'17px'}}>{this.props.name}</span><br/>
                    <span style={{fontSize:'13px', color:'gray'}}>{this.props.department}</span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                    <span style={{fontSize:'13px', color:'red'}}>{this.props.role}</span> 
                </Col>
                <Col span={4}>
                    <Tag color="volcano">{moment().format('LL')}</Tag>&nbsp;&nbsp;&nbsp;&nbsp;
                    <Button key="1" type="primary" onClick={this.toggle} shape="circle">
                        <Icon 
                            className="trigger"
                            type= 'menu-fold' 
                        />
                    </Button>,&nbsp;&nbsp;&nbsp;
                    <Button key="2" type="primary" onClick={this.handleLogout} shape="circle">
                        <Icon type="logout" />
                    </Button>
                </Col>
            </Row>
        </PageHeader>

            <Drawer
            title="Absent on today"
            width={300}
            onClose={this.onClose}
            visible={this.state.visible}
            >
                <br/><br/>
                <List
                    itemLayout="horizontal"
                    dataSource={this.state.data}
                    renderItem={item => (
                    <List.Item>
                        <List.Item.Meta
                        avatar={<Avatar src="https://avatars1.githubusercontent.com/u/8186664?s=460&v=4" />}
                        title={item.user.firstName + " " + item.user.secondName}
                        />
                    </List.Item>
                    )}
                />
            </Drawer>
        </div>
        );
    }
}          

export default withRouter(Header);
