import React from 'react';
import 'antd/dist/antd.css';
import axios from 'axios';
// import swal from 'sweetalert';
import {
  Form,
  Input,
  Button,
  DatePicker,
  Radio,
  Switch,
  Card,
  Icon,
  Select,
  message
} from 'antd';

const { TextArea } = Input;
const { Option } = Select;

class RequestLeave extends React.Component {
  
  componentWillMount() {
    this.setState({
      employees : [],
      types : [],
      count : []
    })

    axios.get('http://localhost:5000/employee/all', 
    {
        headers: {
            Authorization: 'Bearer ' + localStorage.getItem("header")
        }
    })
    .then(res => {
        console.log("Employees:  ", res.data);
        this.setState({
            employees : res.data
        })
    })

    axios.get('http://localhost:5000/leave_type/all', 
    {
        headers: {
            Authorization: 'Bearer ' + localStorage.getItem("header")
        }
    })
    .then(res => {
        console.log("Leave Types:  ", res.data);
        this.setState({
            types : res.data
        })
    })

    axios.get('http://localhost:5000/leave_count/profile/'+4, 
    {
        headers: {
            Authorization: 'Bearer ' + localStorage.getItem("header")
        }
    })
    .then(res => {
        console.log("Leave Count", res.data);
        this.setState({
            count : res.data
        })
    }) 
  }

  state = {
    types : [],
    employees : [],
    period : undefined,
    disable : false,
    startValue: null,
    endValue: null,
    endOpen: false,
    count: [],
    type: null,
    error: null
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err && this.state.error==null) {

        const leave = {
          leave_type : { leave_type_id : values.leave_type } || undefined,
          employee : { emp_id : 4 } || undefined,
          start_date : this.state.startValue || undefined,
          end_date : this.state.endValue || this.state.startValue,
          half_day : values.halfDay || false,
          number_of_leave_days : parseFloat(values.days) || undefined,
          duty : { emp_id : parseInt(values.duty) } || this.state.employees[0],
          special_notes : values.note || "No special note.",
          status : "Pending",
          reject : "Not reject"
        }
        
        console.log('Received values of form: ', leave);

        axios.post(
          'http://localhost:5000/leave/request', 
          leave, 
          { 
              headers: {
                  Authorization: 'Bearer ' + localStorage.getItem("header")
              }
          }
        )
        .then(res => {
          console.log("res", res.data);
          message.success(res.data); 
          this.props.form.resetFields();
          // window.location.reload();
        })
        .catch(e => {
          console.log("Error", e.response);
          message.error(e.response.data.error);
          // swal("Oops!","Something Went Wrong !!!","error"); 
        })
      }else{
        message.error("Leave limit exceeded!");
      }
    });
  };

  handleCancel = () => {
    this.props.form.resetFields();
  };

  disabledStartDate = startValue => {
    const { endValue } = this.state;
    if (!startValue || !endValue) {
      return false;
    }
    return startValue.valueOf() > endValue.valueOf();
  };

  disabledEndDate = endValue => {
    const { startValue } = this.state;
    if (!endValue || !startValue) {
      return false;
    }
    return endValue.valueOf() <= startValue.valueOf();
  };

  onChange = (field, value) => {
    this.setState({
      [field]: value,
    });
  };

  onStartChange = (date, dateString) => {
    this.onChange('startValue', dateString);
    console.log(date)
    console.log(dateString)
  };

  onEndChange = (date, dateString) => {
    var e = new Date(date)
    var s = new Date(this.state.startValue)
    var dif = parseInt(e-s)/(1000*60*60*24)
    this.checkCount(parseInt(dif)+1);
    this.onChange('endValue', dateString);
    this.onChange('period', parseInt(dif)+1);
    
  };

  setType = (e) => {
    this.onChange('type', this.state.types[e.target.value-1].type);
  }

  checkCount = (period) => {
    if(this.state.type === 'Casual' && period > (this.state.types[0].count-this.state.count.casual)){
      this.onChange('error', "* Exceed your casual limit");
    }else if(this.state.type === 'Medical' && period > (this.state.types[1].count-this.state.count.medical)){
      this.onChange('error', "* Exceed your medical limit");
    }else if(this.state.type === 'Maternity' && period > (this.state.types[2].count-this.state.count.maternity)){
      this.onChange('error', "* Exceed your maternity limit");
    }else if(this.state.type === 'Paternity' && period > (this.state.types[3].count-this.state.count.paternity)){
      this.onChange('error', "* Exceed your paternity limit");
    }else if(this.state.type === 'Annual' && period > (this.state.types[4].count-this.state.count.annual)){
      this.onChange('error', "* Exceed your annual limit");  
    }else this.onChange('error', null);;  
  }

  handleStartChange = open => {
    if (!open && !this.state.disable) {
      this.setState({ endOpen: true });
    }
  };

  handleEndChange = open => {
    this.setState({ endOpen: open });
  };

  halfDay = (value) => {
    if(value){
      this.setState({
        period : 0.5,
        disable : true,
        endValue : this.state.startValue
      })
    }else{
      this.setState({
        period : null,
        disable : false
      })
    }  
  } 

  render() {
    const { getFieldDecorator } = this.props.form;
    const { endOpen } = this.state;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
    };
    
    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 24,
          offset: 0,
        },
        sm: {
          span: 16,
          offset: 8,
        },
      },
    };

    return (
        <div>
        <Card title='Request Leave' bordered={false} hoverable='true'>  
        <Form {...formItemLayout} onSubmit={this.handleSubmit}>

        <Form.Item label="Leave Type">
          {getFieldDecorator('leave_type', {
            rules: [{ required: true, message: 'Please input leave type!' }],
          })(
          <Radio.Group name="radiogroup" style={{ width: '560px' }} onChange={this.setType}>
          {this.state.types.map(item => (
                  <Radio.Button key={item.leave_type_id} value={item.leave_type_id}>{item.type}</Radio.Button>
          ))}   
          </Radio.Group>
        )}
        </Form.Item>  
       
        <Form.Item label="Half Day">
          {getFieldDecorator('halfDay', {
            valuePropName: 'checked'
          })(
          <Switch
            checkedChildren={<Icon type="check" />}
            unCheckedChildren={<Icon type="close" />}
            onChange= {this.halfDay} 
          />
          )}
        </Form.Item>

        <Form.Item label="Leave Period">
          <React.Fragment>  
          <DatePicker
            disabledDate={this.disabledStartDate}
            format="YYYY-MM-DD"
            placeholder="Start"
            onChange={this.onStartChange}
            onOpenChange={this.handleStartChange}
            style={{ width: '280px' }}
          />
          <DatePicker
            disabledDate={this.disabledEndDate}
            format="YYYY-MM-DD"
            placeholder="End"
            onChange={this.onEndChange}
            open={endOpen}
            onOpenChange={this.handleEndChange}
            disabled = {this.state.disable}
            style={{ width: '280px' }}
          />
          </React.Fragment>
        </Form.Item>

        <Form.Item label="Number of leave days">
          {getFieldDecorator('days', {
            rules : [{ 
              required: true
            }],
            initialValue : this.state.period
          })(<Input style={{width: '50px'}} disabled={true}/>)}
          <span style={{color:'red'}}>&nbsp;&nbsp; {this.state.error}</span>
        </Form.Item>
        {this.state.employees.length!==0?  
        <Form.Item label="Duty Cover By">
          {getFieldDecorator('duty',
          {
            initialValue: this.state.employees[0].first_name
          }
          )(
          <Select placeholder="Please select a employee" style={{ width: '560px' }}>
            {this.state.employees.map(item => (
                  <Option key={item.emp_id}>{item.first_name} {item.second_name}</Option>
            ))}
          </Select>
          )}
        </Form.Item>
        : null }      
        <Form.Item label="Special Notes">
          {getFieldDecorator('note', {
            rules: [{
                message: 'Please input your note',
              }],
          })(<TextArea rows={4} style={{ width: '560px' }}/>)}
        </Form.Item>

        <Form.Item {...tailFormItemLayout}>
          <Button type="primary" htmlType="submit" style={{width:'100px'}}>
          <Icon type="check-circle" /> 
            Submit
          </Button>
          &nbsp;&nbsp;&nbsp;&nbsp;
          <Button type="danger" onClick={this.handleCancel} style={{width:'100px'}}>
          <Icon type="close-circle" /> 
            Cancel
          </Button>
        </Form.Item>
      </Form>
      </Card>
    </div>
    );
  }
}

const WrappedRequestLeave = Form.create({ name: 'register' })(RequestLeave);

export default WrappedRequestLeave;
