import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import { Card, Descriptions, Table, Modal, Rate, Tag, Row, Col, Icon } from 'antd';

function info(leave) {
    console.log(this.state.count.casual);
    Modal.info({
      title: 'Fully Leave Record',
      content: (
        <div>
         
        </div>
      ),
      onOk() {},
    });
}

class ViewOne extends Component {
    
    componentWillMount(){
        this.setState({
            data : [],
            count : []
        })
        axios.get('http://localhost:5000/leave/find/'+localStorage.getItem("leave_count"), 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("leaves", res.data);
            this.setState({
                data : (res.data).reverse()
            })
        }) 

        axios.get('http://localhost:5000/leave_count/profile/'+localStorage.getItem("leave_count"), 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("count", res.data);
            this.setState({
                count : res.data,
                special : res.data.casual
            })
        }) 

        const employee = { emp_id: localStorage.getItem("leave_count") } 
        axios.post('http://localhost:5000/contact/employee', employee,
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("contact", res.data);
            this.setState({
                contact: res.data
            })
        })
    } 

    componentDidMount() {
        console.log("sssssssssssssss",this.state.special)
    }

    state = {
        data : [],
        count : [],
        contact : [],
        special : null
    }

    render() {

        const columns = [ 
            
            {
                title: 'Date of request',
                dataIndex: 'formatDateTime',
            },
            {
                title: 'Leave Type',
                dataIndex: 'leave_type',
                render: type => (
                type.type
                )
            },
            {
                title: 'Start Date',
                dataIndex: 'start_date'
            },
            {
                title: 'End Date',
                dataIndex: 'end_date'
            },
            {
                title: 'Number of days',
                dataIndex: 'number_of_leave_days'
            },
            {
                title: 'Status',
                dataIndex: 'status',
            },
            {
              dataIndex: '',
              key: 'z',
              width: '1%',
              render: (emp) => <Icon type="info-circle" onClick={info.bind(this, emp)}  theme="twoTone" />,
            }
        ];

        const contacts = [
            {
                title: 'Name',
                dataIndex: 'name'
            },
            {
                title: 'Contact',
                dataIndex: 'contact'
            },
            {
                title: 'Relation',
                dataIndex: 'relation',
            }
        ];

        return (
            <div className='center'>
                 <Card hoverable='true'>
                    <Table rowKey={record => record.contact_id} columns={contacts} dataSource={this.state.contact}  pagination={{ pageSize: 5 }} size="middle" bordered={true}/>
                </Card>
                <br/>
                
                <Card hoverable='true'>
                <Row>
                    <Col span={8}><Tag color="geekblue">Casual</Tag></Col>
                    <Col span={8}><Tag color="geekblue">Medical</Tag></Col>
                    <Col span={8}><Tag color="geekblue">Annual</Tag></Col>
                </Row>
                
                    <Descriptions>
                        <Descriptions.Item><Rate allowHalf defaultValue={this.state.count.casual} count={7} disabled={true} style={{color:'#FC7F03'}} character={<Icon type="bulb" theme="filled" />}/> {this.state.count.casual}</Descriptions.Item>
                        <Descriptions.Item><Rate allowHalf defaultValue={3.5} count={7} disabled={true} style={{color:'#FC7F03'}} character={<Icon type="bulb" theme="filled" />}/></Descriptions.Item>
                        <Descriptions.Item><Rate allowHalf defaultValue={8.5} count={14} disabled={true} style={{color:'#FC7F03'}} character={<Icon type="bulb" theme="filled" />}/></Descriptions.Item>
                    </Descriptions>
                </Card>
                <br/>

                <Card hoverable='true' >
                    <Table rowKey={record => record.leave_id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 7 }} size="middle"/>
                </Card>
              
            </div>
        )
    }
}    

export default ViewOne;
