import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import { Form, Card, Descriptions, Table, Modal, Rate, Tag, Row, Col, Icon } from 'antd';

function info(leave) {
    console.log(this.state.count.casual);
    Modal.info({
      title: 'Fully Leave Record',
      content: (
        <div>
            <p> Date of Request: {leave.formatDateTime}</p><br/>
            <p> Leave Type:  Number of Days: {leave.number_of_leave_days}</p><br/>
            <p> Leave Period: {leave.start_date} to {leave.end_date}</p> <br/>
            <p> Status: {leave.status}</p>
        </div>
      ),
      onOk() {},
    });
}

class Dashboard extends Component {
    
    componentWillMount(){
        this.setState({
            data : [],
            count : []
        })
        axios.get('http://localhost:5000/leave/find/'+4, 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("res", res.data);
            this.setState({
                data : (res.data).reverse()
            })
        }) 

        axios.get('http://localhost:5000/leave_count/profile/'+4, 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("res", res.data);
            this.setState({
                count : res.data
            })
        }) 
    } 

    componentDidMount() {
        console.log(this.state.count.special)
    }

    state = {
        data : [],
        count : [],
    }

    render() {

        const columns = [ 
            
            {
                title: 'Date of request',
                dataIndex: 'formatDateTime',
            },
            {
              title: 'Leave Type',
              dataIndex: 'leave_type',
              render: type => (
               type.type
              )
            },
            {
                title: 'Start Date',
                dataIndex: 'start_date'
            },
            {
                title: 'End Date',
                dataIndex: 'end_date'
            },
            {
                title: 'Number of days',
                dataIndex: 'number_of_leave_days'
            },
            {
                title: 'Status',
                dataIndex: 'status',
                // ...this.getColumnSearchProps('status')
            },
            {
              dataIndex: '',
              key: 'z',
              width: '1%',
              render: (emp) => <Icon type="info-circle" onClick={info.bind(this, emp)}  theme="twoTone" />,
            }
        ];

        return (
            <div className='center'>
                <Card hoverable='true'>
                <Row>
                    <Col span={8}><Tag color="geekblue">Casual</Tag> &nbsp; <Tag color="volcano">{this.state.count.casual}/7</Tag></Col>
                    <Col span={8}><Tag color="geekblue">Medical</Tag> &nbsp; <Tag color="volcano">{this.state.count.medical}/7</Tag></Col>
                    <Col span={8}><Tag color="geekblue">Annual</Tag> &nbsp; <Tag color="volcano">{this.state.count.annual}/14</Tag></Col>
                </Row>
                
                    <Descriptions>
                        <Descriptions.Item><Rate allowHalf defaultValue={this.state.count.casual} count={7} disabled={true} style={{color:'#FC7F03'}} character={<Icon type="bulb" theme="filled" />}/></Descriptions.Item>
                        <Descriptions.Item><Rate allowHalf defaultValue={3.5} count={7} disabled={true} style={{color:'#FC7F03'}} character={<Icon type="bulb" theme="filled" />}/></Descriptions.Item>
                        <Descriptions.Item><Rate allowHalf defaultValue={8.5} count={14} disabled={true} style={{color:'#FC7F03'}} character={<Icon type="bulb" theme="filled" />}/></Descriptions.Item>
                    </Descriptions>
                </Card>
                <br/>

                <Card hoverable='true' >
                    <Table rowKey={record => record.leave_id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 7 }} size="middle" />
                </Card>
              
            </div>
        )
    }
}    

const Dash = Form.create({name: "dashboard"})(Dashboard);

export default Dash;

