import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import {Table, Button, Icon, Input, Drawer, Card, Modal, Form, Tag } from 'antd';
import Highlighter from 'react-highlight-words';
import swal from 'sweetalert';
import UpdateEmployee from '../Employee/UpdateEmployee';
import AddEmployee from '../Employee/AddEmployee';


const CollectionCreateForm = Form.create({ name: 'form_in_modal' })(

  class extends React.Component {
    render() {
      const { visible, onCancel, onCreate, form } = this.props;
      const { getFieldDecorator } = form;
      return (
        <Modal
          visible={visible}
          title="Reject the request"
          okText="Reject"
          onCancel={onCancel}
          onOk={onCreate}
        >
          <Form layout="vertical">
            <Form.Item label="Reason">
              {getFieldDecorator('reason', {
                rules: [{ required: true, message: 'Please input the reaon for reject!' }],
              })(<Input type="textarea"/>)}
            </Form.Item>
          </Form>
        </Modal>
      );
    }
  },
);

class PendingLeaves extends Component {
    
    componentWillMount(){
        this.setState({
            data : []
        })

        axios.get('http://localhost:5000/leave/pending', 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("res", res.data);
            this.setState({
                data : (res.data).reverse()
            })
        }) 
    } 

    state = {
        visible: false,
        searchText: '',
        data : [],
        reject: null,
        visibleAccept: false,
        confirmLoading: false,
        leave: []
    };
    
      getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.state.searchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });
    
      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.setState({ searchText: selectedKeys[0] });
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.setState({ searchText: '' });
      };   

    handleInfo = (emp) => {
      swal(
        `
        Id: ${emp.emp_id} 
        Name: ${emp.first_name} ${emp.second_name}
        Initials: ${emp.initials}
        Email: ${emp.email}
        Contact: 0${emp.contact}
        Address: ${emp.residence}
        Supervisor 01: ${emp.supervisor1}
        Supervisor 02: ${emp.supervisor2}
        Join Date: ${emp.join_date}
        Confirm Date: ${emp.confirm_date}
         `,
      );
      console.log(emp)
    }

    show = (leave) => {
      this.setState({
        visibleAccept: true,
        leave: leave
      });
    };
  
    handleOk = () => {
      this.setState({
        confirmLoading: true,
      });
      setTimeout(() => {
        this.setState({
          visibleAccept: false,
          confirmLoading: false,
        });
      }, 1000);
    };

    handleApprove(leave, e) {
        e.preventDefault();

        swal({
            text: "Wanna accept that request?",
            buttons: {
                confirm: "Confirm",
                cancel: true,
            }
        })
        .then(willSearch => {
            if (willSearch) {
                axios.get('http://localhost:5000/leave/approve/'+leave.leave_id, 
                {
                    headers: {
                        Authorization: 'Bearer ' + localStorage.getItem("header")
                    }
                })
                .then(res => {
                    swal("Yeah!",res.data,"success"); 
                    window.location.reload(); 
                }).catch( err => {
                    console.log("eeeeeeeeeeeeee",err)
                    swal("Oops!","Something Went Wrong!!!","error");
                })
            }
        })
    }

    handleDelete(leave,e) {
        e.preventDefault();
        console.log("Delete record: ", leave.leave_id)
        swal({
            text: "Wanna delete that request?",
            buttons: {
                confirm: "Delete",
                cancel: true,
            }
        })
        .then(willSearch => {
            if (willSearch) {
                axios.get('http://localhost:5000/leave/delete/'+leave.leave_id, 
                {
                    headers: {
                        Authorization: 'Bearer ' + localStorage.getItem("header")
                    }
                })
                .then(res => {
                    swal("Yeah!",res.data,"success"); 
                    window.location.reload(); 
                }).catch( err => {
                    console.log("eeeeeeeeeeeeee",err)
                    swal("Oops!","Something Went Wrong!!!","error");
                })
            }
        })
    }
    
    showModal = (leave) => {
      this.setState({ 
        visible: true,
        reject: leave.leave_id
      });
    };
  
    handleCancel = () => {
      this.setState({ 
        visible: false,
        visibleAccept: false 
      });
    };
  
    handleCreate = () => {
      const { form } = this.formRef.props;
      form.validateFields((err, values) => {
        if (!err) {
          axios.post('http://localhost:5000/leave/reject/'+this.state.reject, 
                {
                  reject:values.reason
                },
                {
                    headers: {
                        Authorization: 'Bearer ' + localStorage.getItem("header")
                    }
                })
                .then(res => {
                    swal("Yeah!",res.data,"success"); 
                    window.location.reload(); 
                }).catch( err => {
                    console.log("eeeeeeeeeeeeee",err)
                    swal("Oops!","Something Went Wrong!!!","error");
                })
          this.setState({ visible: false });
        }
        form.resetFields();
        
      });
    };
  
    saveFormRef = formRef => {
      this.formRef = formRef;
    };

    render() {
      const { visibleAccept, confirmLoading, leave } = this.state;
        const columns = [
            {
              title: 'Name',
              key: '0',
              dataIndex: 'employee',
              render: emp => (
               emp.first_name
              ),
            //   ...this.getColumnSearchProps('first_name'),
            },  
            {
              title: 'Supervisor 01',
              key: '1',
              dataIndex: 'employee',
              render: emp => (
               emp.supervisor1
              ),
            },
            {
                title: 'Supervisor 02',
                key: '2',
                dataIndex: 'employee',
                render: emp => (
                 emp.supervisor2
                ),
              },
            {
              title: 'Leave Type',
              dataIndex: 'leave_type',
              render: type => (
               type.type
              )
            },
            {
                title: 'Start Date',
                dataIndex: 'start_date'
            },
            {
                title: 'End Date',
                dataIndex: 'end_date'
            },
            {
                title: 'Number of days',
                dataIndex: 'number_of_leave_days'
            },
            {
                title: 'Date of request',
                dataIndex: 'formatDateTime',
            },
            {
              dataIndex: '',
              key: 'a',
              width: '1%',
              render: (emp) => <Icon type="info-circle" onClick={this.handleInfo.bind(this, emp)}/>,
            },
            {
                dataIndex: '',
                key: 'b',
                width: '1%',
                render: (emp) => <Icon type="like" onClick={this.show.bind(this, emp)} theme="twoTone" />,
            },
            {
              dataIndex: '',
              key: 'c',
              width: '1%',
              render: (emp) => <Icon type="dislike" onClick={this.showModal.bind(this, emp)} theme="twoTone" />,
          },
            {
                dataIndex: '',
                key: 'd',
                width: '1%',
                render: (emp) => <Icon type="delete" onClick={this.handleDelete.bind(this, emp)}  theme="twoTone" twoToneColor='#EE204D'/>,
            },

          ];

        return (
            <div>
              <Card title="Pending Leaves" hoverable='true'>
                <Table rowKey={record => record.leave_id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 7 }} size="middle" />

                <Drawer
                title="Update Existing Employee"
                width={570}
                onClose={this.onUpdateClose}
                visible={this.state.visibleUpdate}
                >
                    <UpdateEmployee update={this.state.updateEmployee} close={this.onUpdateClose} />
                </Drawer>

                <Drawer
                title="Add New Employee"
                width={570}
                onClose={this.onAddClose}
                visible={this.state.visibleAdd}
                >
                    <AddEmployee close={this.onAddClose}/>
                </Drawer>
                </Card> 
                <CollectionCreateForm
                  wrappedComponentRef={this.saveFormRef}
                  visible={this.state.visible}
                  onCancel={this.handleCancel}
                  onCreate={this.handleCreate}
                />

                <Modal
                title="Accept the leave request."
                visible={visibleAccept}
                onOk={this.handleOk}
                confirmLoading={confirmLoading}
                onCancel={this.handleCancel}
                >
                  <div>
                      <br/>
                      {/* <p>Employee: <Tag color="volcano">{leave.employee.first_name} {leave.employee.second_name}</Tag></p> */}
                      <p>Date of Request: <Tag color="volcano">{leave.formatDateTime}</Tag></p>
                      <p>Leave Type: <Tag color="volcano"></Tag>  Number of Days: <Tag color="volcano">{leave.number_of_leave_days}</Tag></p>
                      <p>Leave Period: <Tag color="volcano">{leave.start_date} to {leave.end_date}</Tag></p>
                      {/* <p>Duty cover by: <Tag color="volcano">{leave.duty.first_name} {leave.duty.second_name}</Tag></p> */}
                      <p>Special note: <Tag color="volcano">{leave.special_notes}</Tag></p>
                  </div>
                </Modal>  
            </div>
        )
    }
}    


export default PendingLeaves;
