import React, { Component } from 'react';
import axios from 'axios';
import swal from 'sweetalert'; 
//import bounce from 'react-animations';
//import styled, {keyframes} from 'styled-components';
import { Card } from 'antd'; 
import { Form, Icon, Input, Button } from 'antd';
//import { height } from '@material-ui/system';

// const loginStyle = {
//     width: "90%",
//     maxWidth: "500px",
//     margin: "100px auto",
//     border: "5px solid #ddd",
//     borderRadius: "5px",
//     padding: "30px"
// }

//const Bounce = styled.div`animation: 2s ${keyframes`${bounce}`}infinite`;

//const { Meta } = Card;

export default class Delete extends Component {
    state = {
        data: undefined,
        id: ''
    }

    componentDidMount(){
        this.setState({
            id: ''
        })
    }

    handleChange = (e) => {
        this.setState({
          [e.target.id] : e.target.value
        })
    }

    click = (e) => {
        axios.get('http://localhost:5000/delete/'+this.state.id)
        .then(res => {
            swal("OMG!",res.data,"success");  
        }).catch( err => {
            console.log(err)
            swal("Oops!","User does not exist!!!","error");
        })
        //this.props.history.push('/');
    }

    render() {
        return (
            <div>
                <Card
                    hoverable
                    style={{ width: 24 }}
                    cover={<img height="100px" alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />}
                >
                    
                </Card> 
                <Form layout="inline" onSubmit={this.handleSubmit}>
                <Form.Item>
                
                    <Input
                    prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                    placeholder="Id Number"
                    />
                </Form.Item>
                
                <Form.Item>
                <Button type="primary" htmlType="submit">
                    Delete
                </Button>
                </Form.Item>
                </Form>
                   
        </div>
        )
    }
}


