import React from 'react';
import 'antd/dist/antd.css';
import axios from '../../config/axios'
import moment from 'moment';
import {
  Form,
  Input,
  Button,
  DatePicker,
  Card,
  Icon,
  message,
  Row,
  Col,
  Table,
  Modal,
  InputNumber
} from 'antd';

const { TextArea } = Input;
const { confirm } = Modal; 

class CollectLieu extends React.Component {

  componentWillMount() {
    this.setState({
      employees : [],
      types : [],
      summery : []
    })

    if(localStorage.getItem("header") !== null){ 
      axios.get('leave_count/summery/'+this.parseJwt(localStorage.getItem("header")), 
      {
          headers: {
              Authorization: 'Bearer ' + localStorage.getItem("header")
          }
      })
      .then(res => {
          if (res.data.success === true) {
            this.setState({
              summery : res.data.list
            })
          } else {
                message.error(res.data.message);
          }
      }) 
      .catch(e => {
        message.error("Something went wrong");
        console.log(e.response.data.error);
      })

      axios.get('user/all', 
      {
          headers: {
              Authorization: 'Bearer ' + localStorage.getItem("header")
          }
      })
      .then(res => {
          if (res.data.success === true) {  
            this.setState({
                employees : res.data.list
            })
          } else {
            message.error(res.data.message);
          }
      })
      .catch(e => {
        message.error("Something went wrong");
        console.log(e.response.data.error);
      })

      axios.get('leave_type/all', 
      {
          headers: {
              Authorization: 'Bearer ' + localStorage.getItem("header")
          }
      })
      .then(res => {

        if (res.data.success === true) {  
          this.setState({
            types : res.data.list
          })
        } else {
          message.error(res.data.message);
        }
      })
      .catch(e => {
        message.error("Something went wrong");
        console.log(e.response.data.error);
      }) 

      axios.get('calender/all', 
      {
          headers: {
              Authorization: 'Bearer ' + localStorage.getItem("header")
          }
      })
      .then(res => {
        if (res.data.success === true) {  
          this.setState({
            saveDates : res.data.list
          });
        } else {
          message.error(res.data.message);
        }
      })
      .catch(e => {
        message.error("Something went wrong");
        console.log(e.response.data.error);
      })
    }
  }

  parseJwt (token) {
    var base64Url = token.split('.')[1];
    var base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    var jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
    }).join(''));

    return JSON.parse(jsonPayload).sub;
  };

  state = {
    types : [],
    employees : [],
    summery : [],
    period : null,
    disable : false,
    startValue: null,
    endValue: null,
    endOpen: false,
    type: null,
    error: null,
    duty: null,
    start_half: "Full Day",
    end_half: "Full Day",
    start_full_disable: false,
    start_morning_disable: true,
    half_day: false,
    start_day : null,
    end_day : null,
    saveDates : [],
    count: 0
  };

  handleSubmit = e => {
    e.preventDefault();
    confirm({
      title: 'Sure to submit your leave request?',
      content: 'If you submit, Send request to administraters and supervisors. Inform duty coverer.',
      okText: 'Sumbit',
      okType: 'primary',
      onOk: () => {
    this.props.form.validateFieldsAndScroll((err, values) => {

      if (!err && this.state.error==null) {

        if(this.state.duty !== null){ 
          const leave = {
          leave_type : { leave_type_id : this.state.type } || undefined,
          user : { id : this.parseJwt(localStorage.getItem("header")) } || undefined,
          start_date : this.state.startValue || undefined,
          end_date : this.state.endValue || this.state.startValue,
          startHalf : this.state.start_half,
          endHalf : this.state.end_half,
          number_of_leave_days : parseFloat(values.project) || undefined,
          duty : {id : parseInt(this.state.duty)}, 
          special_notes : values.note || "No special note.",
          status : "Pending",
          reject : "Not reject"
          }

          axios.post(
            'leave/request', 
            leave, 
            { 
                headers: {
                    Authorization: 'Bearer ' + localStorage.getItem("header")
                }
            }
          )
          .then(res => {
            if (res.data.success === true) {              
              message.success(res.data.message); 
              this.handleCancel();
            } else {
                message.error(res.data.message);
            }
          })
          .catch(e => {
            console.log(e.response.data.error);
            message.error("Something went wrong"); 
          })
        }else{
            const leave = {
              leave_type : { leave_type_id : this.state.type } || undefined,
              user : { id : this.parseJwt(localStorage.getItem("header")) } || undefined,
              start_date : this.state.startValue || undefined,
              end_date : this.state.endValue || this.state.startValue,
              startHalf : this.state.start_half,
              endHalf : this.state.end_half,
              number_of_leave_days : parseFloat(values.project) || undefined,
              special_notes : values.note || "No special note",
              status : "Pending",
              reject : "Not reject"
            }

            axios.post(
              'leave/request', 
              leave, 
              { 
                  headers: {
                      Authorization: 'Bearer ' + localStorage.getItem("header")
                  }
              }
            )
            .then(res => {
              if (res.data.success === true) {              
                message.success(res.data.message); 
                this.handleCancel();
              } else {
                message.error(res.data.message);
              }
            })
            .catch(e => {
              console.log(e.response.data.error);
              message.error("Something went wrong"); 
            })  
        }
      }else if(this.state.error !== null){
        message.error(this.state.error);
      }
    });
    }
  })
  }

  handleCancel = () => {
    this.props.form.resetFields();
    this.setState({
      disable : false,
      period : null,
      start_full_disable : false,
      start_morning_disable : true,
      start_half : "Full Day",
      end_half : "Full Day",
      error: null,
      count : 0
    });
  };

  disabledStartDate = startValue => {
    return startValue && startValue < moment().endOf('day');
  };

  disabledEndDate = endValue => {
    const { start_day } = this.state;
    if (!endValue || !start_day) {
      return false;
    }
    return endValue.valueOf() <= start_day.valueOf();
  };

  onChange = (field, value) => {
    this.setState({
      [field]: value,
    });
  };

  onStartChange = (date, dateString) => {
    this.onChange('startValue', dateString);
    this.onChange('start_day',date)
  };

  setType = (e) => {
    e.preventDefault();
    this.handleCancel();
    this.onChange('type', this.state.types[e.target.value].leave_type_id);
  }

  handleStartChange = open => {
    if (!open && !this.state.disable) {
      this.setState({ endOpen: true });
    }
  };


  render() {
    const { getFieldDecorator } = this.props.form;
    const { summery } = this.state;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
    };

    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 24,
          offset: 0,
        },
        sm: {
          span: 16,
          offset: 8,
        },
      },
    };

    const columns = [
      {
        title: 'Date',
        key: '0',
        dataIndex: 'type'
      },
      {
        title: 'Hours',
        key: '1',
        dataIndex: 'entitlement'
      },    
      {
        title: 'Project',
        key: '2',
        dataIndex: 'utilized'
      },    
      {
        title: 'Status',
        key: '3',
        dataIndex: 'utilized'
      }
    ];

    return (
        <div>
          <Row gutter={16}>
            <Col span={13} > 
              <Card title='Collect the lieu leaves' bordered={false} hoverable='true'>  
                <Form {...formItemLayout} onSubmit={this.handleSubmit}>

                <Form.Item label="Date">
                {getFieldDecorator('start_date', {
                    rules: [{ required: true, message: 'Please input Date!' }],
                  })(
                  <DatePicker
                    disabledDate={this.disabledStartDate}
                    format="YYYY-MM-DD"
                    placeholder="Pickup a Date"
                    onChange={this.onStartChange}
                    onOpenChange={this.handleStartChange}
                    style={{ width: '400px' }}
                  />
                  )}
                </Form.Item>

                <Form.Item label="Hours">
                {getFieldDecorator('hours', {
                    rules: [{ required: true, message: 'Please input worked hours!' }],
                  })(
                    <InputNumber
                        defaultValue={1}
                        min={1}
                        max={24}
                        formatter={value => Math.round(value)}
                    />
                  )}
                </Form.Item>                

                <Form.Item label="Project">
                  {getFieldDecorator('project', {
                    rules : [{ 
                      required: true, 
                      message: 'Please input the project!' 
                    }],
                    initialValue : this.state.period
                  })(<Input style={{width: '400px'}} />)}
                </Form.Item>

                <Form.Item label="Works done">
                  {getFieldDecorator('note', {
                    rules: [{
                        required: true, 
                        message: 'Please input the works you done',
                      }],
                  })(<TextArea rows={4} style={{ width: '400px', height: '170px' }}/>)}
                </Form.Item>

                <Form.Item {...tailFormItemLayout}>
                  <Button type="primary" htmlType="submit" style={{width:'100px'}}>
                  <Icon type="check-circle" /> 
                    Submit
                  </Button>
                  &nbsp;&nbsp;&nbsp;&nbsp;
                  <Button type="danger" onClick={this.handleCancel} style={{width:'100px'}}>
                  <Icon type="close-circle" /> 
                    Cancel
                  </Button>
                </Form.Item>
              </Form>
              </Card>
            </Col>
            <Col span={11}>
              <Card hoverable='true'>
              <Table rowKey={record => record.type} columns={columns} dataSource={summery} size="small" pagination={false}/>
              </Card>
            </Col>
          </Row>

    </div>
    );
  }
}

