import React from 'react';
import 'antd/dist/antd.css';
import {withRouter} from 'react-router-dom';
import { PageHeader, Icon, Button, Modal, Row, Col, Avatar } from 'antd';

const { confirm } = Modal;

class Header extends React.Component { 

    handleLogout = (e) => {
        e.preventDefault();
        confirm({
          title: 'Do you want to logout?',
          onOk: () => {
            localStorage.removeItem("header");
            this.props.history.push('/login')
          },
          onCancel() {
            console.log('Cancel');
          },
        });
    }

    state = {
        collapsed: false,
    };

    toggle = () => {
        this.setState({
          collapsed: !this.state.collapsed,
        });
    };

    render () {
        // const routes = [
        //     {
        //         path: '/',
        //         breadcrumbName: "Home",
        //     },
        //     {
        //         path: 'first',
        //         breadcrumbName: 'Second-level Menu',
        //     },
        //     {
        //         path: 'second',
        //         breadcrumbName: 'Third-level Menu',
        //     },
        // ];

    return (
        <PageHeader
           
            // title={this.props.name}
            style={{
            background: 'white',    
            border: '1px solid rgb(235, 237, 240)',
            }}
            // subTitle={this.props.role}
            // extra={[
            // <Button key="1" type="primary" onClick={this.toggle} shape="circle">
            //    <Icon 
            //     className="trigger"
            //     type={this.state.collapsed ? 'menu-unfold' : 'menu-fold'}
            //    />
            // </Button>,
            // <Button key="2" type="primary" onClick={this.handleLogout} shape="circle">
            //    <Icon type="logout" />
            // </Button>
            // ]}
            
            // avatar={{ src: 'https://avatars1.githubusercontent.com/u/8186664?s=460&v=4'}}
            // breadcrumb={{ routes }}
            
        >
            <Row>
                <Col span={4}>
                    <Avatar size={100} icon="user"  src= 'https://avatars1.githubusercontent.com/u/8186664?s=460&v=4'/>
                </Col>
                <Col span={19}>
                    <span style={{fontSize:'30px'}}>{this.props.name}</span><br/>
                    <span style={{fontSize:'20px', color:'gray'}}>{this.props.department}</span><br/>
                    <span style={{fontSize:'20px', color:'red'}}>{this.props.role}</span> 
                </Col>
                <Col span={2}>
                    <Button key="1" type="primary" onClick={this.toggle} shape="circle">
                        <Icon 
                            className="trigger"
                            type={this.state.collapsed ? 'menu-unfold' : 'menu-fold'}
                        />
                    </Button>,
                    <Button key="2" type="primary" onClick={this.handleLogout} shape="circle">
                        <Icon type="logout" />
                    </Button>
                </Col>
            </Row>
        </PageHeader>
        );
    }
}          

export default withRouter(Header);
