import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import { Card, Table, Modal, Tag, Row, Col, Icon } from 'antd';

class OneAttendence extends Component {
    
    componentWillMount(){
        this.setState({
            data : [],
            count : []
        })
        axios.get('http://localhost:5000/leave/find/'+localStorage.getItem("leave_count"), 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("leaves", res.data);
            this.setState({
                data : (res.data).reverse()
            })
        }) 

        axios.get('http://localhost:5000/leave_count/profile/'+localStorage.getItem("leave_count"), 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("count", res.data);
            this.setState({
                count : res.data,
                special : res.data.casual
            })
        }) 

        const employee = { emp_id: localStorage.getItem("leave_count") } 
        axios.post('http://localhost:5000/contact/employee', employee,
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("contact", res.data);
            this.setState({
                contact: res.data
            })
        })
    } 

    componentDidMount() {
        console.log("sssssssssssssss",this.state.special)
    }

    state = {
        data : [],
        count : [],
        contact : [],
        special : null
    }

    render() {

        const columns = [ 
            
            {
                title: 'Date of request',
                dataIndex: 'formatDateTime',
            },
            {
                title: 'Leave Type',
                dataIndex: 'leave_type',
                render: type => (
                type.type
                )
            },
            {
                title: 'Start Date',
                dataIndex: 'start_date'
            },
            {
                title: 'End Date',
                dataIndex: 'end_date'
            },
            {
                title: 'Number of days',
                dataIndex: 'number_of_leave_days'
            },
            {
                title: 'Status',
                dataIndex: 'status',
            },
            {
              dataIndex: '',
              key: 'z',
              width: '1%',
              render: (emp) => <Icon type="info-circle" onClick={info.bind(this, emp)}  theme="twoTone" />,
            }
        ];

        return (
            <div className='center'>
                <Card hoverable='true'>
                <Row>
                    <Col span={8}><Tag color="geekblue">Casual</Tag> &nbsp; <Tag color="volcano">{this.state.count.casual}/7</Tag></Col>
                    <Col span={8}><Tag color="geekblue">Medical</Tag> &nbsp; <Tag color="volcano">{this.state.count.medical}/7</Tag></Col>
                    <Col span={8}><Tag color="geekblue">Annual</Tag> &nbsp; <Tag color="volcano">{this.state.count.annual}/14</Tag></Col>
                </Row>

                </Card>
                <br/>

                <Card hoverable='true' >
                    <Table rowKey={record => record.leave_id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 7 }} size="middle"/>
                </Card>
              
            </div>
        )
    }
}    

export default OneAttendence;
