import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import {Table } from 'antd';

class LeaveCount extends Component {
    
    componentWillMount(){
        this.setState({
            data : []
        })
        axios.get('http://localhost:5000/employee/all', 
        {
            headers: {
                Authorization: 'Bearer eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJ1c2VybmFtZSIsImV4cCI6MTU3MjI2MjMwMiwiaWF0IjoxNTcyMjQ0MzAyfQ.xeStub_a7esd_huszd4R3g5b-lYR_uJOMT24qgiv8czRnL5pQNhSGS7xdfel64P631xx_uyignG8eenrMSP0VA'
            }
        })
        .then(res => {
            console.log("res", res.data);
            this.setState({
                data : res.data
            })
        }) 
    } 

    state = {
        data : []
    }

    render() {
       
        const columns = [
            {
              title: 'Name',
              dataIndex: 'name',
            },
            {
              title: 'Age',
              dataIndex: 'age',
            },
            {
              title: 'Address',
              dataIndex: 'address',
            },
        ];

        //   const data = [
        //     {
        //       key: '1',
        //       name: 'John Brown',
        //       age: 32,
        //       address: 'New York No. 1 Lake Park',
        //     },
        //     {
        //       key: '4ssssss',
        //       name: 'Jim Green',
        //       age: 42,
        //       address: 'London No. 1 Lake Park',
        //     },
        //     {
        //       key: '3',
        //       name: 'Joe Black',
        //       age: 32,
        //       address: 'Sidney No. 1 Lake Park',
        //     },
        //   ];

        return (
            <div>
                <Table columns={columns} dataSource={this.data}  pagination={{ pageSize: 2 }} size="middle" />

              
            </div>
        )
    }
}    


export default LeaveCount;
