import React from 'react';
import 'antd/dist/antd.css';
import axios from 'axios';
import { Calendar, Card, Modal, Form, Input, message, Badge } from 'antd';

const ReasonForm = Form.create({ name: 'form_in_modal' })(

  class extends React.Component {
    render() {
      const { visible, onCancel, onCreate, form } = this.props;
      const { getFieldDecorator } = form;
      return (
        <Modal
          visible={visible}
          title="Set holiday"
          okText="Save"
          onCancel={onCancel}
          onOk={onCreate}
        >
          <Form layout="vertical">
            <Form.Item label="Reason">
              {getFieldDecorator('reason', {
                rules: [{ required: true, message: 'Please input the reason' }],
              })(<Input type="textarea"/>)}
            </Form.Item>
          </Form>
        </Modal>
      );
    }
  },
);

function getListData(value) {
  let listData;
  switch (value.date()) {
    case 8:
      listData = [
        { type: 'warning', content: 'This is warning event.' },
        { type: 'success', content: 'This is usual event.' },
      ];
      break;
    case 10:
      listData = [
        { type: 'warning', content: 'This is warning event.' },
        { type: 'success', content: 'This is usual event.' },
        { type: 'error', content: 'This is error event.' },
      ];
      break;
    case 15:
      listData = [
        { type: 'warning', content: 'This is warning event' },
        { type: 'success', content: 'This is very long usual event。。....' },
        { type: 'error', content: 'This is error event 1.' },
        { type: 'error', content: 'This is error event 2.' },
        { type: 'error', content: 'This is error event 3.' },
        { type: 'error', content: 'This is error event 4.' },
      ];
      break;
    default:
  }
  return listData || [];
}

function dateCellRender(value) {
  const listData = getListData(value);
  return (
    <ul className="events">
      {listData.map(item => (
        <li key={item.content}>
          <Badge status={item.type} text={item.content} />
        </li>
      ))}
    </ul>
  );
}

class LeaveCalender extends React.Component {

  componentWillMount () {
    this.reload();
  }
  
  re () {
    axios.post('http://localhost:5000/calender/all', 
    {
        headers: {
            Authorization: 'Bearer ' + localStorage.getItem("header")
        }
    })
    .then(res => {
      console.log("save dates",res.data)
      this.setState({
        saveDates : res.data
      });
    }).catch(e => {
      console.log(e.response.data.error);
    })
  }

  state = {
    saveDates: [],
    selectedValue: null,
    visible: false
  };

  onSelect = value => {
    this.setState({
      selectedValue: value,
      visible: true
    });
  };

  handleCancel = () => {
    const { form } = this.formRef.props;
    form.resetFields(); 
    this.setState({ 
      visible: false
    });
  };

  handleSet = () => {
    
    const { form } = this.formRef.props;
    form.validateFields((err, values) => {

      const calendar = {
        date: this.state.selectedValue.format('YYYY-MM-DD'),  
        reason : values.reason
      }

      if (!err) {
        axios.post('http://localhost:5000/calender/set_date', 
        calendar,
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            if(res.data === true){
              message.success("Set as a holiday!");
              this.props.form.resetFields(); 
              this.handleCancel();
            }  else{
              message.error("Internal server error!"); 
            }
        }).catch(e => {
          message.error(e.response.data.error); 
        })
        
      }
    });
  }

  saveFormRef = formRef => {
    this.formRef = formRef;
  };

  render() {
    return (
      <div>
        <Card>
          <Calendar onSelect={this.onSelect} dateCellRender={dateCellRender} />
        </Card>

        <ReasonForm
          wrappedComponentRef={this.saveFormRef}
          visible={this.state.visible}
          onCancel={this.handleCancel}
          onCreate={this.handleSet}
        />
      </div>
    );
  }
}


const WrappedLeaveCalender = Form.create({ name: 'calender' })(LeaveCalender);

export default WrappedLeaveCalender;
