import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import { Form, Card, Descriptions, Table, Modal, Tag, Row, Col, Icon, Input, Button, Badge, Progress } from 'antd';
import Highlighter from 'react-highlight-words';

class Dashboard extends Component {
  
    componentDidMount = () => {

      this.setState({
            data : [],
            count : [],
            summery : []
      })

        axios.get('http://localhost:5000/leave_count/summery/'+this.parseJwt(localStorage.getItem("header")), 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            this.setState({
                summery : res.data
            })
        }) 

        axios.get('http://localhost:5000/leave/find/'+this.parseJwt(localStorage.getItem("header")), 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            this.setState({
                data : (res.data).reverse()
            })
        }) 
        
        axios.get('http://localhost:5000/user/'+this.parseJwt(localStorage.getItem("header")), 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            this.setState({
                employee : res.data
            })
        }) 
    } 

    parseJwt (token) {
      var base64Url = token.split('.')[1];
      var base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
      var jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
          return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
      }).join(''));
  
      return JSON.parse(jsonPayload).sub;
    };

    state = {
        data : [],
        leave : [],
        employee : [],
        summery : []
    }

    showModal = (leave) => {
        this.setState({
          visible: true,
          leave: leave
        });
      };
    
      handleOk = e => {
        console.log(e);
        this.setState({
          visible: false,
        });
    };

    getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.state.searchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });

      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.setState({ searchText: selectedKeys[0] });
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.setState({ searchText: '' });
      };

    render() {

        const {leave, employee, summery} = this.state;

        const summeryCol = [
          {
            title: 'Type',
            key: '0',
            dataIndex: 'type'
          },
          {
            title: 'Entitlement',
            key: '1',
            dataIndex: 'entitlement'
          },    
          {
            title: 'Utilized',
            key: '2',
            dataIndex: 'utilized'
          },
          {
            title: 'Remaining',
            key: '3',
            dataIndex: 'remaining'
          }
        ]; 

        const columns = [ 
            
            {
                title: 'Date of request',
                dataIndex: 'formatDateTime',
                ...this.getColumnSearchProps('formatDateTime'),
            },
            {
              title: 'Leave Type',
              dataIndex: 'leave_type',
              render: type => (
               type.type
              )
            },
            {
                title: 'Start Date',
                dataIndex: 'start_date'
            },
            {
                title: 'End Date',
                dataIndex: 'end_date'
            },
            {
                title: 'Number of days',
                dataIndex: 'number_of_leave_days'
            },
            {
                title: 'Status',
                dataIndex: 'status',
                render: tag => {
                    let color;
                    if (tag === 'Pending') {
                      color = 'green';
                    }else if (tag === 'Approved') {
                      color = 'geekblue';
                    }else {
                      color = 'red';
                    }
                    return (
                      <Tag color={color} key={tag}>
                        {tag}
                      </Tag>
                    );
                  }
                // ...this.getColumnSearchProps('status')
            },
            {
              dataIndex: '',
              key: 'z',
              width: '1%',
              render: (emp) => <Icon type="info-circle" onClick={this.showModal.bind(this, emp)}  theme="twoTone" />,
            }
        ];

        return (
            <div >
              <Card hoverable='true'>
                  {summery.length !== 0 ?   
                <Row gutter={16}>
                  <center> 
                  <Col span={8}>
                    <Progress type="circle" percent={this.state.summery[0].utilized / this.state.summery[0].entitlement * 100} format={percent => `${this.state.summery[0].utilized} / ${this.state.summery[0].entitlement}`}/>
                    <br/><br/>
                    <Tag color="volcano">Casual</Tag>
                  </Col>
                  <Col span={8}>
                    <Progress type="circle" percent={this.state.summery[1].utilized / this.state.summery[1].entitlement * 100} format={percent => `${this.state.summery[1].utilized} / ${this.state.summery[1].entitlement}`}/>
                    <br/><br/>
                    <Tag color="volcano">Medical</Tag>
                  </Col> 
                  <Col span={8}>
                    <Progress type="circle" percent={this.state.summery[2].utilized / this.state.summery[2].entitlement * 100} format={percent => `${this.state.summery[2].utilized} / ${this.state.summery[2].entitlement}`}/>
                    <br/><br/>
                    <Tag color="volcano">Annual</Tag>
                  </Col>
                  </center>
                </Row>
                : null}
              </Card>
              <br/>
              <Row gutter={16}>
                <Col span={10} > 
                  <Card hoverable='true'>
                    <Table rowKey={record => record.type} columns={summeryCol} dataSource={summery} size="small" pagination={false}/>
                  </Card>
                </Col>
                <Col span={14}>  
                  <Card hoverable='true'>
                    {employee.length !== 0 ?
                    <Descriptions title="User Infomation" size="small" bordered column={2} >
                      <Descriptions.Item label="Name">{employee.firstName} {employee.secondName}</Descriptions.Item>
                      <Descriptions.Item label="Email" span={1}>{employee.email}</Descriptions.Item>
                      <Descriptions.Item label="Residence" >{employee.residence}</Descriptions.Item>
                      <Descriptions.Item label="Contact No" span={1}> {employee.contact}</Descriptions.Item>
                      <Descriptions.Item label="Role">{employee.role}</Descriptions.Item>
                      <Descriptions.Item label="Department" span={1}>{employee.department}</Descriptions.Item>
                      <Descriptions.Item label="Status" span={2}>
                        <Badge status="processing" text={employee.status} />
                      </Descriptions.Item>
                      <Descriptions.Item label="Supervisor 01">{employee.supervisor1}</Descriptions.Item>
                      <Descriptions.Item label="Supervisor 02" span={1}>{employee.supervisor2}</Descriptions.Item>
                      <Descriptions.Item label="Join Date">{employee.joinDate}</Descriptions.Item>
                      <Descriptions.Item label="Confirm Date">{employee.confirmDate}</Descriptions.Item>
                    </Descriptions> : null}
                  </Card>
                </Col>
            </Row>
                <br/>
              
                <Card hoverable='true' >
                    <Table rowKey={record => record.leave_id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 5 }} size="middle" />
                </Card>
                {leave.length!==0? <Modal
                  title="Full Leave Record"
                  visible={this.state.visible}
                  onOk={this.handleOk}
                  onCancel={this.handleOk}
                  footer={null}
                >
                  <p>Date of Request: <Tag color="volcano">{leave.formatDateTime}</Tag> Leave Type: <Tag color="volcano">{leave.leave_type.type}</Tag></p>
                  <p>Leave Period: <Tag color="volcano">{leave.start_date} {leave.startHalf }</Tag>To  <Tag color="volcano">{leave.end_date} {leave.endHalf}</Tag></p>
                  <p>Number of Days: <Tag color="volcano">{leave.number_of_leave_days}</Tag></p>
                  <p>Status: <Tag color="volcano">{leave.status}</Tag> </p>
                  <p>Duty cover by: <Tag color="volcano">{leave.duty.first_name} {leave.duty.second_name}</Tag></p>
                  <p>Special note: <Tag color="volcano">{leave.special_notes}</Tag></p>
                  <p>If reject, reason: <Tag color="volcano">{leave.reject}</Tag></p>
                </Modal>: null}
            </div>
        )
    }
}    

const Dash = Form.create({name: "dashboard"})(Dashboard);

export default Dash;
