import React from "react";
import { Link } from 'react-router-dom';
import { Icon, Form, Button, Input, Card, message, Row, Col } from 'antd';
import axios from 'axios';

class Forgot extends React.Component {

    state = {
        confirmDirty: false,
        button: "Send",
        email: null
    }

    changePassword = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
          const passwords = {
            email: this.state.email,  
            currentPassword : values.currentPassword,
            newPassword : values.newPassword
          }

          if (!err) {
            axios.post('http://localhost:5000/api/auth/forgot_password', 
            passwords,
            {
                headers: {
                    Authorization: 'Bearer ' + localStorage.getItem("header")
                }
            })
            .then(res => {
                if(res.data === true){
                  message.success("Password Updated Successfully!");
                  this.props.form.resetFields(); 
                }  else{
                  message.error("Confirm code not match!"); 
                }
            }).catch(e => {
              console.log(e);
              message.error("Something went wrong!"); 
            })
            
          }
        });
    }
    
    setEmail = (value) => {
        this.setState({email: value.target.value});
    }

    sendCode = () => {
        const user = {
            usernameOrEmail : this.state.email
        }
        axios.post('http://localhost:5000/api/auth/forgot', 
            user,
            )
            .then(res => {
                if(res.data === true){
                  message.success("Check email for confirm code");
                  this.setState({button: "Send Again"});
                }  else{
                  message.error(" went wrong"); 
                }
            }).catch(e => {
              message.error("Something went wrong"); 
            })
    }

    handleConfirmBlur = e => {
        const { value } = e.target;
        this.setState({ confirmDirty: this.state.confirmDirty || !!value });
    };
    
    compareToFirstPassword = (rule, value, callback) => {
        const { form } = this.props;
        if (value && value !== form.getFieldValue("newPassword")) {
          callback("Two passwords that you enter is inconsistent!");
        } else {
          callback();
        }
    };
    
    validateToNextPassword = (rule, value, callback) => {
        const { form } = this.props;
        if (value && this.state.confirmDirty) {
          form.validateFields(["confirm"], { force: true });
        }
        callback();
    };

    render () {

        const { getFieldDecorator } = this.props.form;
       
        const formItemLayout = {
            wrapperCol: {
              xs: { span: 24 }
            },
        };

        return(
            <div style={{ background: '#ECECEC', padding: '150px', height: '760px'}}>
                <Row gutter={5}>
                    <Col span={8} offset={3}>
                        <img height= '430px' alt="example" src="https://image.freepik.com/free-vector/flat-thinking-concept_23-2148163823.jpg" />
                    </Col> 

                    <Col span={9}>
                    <Card title="Forgot Password" bordered={false} style={{ width: 500, height: '430px'}}>
                    
                    <Form layout="inline">
                    <Form.Item >
                        <Input 
                            prefix={<Icon type="mail" style={{ color: "rgba(0,0,0,.25)"}} />}
                            placeholder="Your Email"
                            style={{ width: "300px" }}
                            onChange={this.setEmail}
                            />
                    </Form.Item>

                    <Form.Item>
                    <Button type="primary" htmlType="submit" onClick={this.sendCode} style={{width:'120px'}}>
                            {this.state.button} 
                    </Button>
                    </Form.Item>
                    </Form>
                        <br/>                
                        <Form {...formItemLayout} layout="vertical" hideRequiredMark>
        
                            <Form.Item> 
                                {getFieldDecorator('code', {
                                    rules: [{ 
                                    required: true, 
                                    len : 4,
                                    message: 'Please input valid number!', 
                                    }],
                                })(<Input  
                                    id= '1'
                                    prefix={<Icon type="key" style={{ color: "rgba(0,0,0,.25)" }} />}
                                    placeholder="Confirm Code"
                                    />)} 
                            </Form.Item>
                            
                            <Form.Item hasFeedback>
                                {getFieldDecorator("newPassword", {
                                rules: [
                                    {
                                    required: true,
                                    message: "Please input your new password!"
                                    },
                                    {
                                    validator: this.validateToNextPassword
                                    }
                                ]
                                })(<Input.Password 
                                id= '2'
                                prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                                placeholder="New Password"
                                />)}
                            </Form.Item>
                            <Form.Item hasFeedback>
                                {getFieldDecorator("confirm", {
                                rules: [
                                    {
                                    required: true,
                                    message: "Please confirm your new password!"
                                    },
                                    {
                                    validator: this.compareToFirstPassword
                                    }
                                ]
                                })(<Input.Password 
                                id= '3'
                                onBlur={this.handleConfirmBlur}
                                prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                                placeholder="Confirm Password"
                                />)}
                            </Form.Item>
                            <Form.Item >
            
                            <Button onClick={this.changePassword} type="primary" htmlType="submit" className="login-form-button">
                            Reset Password
                            </Button>
                            <Link className="login-form-forgot" to='/login'>Log in</Link>
                            <br/>
                        </Form.Item>
                        </Form>
                    </Card>
                    </Col> 
                </Row>
            </div>
        );
    }
}

const WrappedForgotPassword= Form.create({ name: 'register' })(Forgot);

export default WrappedForgotPassword;
