        data : [],
        filteredData : [],
        filteredWhose : [],
        visible: false,
        leave : [],
        selectWhose : null,
        selectStartDate : null,
        employees : [],
        departments : [] 
    };
    
    reload () {
      this.setState({
        data : []
      })
    
      axios.get('http://localhost:5000/leave/all', 
      {
          headers: {
              Authorization: 'Bearer ' + localStorage.getItem("header")
          }
      })
      .then(res => {
          this.setState({
              data : (res.data).reverse()
          })
      })
      .catch(e => {
        console.log(e.response.data.error);
      })
      
      axios.get('http://localhost:5000/leave/all', 
      {
          headers: {
              Authorization: 'Bearer ' + localStorage.getItem("header")
          }
      })
      .then(res => {
          console.log(res.data)
          this.setState({
              filteredData : (res.data).reverse()
          })
      })
      .catch(e => {
        console.log(e.response.data.error);
      })

      axios.get('http://localhost:5000/user/all', 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            this.setState({
                employees : res.data
            })
        })
        .catch(e => {
            console.log(e.response.data.error);
        })
     
      axios.get('http://localhost:5000/department/all', 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            this.setState({
                departments : res.data
            })
        })
        .catch(e => {
            console.log(e.response.data.error);
        })
    }

      getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.state.searchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });
    
      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.setState({ searchText: selectedKeys[0] });
      };
    
    handleReset = clearFilters => {
        clearFilters();
        this.setState({ searchText: '' });
    };   

    showModal = (leave) => {
      this.setState({
        visible: true,
        leave: leave
      });
    };
  
    handleOk = e => {
      this.setState({
        visible: false,
      });
    };

    handleWhose = (value) => {
        this.props.form.resetFields();
        this.setState({
            selectWhose : value,
            selectStartDate : 0,
            filteredData : this.state.data          
        })
    }

    handleStartDate = (value) => {
        this.setState({
            selectStartDate : value,
            filteredData : this.state.filteredWhose           
        })
    }

    onChangeIs = (date, dateString) => {
        let arr = [];
        this.state.filteredWhose.forEach(item => {
            if(item.startDate === dateString){
                arr.push(item)
            }
        });
        this.setState({ filteredData : arr })
    }

    onChangeBetween = (date, dateString) => {
        var s = new Date(date[0])
        var e = new Date(date[1])

        let arr = [];
        this.state.filteredWhose.forEach(item => {
            var date = new Date(item.startDate)
            if(dateString[0] === item.startDate || dateString[1] === item.startDate){
                arr.push(item)
            }
            if(s-1 < date && date < e && !arr.includes(item)){
                arr.push(item)
            }
        });
        this.setState({ filteredData : arr })
    }

    onChangeAfter = (date, dateString) => {
        var s = new Date(date)

        let arr = [];
        this.state.filteredWhose.forEach(item => {
            var date = new Date(item.startDate)
            
            if( date > s ){
                arr.push(item)
            }

        });

        arr.forEach((item, index) => {
            if(dateString === item.startDate){
                if (index > -1) {
                    arr.splice(index, 1);
                }
            }
        });

        this.setState({ filteredData : arr })
    }

    onChangeBefore = (date, dateString) => {
        var s = new Date(date)

        let arr = [];
        this.state.filteredWhose.forEach(item => {
            var date = new Date(item.startDate)
            
            if( date < s ){
                arr.push(item)
            }
        });

        arr.forEach((item, index) => {
            if(dateString === item.startDate){
                if (index > -1) {
                    arr.splice(index, 1);
                }
            }
        });

        this.setState({ filteredData : arr })
    }

    handleSelectedEmployee = (value) => {
      this.props.form.resetFields();
      
      let arr = [];
      this.state.data.forEach(item => {
        if(item.user === value){
          arr.push(item);
        }
      })
      this.setState({ 
        filteredData : arr,
        filteredWhose : arr,
        selectStartDate : 0
       });
    }

    handleSelectedDepartment = (value) => {
      this.props.form.resetFields();
      
      let arr = [];
      this.state.data.forEach(item => {
        if(item.department === value){
          arr.push(item);
        }
      })
      this.setState({ 
        filteredData : arr,
        filteredWhose : arr,
        selectStartDate : 0
       });
    }

    // download_csv = () => {
    //   var csv = 'Name,Title\n';
    //   this.state.filteredData.forEach(
    //     function(row) {
    //           csv += row.join(',');
    //           csv += "\n";
    //     }
    //   );
   
    //   console.log(csv);
    //   var hiddenElement = document.createElement('a');
    //   hiddenElement.href = 'data:text/csv;charset=utf-8,' + encodeURI(csv);
    //   hiddenElement.target = '_blank';
    //   hiddenElement.download = 'people.csv';
    //   hiddenElement.click();
    // }

    render() {
        const { getFieldDecorator } = this.props.form;
       
        const {leave} = this.state;

        const columns = [
            {
              title: 'Name',
              dataIndex: 'user',
              ...this.getColumnSearchProps('user')
            },  
            {
                title: 'Date of request',
                dataIndex: 'formatDateTime'
            },
            {
              title: 'Leave Type',
              dataIndex: 'type',
              ...this.getColumnSearchProps('type')
            },
            {
                title: 'Start Date',
                dataIndex: 'startDate'
            },
            {
                title: 'End Date',
                dataIndex: 'endDate'
            },
            {
              title: 'Checked By',
              dataIndex: 'checkBy'
            },
            {
              title: 'Status',
              dataIndex: 'status',
              ...this.getColumnSearchProps('status'),
              render: tag => {
                        let color;
                        if (tag === 'Pending') {
                        color = 'geekblue';
                        }else if (tag === 'Approved') {
                        color = 'green';
                        }else {
                        color = 'red';
                        }
                        return (
                          <Tag color={color} key={tag}>
                            {tag}
                          </Tag>
                        );
                      },
            },
            {
              dataIndex: '',
              key: 'z',
              width: '1%',
              render: (emp) => <Icon type="info-circle" onClick={this.showModal.bind(this, emp)}/>,
            }

          ];

        return (
            <div>
              <Card hoverable='true'>
                <Row>
                    <Col span={1}/>
                    <Col span={6}>
                        Whose: &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                        <Select style={{ width: 200 }} onChange={this.handleWhose} defaultValue={0}>
                            <Option value={0}>All Employees</Option>
                            <Option value={1}>My</Option>
                            <Option value={2}>Selected Department</Option>
                            <Option value={3}>Selected Employee</Option>
                        </Select>
                    </Col>
                    <Col span={13}>
                        &nbsp;
                        {this.state.selectWhose === 3?
                        <Select placeholder="Please select employee" style={{ width: '210px' }} onChange={this.handleSelectedEmployee}>
                            {this.state.employees.map(item => (
                                <Option key={item.firstName +" "+ item.secondName}>{item.firstName} {item.secondName}</Option>
                            ))}
                        </Select>
                        :null
                        }
                        
                        {this.state.selectWhose === 2?
                        <Select placeholder="Please select department" style={{ width: '210px' }} onChange={this.handleSelectedDepartment}>
                            {this.state.departments.map(item => (
                                <Option key={item.name}>{item.name}</Option>
                            ))}
                        </Select>
                        :null
                        }
                    </Col>
                    <Col span={3}>
                        <Button  type="primary" shape="circle" icon="download" style={{float:'right'}} onClick={this.download_csv}/>
                    </Col>
                </Row> 
                <br/>
                <Row gutter={10}>
                    <Col span={1}/>
                    <Col span={6}>
                        Start Date: &nbsp;&nbsp;&nbsp;&nbsp;
                        {getFieldDecorator('start', {
                          initialValue : 0
                        })(
                        <Select style={{ width: 200 }} onChange={this.handleStartDate}>
                            <Option value={0}>All</Option>
                            <Option value={1}>Is</Option>
                            <Option value={2}>Between</Option>
                            <Option value={3}>After</Option>
                            <Option value={4}>Before</Option>
                        </Select>
                        )}
                    </Col>
                    <Col span={16}>
                        {this.state.selectStartDate === 1?
                        <DatePicker onChange={this.onChangeIs} style={{ width: 210 }}/>
                        :null
                        }
                        
                        {this.state.selectStartDate === 2?
                        <RangePicker onChange={this.onChangeBetween} />
                        :null
                        }

                        {this.state.selectStartDate === 3?
                        <DatePicker onChange={this.onChangeAfter} style={{ width: 210 }}/>
                        :null
                        }

                        {this.state.selectStartDate === 4?
                        <DatePicker onChange={this.onChangeBefore} style={{ width: 210 }}/>
                        :null
                        }
                    </Col>
                </Row>     
              </Card>   
              <br/>
              <Card hoverable='true'>
                <Table rowKey={record => record.id} columns={columns} dataSource={this.state.filteredData}  pagination={{ pageSize: 5 }} size="middle" />
              </Card> 

                {leave.length!==0? <Modal
                  title="Full Leave Record"
                  visible={this.state.visible}
                  onCancel={this.handleOk}
                  footer={null}
                >
                  <p>Date of Request: <Tag color="volcano">{leave.formatDateTime}</Tag> Leave Type: <Tag color="volcano">{leave.type}</Tag></p>
                  <p>Leave Period: <Tag color="volcano">{leave.startDate} {leave.startHalf }</Tag>To  <Tag color="volcano">{leave.endDate} {leave.endHalf}</Tag></p>
                  <p>Number of Days: <Tag color="volcano">{leave.number_of_leave_days}</Tag> Status: <Tag color="volcano">{leave.status}</Tag></p>
                      <p>Supervisor 01: <Tag color="volcano">{leave.supervisor1}</Tag>  Supervisor 02: <Tag color="volcano">{leave.supervisor2}</Tag></p>
                  <p> Checked By: <Tag color="volcano">{leave.checkBy}</Tag> Checked On: <Tag color="volcano">{leave.checkTime}</Tag></p>
                  <p>Duty cover by: <Tag color="volcano">{leave.duty} </Tag></p>
                  <p>Special note: <Tag color="volcano">{leave.specialNotes}</Tag></p>
                  <p>If reject, reason: <Tag color="volcano">{leave.reject}</Tag></p>
                </Modal>: null}
            </div>
        )
    }
}    

const WrappedHistory = Form.create({ name: 'history' })(History);

export default WrappedHistory;
