import React from 'react';
import 'antd/dist/antd.css';
import {withRouter} from 'react-router-dom';
import { PageHeader, Icon, Button, Modal } from 'antd';
import axios from 'axios';

const { confirm } = Modal;

class Header extends React.Component { 

    componentWillMount () {
        setTimeout(() => {
            this.setState({
                user_name: localStorage.getItem("user_name"),
                user_role: localStorage.getItem("user_role")
            })
        }, 100);

        axios.get('http://localhost:5000/api/auth/info',
        {
          headers: {
            Authorization: 'Bearer ' + localStorage.getItem("header")
          }
        })
        .then(res => {
            this.setState({
                user_name: res.data.firstName+ " " + res.data.secondName,
                user_role: res.data.role
            })
          localStorage.setItem("user_id", res.data.id);
          localStorage.setItem("user_name", res.data.firstName+" "+res.data.secondName);
          localStorage.setItem("user_role", res.data.role);
          this.props.history.push('/');
        }).catch(e => {
          console.log(e);
        });
    }

    state = {
        user_name: null,
        user_role: null
    }
    handleLogout = (e) => {
        e.preventDefault();
        confirm({
          title: 'Do you want to logout?',
          onOk: () => {
            localStorage.removeItem("user_id");
            localStorage.removeItem("user_name");
            localStorage.removeItem("user_role");
            localStorage.removeItem("header");
            this.props.history.push('/login')
          },
          onCancel() {
            console.log('Cancel');
          },
        });
    }

    render () {
        const routes = [
            {
                path: '/',
                breadcrumbName: "Home",
            },
            {
                path: 'first',
                breadcrumbName: 'Second-level Menu',
            },
            {
                path: 'second',
                breadcrumbName: 'Third-level Menu',
            },
        ];

    return (
        <PageHeader
           
            title={this.state.user_name}
            style={{
            background: 'white',    
            border: '1px solid rgb(235, 237, 240)',
            }}
            subTitle={this.state.user_role}
            extra={[
            <Button key="1" type="primary" onClick={this.handleLogout} shape="circle">
               <Icon type="logout" />
            </Button>
            ]}
            
            avatar={{ src: 'https://avatars1.githubusercontent.com/u/8186664?s=460&v=4', size: '1000px'}}
            breadcrumb={{ routes }}
            
        >
        </PageHeader>
        );
    }
}          

export default withRouter(Header);
