import React from 'react';
import 'antd/dist/antd.css';
import axios from 'axios';
import { Calendar, Card, Modal, Form, Input, message } from 'antd';
import moment from 'moment';

const ReasonForm = Form.create({ name: 'form_in_modal' })(

  class extends React.Component {
    render() {
      const { visible, onCancel, onCreate, form } = this.props;
      const { getFieldDecorator } = form;
      return (
        <Modal
          visible={visible}
          title="Set holiday"
          okText="Save"
          onCancel={onCancel}
          onOk={onCreate}
        >
          <Form layout="vertical">
            <Form.Item label="Reason">
              {getFieldDecorator('reason', {
                rules: [{ required: true, message: 'Please input the reason' }],
              })(<Input type="textarea"/>)}
            </Form.Item>
          </Form>
        </Modal>
      );
    }
  },
);

class LeaveCalender extends React.Component {
  
  state = {
    value: null,
    selectedValue: moment('2020-01-25'),
    visible: false
  };

  onSelect = value => {
    this.setState({
      value,
      selectedValue: value,
      visible: true
    });
  };

  handleCancel = () => {
    const { form } = this.formRef.props;
    form.resetFields(); 
    this.setState({ 
      visible: false
    });
  };

  handleSet = () => {
    
    const { form } = this.formRef.props;
    form.validateFields((err, values) => {

      const calendar = {
        date: this.state.selectedValue.format('YYYY-MM-DD'),  
        reason : values.reason
      }

      if (!err) {
        axios.post('http://localhost:5000/calender/set_date', 
        calendar,
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            if(res.data === true){
              message.success("Set ",this.state.selectedValue.format('YYYY-MM-DD')," as the ",values.reason,"!");
              this.props.form.resetFields(); 
              this.handleCancel();
            }  else{
              message.error("Internal server error!"); 
            }
        }).catch(e => {
          console.log(e);
          message.error("Something went wrong!"); 
        })
        
      }
    });
  }

  saveFormRef = formRef => {
    this.formRef = formRef;
  };

  render() {
    return (
      <div>
        <Card>
          <Calendar onSelect={this.onSelect}  />
        </Card>

        <ReasonForm
          wrappedComponentRef={this.saveFormRef}
          visible={this.state.visible}
          onCancel={this.handleCancel}
          onCreate={this.handleSet}
        />
      </div>
    );
  }
}


const WrappedLeaveCalender = Form.create({ name: 'calender' })(LeaveCalender);

export default WrappedLeaveCalender;
