import React from 'react';
import 'antd/dist/antd.css';
import {withRouter} from 'react-router-dom';
import moment from 'moment';
import axios from 'axios';
import { PageHeader, Icon, Button, Modal, Row, Col, Avatar, Tag, Drawer } from 'antd';

const { confirm } = Modal;

class Header extends React.Component { 

    componentWillMount(){
        this.setState({
            data : []
        })
        axios.get('http://localhost:5000/leave/absent', 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("res", res.data);
            this.setState({
                data : res.data
            })
        }) 
    } 

    handleLogout = (e) => {
        e.preventDefault();
        confirm({
          title: 'Do you want to logout?',
          onOk: () => {
            localStorage.removeItem("header");
            this.props.history.push('/login')
          },
          onCancel() {
            console.log('Cancel');
          },
        });
    }

    state = {
        visible: false,
        data: []
    };

    toggle = () => {
        this.setState({
          visible: true
        });
    };

    onClose = () => {
        this.setState({
          visible: false
        });
    };

    render () {

    return (
        <div>
        <PageHeader
            style={{   
            border: '1px solid rgb(235, 237, 240)',
            backgroundImage: 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSQhsbd4x-xRzJrNDxCCL0qT7mQa_tk4jS9WvFALCBAbKFSWwqw',
            }}
        >
            <Row>
                <Col span={3}>
                    <Avatar size={90} icon="user"  src= 'https://avatars1.githubusercontent.com/u/8186664?s=460&v=4'/>
                </Col>
                <Col span={16}>
                    <span style={{fontSize:'30px'}}>{this.props.name}</span><br/>
                    <span style={{fontSize:'20px', color:'gray'}}>{this.props.department}</span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                    <span style={{fontSize:'20px', color:'red'}}>{this.props.role}</span> 
                </Col>
                <Col span={5}>
                    <Tag color="volcano">{moment().format('LL')}</Tag>&nbsp;&nbsp;&nbsp;&nbsp;
                    <Button key="1" type="primary" onClick={this.toggle} shape="circle">
                        <Icon 
                            className="trigger"
                            type= 'menu-fold' 
                        />
                    </Button>,&nbsp;&nbsp;&nbsp;
                    <Button key="2" type="primary" onClick={this.handleLogout} shape="circle">
                        <Icon type="logout" />
                    </Button>
                </Col>
            </Row>
        </PageHeader>

            <Drawer
            title="Absent on today"
            width={250}
            onClose={this.onClose}
            visible={this.state.visible}
            >
                <List
                    itemLayout="horizontal"
                    dataSource={this.state.data}
                    renderItem={item => (
                    <List.Item>
                        <List.Item.Meta
                        avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                        title={item.title}
                        />
                    </List.Item>
                    )}
                />
            </Drawer>
        </div>
        );
    }
}          

export default withRouter(Header);
