import React from "react";
import { Icon, Form, Button, Row, Col, Input, Card } from 'antd';
import axios from 'axios';
import swal from 'sweetalert';

class ChangePassword extends React.Component {

    componentWillMount () {
        axios.get('http://localhost:5000/employee/'+ 4, 
            {
                headers: {
                    Authorization: 'Bearer ' + localStorage.getItem("header")
                }
            })
            .then(res => {
                console.log("res", res.data);
            })
    }

    state = {
        confirmDirty: false,
        currentPassword: undefined
    }

    changePassword = e => {
        e.preventDefault();
        this.props.form.validateFieldsAndScroll((err, values) => {
          if (!err) {
            console.log("Received values of form: ", values);
    
            var passwordHash = require('password-hash');
            var hashedPassword = passwordHash.generate(values.newpassword);
            const user_id = 7;
    
            axios.get('http://localhost:5000/employee/change/'+user_id, 
            {
                headers: {
                    Authorization: 'Bearer ' + localStorage.getItem("header")
                }
            })
            .then(res => {
                if(passwordHash.verify(values.password, res.data.password)){
                  const employee = {
                    emp_id : user_id || undefined,
                    password: hashedPassword || undefined
                  }
                  axios.post('http://localhost:5000/employee/changepassword',employee, 
                  {
                      headers: {
                          Authorization: 'Bearer ' + localStorage.getItem("header")
                      }
                  })
                  .then(res => {
                    if(res.data){
                      swal("Yeah!","Password Updated !!!","success");
                      window.location.reload();
                    }
                    else{
                      swal("Oops!","Something Went Wrong!!!","error"); 
                    }
                  }).catch(e => console.log("eeeeeeeeeeee",e))
                }
                else{
                  swal("Oops!","Invalid Current Password !!!","warning");
                }
            }).catch(e => {
                console.log("eeeeeeeeeeee",e)
                swal("Oops!","Something Went Wrong!!!","error");          
              });
          }
        });
    }
    
    handelCancel() {
        window.location.reload();
    }

    handleConfirmBlur = e => {
        const { value } = e.target;
        this.setState({ confirmDirty: this.state.confirmDirty || !!value });
    };
    
    compareToFirstPassword = (rule, value, callback) => {
        const { form } = this.props;
        if (value && value !== form.getFieldValue("newpassword")) {
          callback("Two passwords that you enter is inconsistent!");
        } else {
          callback();
        }
    };
    
    validateToNextPassword = (rule, value, callback) => {
        const { form } = this.props;
        if (value && this.state.confirmDirty) {
          form.validateFields(["confirm"], { force: true });
        }
        callback();
    };

    render () {

        const { getFieldDecorator } = this.props.form;
       
        const formItemLayout = {
            labelCol: {
              xs: { span: 24 },
              sm: { span: 8 },
            },
            wrapperCol: {
              xs: { span: 24 },
              sm: { span: 16 },
            },
        };

        const tailFormItemLayout = {
            wrapperCol: {
              xs: {
                span: 24,
                offset: 0,
              },
              sm: {
                span: 16,
                offset: 8,
              },
            },
        };

        return(
            <div>
                <Card title='Change Password' bordered={false}> 
                 <Form {...formItemLayout} layout="vertical" hideRequiredMark>
            
                    <Row gutter={16}>
                    <Form.Item hasFeedback>
                        {getFieldDecorator("password", {
                        rules: [
                            {
                            required: true,
                            message: "Please input your current password!"
                            },
                            {
                            validator: this.validateToNextPassword
                            }
                        ]
                        })(<Input.Password 
                        id= '1'
                        prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                        placeholder="Current Password"
                        />)}
                    </Form.Item>
                    </Row>

                    <Row gutter={16}>
                    <Form.Item hasFeedback>
                        {getFieldDecorator("newpassword", {
                        rules: [
                            {
                            required: true,
                            message: "Please input your new password!"
                            },
                            {
                            validator: this.validateToNextPassword
                            }
                        ]
                        })(<Input.Password 
                        id= '2'
                        prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                        placeholder="New Password"
                        />)}
                    </Form.Item>
                    </Row>

                    <Row gutter={16}>
                    
                    <Form.Item hasFeedback>
                        {getFieldDecorator("confirm", {
                        rules: [
                            {
                            required: true,
                            message: "Please confirm your new password!"
                            },
                            {
                            validator: this.compareToFirstPassword
                            }
                        ]
                        })(<Input.Password 
                        id= '3'
                        onBlur={this.handleConfirmBlur}
                        prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                        placeholder="Confirm Password"
                        />)}
                    </Form.Item>
                    </Row> 
                        <Form.Item {...tailFormItemLayout}>
                        <Button type="primary" onClick={this.changePassword} style={{width:'100px'}}>
                        <Icon type="check-circle" /> 
                            Submit
                        </Button>
                        &nbsp;&nbsp;&nbsp;&nbsp;
                        <Button type="danger" onClick={this.handelCancel} style={{width:'100px'}}>
                        <Icon type="close-circle" /> 
                            Cancel
                        </Button>
                        </Form.Item>

                        {/* <Col span={7}> 
                        <Form.Item {...tailFormItemLayout}>
                        <Button type="danger" onClick={this.handelCancel} style={{width:'100px', float:'left'}}>
                        <Icon type="close-circle" /> 
                            Cancel
                        </Button>
                        </Form.Item>
                        </Col> 
                        </Row> */}
                </Form>
                </Card>
            </div>
        );
    }
}

const WrappedChangePassword= Form.create({ name: 'register' })(ChangePassword);

export default WrappedChangePassword;
