import React, { Component } from 'react';
import axios from 'axios'; 

const loginStyle = {
    width: "90%",
    maxWidth: "500px",
    margin: "100px auto",
    border: "5px solid #ddd",
    borderRadius: "5px",
    padding: "30px"
}

export default class Update extends Component {
    state = {
        data: [],
        id: ''
    }

    handleChange = (e) => {
        this.setState({
          [e.target.id] : e.target.value
        })
    }

    click = (e) => {
        axios.get('http://localhost:5000/delete/'+this.state.id)
        .then(res => {
            this.setState({
                data : res.data
            })
        })
        console.log("state    "+this.state.id);   
    }    
    
    render() {
        return (
            <div>
                <div style={loginStyle} className="white">
                    <form onSubmit={this.click}>
                        <h5>Update the Users</h5>
                        <br/>
                        <label htmlFor="id"> ID </label>  
                        <div className="input-field">  
                            <input id="id" type="text" onChange={this.handleChange}/>       
                        </div>
                                                
                        <div className="input-field">
                            <button className="btn blue lighten-1 z-depth-0">Search</button>  
                        </div>
                    </form>
                </div>
                <div className="card-title gray">
                    {this.state.data}
                </div>
            </div>
        )
    }
}
