import React, { Component } from 'react';
import axios from 'axios';
import Table from 'react-bootstrap/Table';
import 'antd/dist/antd.css';
import { AutoComplete, Button, Descriptions, Popover } from 'antd';
import swal from 'sweetalert';

const loginStyle = {
    width: "100%",
    maxWidth: "1300px",
    margin: "30px auto",
    border: "5px solid #ddd",
    borderRadius: "3px",
    padding: "0px"
}

export default class Dashboard extends Component {
    
    componentWillMount(){
        axios.get('http://localhost:5000/all')
        .then(res => {
            console.log("res", res.data);
            this.setState({
                data : res.data
            })
        }) 
    } 

    state = {
        user : false,
        value : '',
        dataSource : [],
        data : [],
        filter : [],
        favorites : [],
        user_name : undefined
    }
   
    handleClick(id) {
        axios.get('http://localhost:5000/favorites/'+id)
        .then(res => {
            console.log("res", res.data);
            this.setState({
                favorites : res.data,
                user_name : res.data[0].user_name
            })
            console.log(this.state.user_name);
        })
    }

    handleUpdate() {

    }

    handleDelete(id,e) {
        e.preventDefault();
        
        swal({
            text: "Wanna delete that user?",
            buttons: {
                confirm: "Delete",
                cancel: true,
            }
        })
        .then(willSearch => {
            if (willSearch) {
                axios.get('http://localhost:5000/delete/'+id)
                .then(res => {
                    swal("Omg!",res.data,"success"); 
                }).catch( err => {
                    swal("Oops!","User does not exist!!!","error");
                })
            }
        })
    }

    onSelect = (value) => {
        const data = this.state.data;
        let len = data.length;
        for(var i=0; i<len; i++){
          var string = data[i]["name"];
          if(value===string){
            this.setState({
                user : true,
                filter : data[i]
              });
          }}
        
      }
    
      onSearch = searchText => {
        const data = this.state.data;
        let len = data.length;
        var arr = [];
        for(var i=0; i<len; i++){
          var re = new RegExp(searchText, 'gi');
          var string = data[i]["name"];
          if(string.match(re)){
            arr.push(string);
          }}
        this.setState({
          dataSource: arr
        });
      };
    
      onChange = value => {
        this.setState({ value });
      };

    render() {
        const { dataSource, value, data, user, filter, favorites, user_name } = this.state; 
        let us = user; 
        const text = <h6>{user_name}'s Favorites</h6>;
        const content = (
            favorites.map(item => (
                <React.Fragment key={item.id}>                        
                   <div style={{maxWidth: "600px"}}>
                        <Descriptions >
                            <Descriptions.Item label="Food">{item.food}</Descriptions.Item>
                            <Descriptions.Item label="Drink">{item.drink}</Descriptions.Item>
                            <Descriptions.Item label="Animal">{item.animal}</Descriptions.Item>
                            <Descriptions.Item label="Bird"> {item.bird}</Descriptions.Item>
                            <Descriptions.Item label="Hobby">{item.hobby}</Descriptions.Item>
                            <Descriptions.Item label="Place"> {item.place} </Descriptions.Item>
                        </Descriptions>
                        <hr/>
                    </div>
                </React.Fragment>
            ))
        );      

        return (
            <div style={loginStyle} className="white card z-depth-0 card-content center">
                <div>
                    <AutoComplete
                    value={value}
                    dataSource={dataSource}
                    style={{ width: 200 }}
                    onSelect={this.onSelect}
                    onSearch={this.onSearch}
                    onChange={this.onChange}
                    placeholder="Filter Here By Name"
                    />
                </div>
                <div className="scrollable-container-dash">
                <div className="background">
                <Table striped bordered hover >
                    <thead>
                       <tr>
                            <td> &nbsp;&nbsp;&nbsp;&nbsp; </td>
                            <td><h6>Id</h6></td>
                            <td><h6>Name</h6></td>
                            <td><h6>Email</h6></td>
                            <td><h6>Contact</h6></td>
                            <td><h6> Salary</h6></td>
                            <td><h6>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</h6></td>
                        </tr>
                    </thead>
                    <tbody>
                        {us && value!==''? 
                        <tr>
                            <td> &nbsp;&nbsp;&nbsp;&nbsp; </td>
                            <td> {filter.user_id} </td>
                            <td> {filter.name} </td>
                            <td> {filter.email} </td>
                            <td> {filter.contact} </td>
                            <td> {filter.salary} </td>
                            <td> 
                                <div className="demo">
                                <div style={{ clear: 'both', whiteSpace: 'nowrap' }}>
                                    <Popover placement="bottomRight" title={text} content={content} trigger="click">
                                    <Button type="primary" onClick={this.handleClick.bind(this, filter.user_id)} shape="circle" icon="search" />
                                    </Popover>
                                    &nbsp;&nbsp;&nbsp;
                                    <Button type="primary" onClick={this.handleClick.bind(this, filter.user_id)} shape="circle" icon="edit" />
                                    &nbsp;&nbsp;&nbsp;
                                    <Button type="danger" onClick={this.handleDelete.bind(this, filter.user_id)} shape="circle" icon="delete" />
                                </div>
                                </div>
                            </td>
                        </tr>
                        :   data.map(item => (
                            <React.Fragment key={item.user_id}>                        
                                <tr>
                                    <td> &nbsp;&nbsp;&nbsp;&nbsp; </td>
                                    <td> {item.user_id} </td>
                                    <td> {item.name} </td>
                                    <td> {item.email} </td>
                                    <td> {item.contact} </td>
                                    <td> {item.salary} </td>
                                    <td> 
                                        <div className="demo">
                                        <div style={{ clear: 'both', whiteSpace: 'nowrap' }}>
                                            <Popover placement="bottomRight" title={text} content={content} trigger="click">
                                            <Button type="primary" onClick={this.handleClick.bind(this, item.user_id)} shape="circle" icon="search" />
                                            </Popover>
                                            &nbsp;&nbsp;&nbsp;
                                            <Button type="primary" onClick={this.handleClick.bind(this, item.user_id)} shape="circle" icon="edit" />
                                            &nbsp;&nbsp;&nbsp;
                                            <Button type="danger" onClick={this.handleDelete.bind(this, item.user_id)} shape="circle" icon="delete" />
                                        </div>
                                        </div>
                                    </td>
                                </tr>
                            </React.Fragment>
                                ))
                        }
                    </tbody>
                </Table>
                </div>
                <div>
          <Drawer
          title="Change Password"
          width={300}
          onClose={this.onClose}
          visible={visible}
          >
          <Form layout="vertical" hideRequiredMark>
            
            <Row gutter={16}>
              <Form.Item hasFeedback>
                {getFieldDecorator("password", {
                  rules: [
                    {
                      required: true,
                      message: "Please input your current password!"
                    },
                    {
                      validator: this.validateToNextPassword
                    }
                  ]
                })(<Input.Password 
                  id= '1'
                  prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                  placeholder="Current Password"
                  />)}
              </Form.Item>
            </Row>

            <Row gutter={16}>
              <Form.Item hasFeedback>
                {getFieldDecorator("newpassword", {
                  rules: [
                    {
                      required: true,
                      message: "Please input your new password!"
                    },
                    {
                      validator: this.validateToNextPassword
                    }
                  ]
                })(<Input.Password 
                  id= '2'
                  prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                  placeholder="New Password"
                  />)}
              </Form.Item>
            </Row>

            <Row gutter={16}>
            
              <Form.Item hasFeedback>
                {getFieldDecorator("confirm", {
                  rules: [
                    {
                      required: true,
                      message: "Please confirm your new password!"
                    },
                    {
                      validator: this.compareToFirstPassword
                    }
                  ]
                })(<Input.Password 
                  id= '3'
                  onBlur={this.handleConfirmBlur}
                  prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                  placeholder="Confirm Password"
                />)}
              </Form.Item>
            </Row>
          </Form>
          <div
            style={{
              position: 'absolute',
              left: 0,
              bottom: 0,
              width: '100%',
              borderTop: '1px solid #e9e9e9',
              padding: '10px 16px',
              background: '#fff',
              textAlign: 'right',
            }}
          >
            <Button onClick={this.onClose} style={{ marginRight: 8 }}>
              Cancel
            </Button>
            <Button onClick={this.onSubmit} type="primary">
              Submit
            </Button>
          </div>
        </Drawer>
        </div>
                </div>
            </div>
        )
    }
}
