import React, { Component } from 'react';
import axios from 'axios';

const loginStyle = {
    width: "90%",
    maxWidth: "500px",
    margin: "100px auto",
    border: "5px solid #ddd",
    borderRadius: "5px",
    padding: "30px"
}

export default class Dashboard extends Component {
    
    state = {
        data: []
    }
    click = (e) => {
        axios.get('http://localhost:5000/all')
        .then(res => {
            console.log("res", res.data);
            this.setState({
                data : res.data
            })
        })   
    }
    
    render() {
        return (
            <div style={loginStyle} className="section notf">
            <div className="card z-depth-0">
                <div className="card-content center">
                    <span className="card-title">
                        Get Data
                    </span>
                    <div className="input-field">
                        <button onClick={this.click} className="btn blue lighten-1 z-depth-0">Search</button>  
                    </div>
                    <div className="card-title gray">
                    
                    <table>
                        <th>
                            <tr></tr>
                        </th>
                    <ul>
                        {this.state.data.map(item => (
                            <li key={item.id}><h6>{item.id}   |   {item.name}   |   {item.salary}</h6><hr/></li>
                        ))}
                    </ul>
                    </table>    
                    </div>
                </div>
            </div>
        </div>
        )
    }
}
