import React from 'react';
import { Link, Switch, Route } from 'react-router-dom';
import 'antd/dist/antd.css';
import './index.css';
import { Layout, Menu, Breadcrumb, Icon, Dropdown, Avatar, Drawer, Form, Button, Row, Input } from 'antd';
import Signup from '../auth/Signup';
import Login from '../auth/Login';
import Dashboard from './Dashboard';
import Delete from './Delete';
import Update from './Update';
import Calendar from './Header';
import Profile from './Profile';
import Favorite from './favorite';
import ReadFav from './ReadFav';
import axios from 'axios';
import swal from 'sweetalert';

const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;


class SiderDemo extends React.Component {
  
  state = {
    collapsed: false,
    redirectToReferrer: false,
    color: '#00a2ae',
    visible: false
  };

  onCollapse = collapsed => {
    console.log(collapsed);
    this.setState({ collapsed });
  };
  
  handleClick = () => {
    this.setState({redirectToReferrer: true});
    localStorage.removeItem("user");
  }

  showDrawer = () => {
    this.setState({
      visible: true
    });
  };

  onClose = () => {
    this.setState({
      visible: false
    });
  };

  onSubmit = (e) => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        console.log("Received values of form: ", values);
        const user = {
            user_id : localStorage.getItem("id"),
            password: values.password || undefined,
            salary: values.newpassword || undefined
        }
        axios.post('http://localhost:5000/change', user)
        .then(res => {
            console.log("res", res.data);
            if(res.data){
              swal("Yeah!","Update Password Successfully!!!","success");
              this.setState({
                visible: false
              });
            }
            else{
              swal("Opps!","Update Password Unsuccessfull!!!","error");
            }
        }).catch(e => console.log("eeeeeeeeeeee",e));
        console.log("user", user);  
      }
    });
  };  

  handleConfirmBlur = e => {
    const { value } = e.target;
    this.setState({ confirmDirty: this.state.confirmDirty || !!value });
  };

  compareToFirstPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && value !== form.getFieldValue("newpassword")) {
      callback("Two passwords that you enter is inconsistent!");
    } else {
      callback();
    }
  };

  validateToNextPassword = (rule, value, callback) => {
    const { form } = this.props;
    if (value && this.state.confirmDirty) {
      form.validateFields(["confirm"], { force: true });
    }
    callback();
  };

  render() {
    const {redirectToReferrer, visible} = this.state;
    const { getFieldDecorator } = this.props.form;
    const menu = (
      <Menu>
        <Menu.Item onClick={this.showDrawer}>
          Change Password
        </Menu.Item>
        <Menu.Item onClick={this.handleClick}>
          Log Out
        </Menu.Item>
      </Menu>
    );
    const user = localStorage.getItem("user");
    if (redirectToReferrer) {
      return (<Login/>)
    }
    else{
    return (
      <Layout style={{ minHeight: '100vh' }}>
        <Sider 
          collapsible collapsed={this.state.collapsed} 
          onCollapse={this.onCollapse}
        >
          <div className="logo" >&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
          <img height= '35px' alt="example" src="https://rakiya.lk/files/recruiters/1805/logo.png" />
          </div>
          <Menu theme="dark" defaultSelectedKeys={['1']} selectedKeys={[]} mode="inline">
            
            <Menu.Item key="1">
              <Link to='/dashboard'/>
              <Icon type="read" />
              <span>User Details</span>
            </Menu.Item>
            <Menu.Item key="2">
                <Link to='/favorite'/>
                <Icon type="heart" />
                <span>Add Favorite</span>
            </Menu.Item>
            <Menu.Item key="3">
                <Link to='/read'/>
                <Icon type="hourglass" />
                <span>View Favorites</span>
            </Menu.Item>
            <Menu.Item key="6">
                <Link to='/calendar'/>
                <Icon type="calendar" />
                <span>Calendar</span>
              </Menu.Item>
            <SubMenu
              key="sub1"
              title={
                <span>
                  <Icon type="appstore" />
                  <span>Applications</span>
                </span>
              }
            >
              <Menu.Item key="4">
                <Link to='/update'/>
                <Icon type="edit" />
                <span>Update</span>
              </Menu.Item>
              <Menu.Item key="5">
                <Link to='/delete'/>
                <Icon type="delete" />
                <span>Delete</span>
              </Menu.Item>
            </SubMenu>
          </Menu>
        </Sider>
        <Layout>
          <Header style={{ background: 'gray', padding: 0 }} >
            <nav className="nav-wrapper black darken-3">
            <div>
            <h5 className="brand-logo center">React | Spring-Boot | MySQL</h5>
            </div>
            <ul className="right">
              <li>
              <Avatar style={{ backgroundColor: this.state.color, verticalAlign: 'middle' }} size="large">
                {user}
              </Avatar>
              </li>
              <li>
              &nbsp;&nbsp;&nbsp;&nbsp;
              </li>
              <li>
                <Dropdown overlay={menu} placement="bottomRight">
                  <Icon type="menu" />  
                </Dropdown>&nbsp;&nbsp;&nbsp;&nbsp;
              </li>
            </ul>
            </nav>
          </Header>
          <Content style={{ margin: '0 16px' }}>
            <Breadcrumb style={{ margin: '16px 0' }}>
              <Breadcrumb.Item>User</Breadcrumb.Item>
              <Breadcrumb.Item>Bill</Breadcrumb.Item>
            </Breadcrumb>
            <div style={{ padding: 30, background: '#ECECEC', minHeight: 570 }}>
              <Switch>
                <Route path='/signup' component={Signup} />
                <Route path='/profile' component={Profile} />
                <Route path='/delete' component={Delete} />
                <Route path='/update' component={Update} />
                <Route path='/favorite' component={Favorite} />
                <Route path='/read' component={ReadFav} />
                <Route exact path='/' component={Dashboard} />
                <Route exact path='/dashboard' component={Dashboard} />
                <Route exact path='/calendar' component={Calendar} />
              </Switch>
            </div>
            <div>
          <Drawer
          title="Change Password"
          width={300}
          onClose={this.onClose}
          visible={visible}
          >
          <Form layout="vertical" hideRequiredMark>
            
            <Row gutter={16}>
              <Form.Item hasFeedback>
                {getFieldDecorator("password", {
                  rules: [
                    {
                      required: true,
                      message: "Please input your current password!"
                    },
                    {
                      validator: this.validateToNextPassword
                    }
                  ]
                })(<Input.Password 
                  id= '1'
                  prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                  placeholder="Current Password"
                  />)}
              </Form.Item>
            </Row>

            <Row gutter={16}>
              <Form.Item hasFeedback>
                {getFieldDecorator("newpassword", {
                  rules: [
                    {
                      required: true,
                      message: "Please input your new password!"
                    },
                    {
                      validator: this.validateToNextPassword
                    }
                  ]
                })(<Input.Password 
                  id= '2'
                  prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                  placeholder="New Password"
                  />)}
              </Form.Item>
            </Row>

            <Row gutter={16}>
            
              <Form.Item hasFeedback>
                {getFieldDecorator("confirm", {
                  rules: [
                    {
                      required: true,
                      message: "Please confirm your new password!"
                    },
                    {
                      validator: this.compareToFirstPassword
                    }
                  ]
                })(<Input.Password 
                  id= '3'
                  onBlur={this.handleConfirmBlur}
                  prefix={<Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />}
                  placeholder="Confirm Password"
                />)}
              </Form.Item>
            </Row>
          </Form>
          <div
            style={{
              position: 'absolute',
              left: 0,
              bottom: 0,
              width: '100%',
              borderTop: '1px solid #e9e9e9',
              padding: '10px 16px',
              background: '#fff',
              textAlign: 'right',
            }}
          >
            <Button onClick={this.onClose} style={{ marginRight: 8 }}>
              Cancel
            </Button>
            <Button onClick={this.onSubmit} type="primary">
              Submit
            </Button>
          </div>
        </Drawer>
        </div>
          </Content>
          
          <Footer style={{ textAlign: 'center' }}>React | Spring-Boot | MySQL ©2019 Created by Pramuditha</Footer>
        </Layout>
      </Layout>
    );}
  }
}

const Main = Form.create()(SiderDemo);

export default Main;

