import React, { Component } from 'react';
import axios from 'axios';
import 'antd/dist/antd.css';
import {Table, Button, Icon, Input, Drawer, Card, Tag, Modal } from 'antd';
import Highlighter from 'react-highlight-words';
import swal from 'sweetalert';
import UpdateEmployee from '../Employee/UpdateEmployee';
import AddEmployee from '../Employee/AddEmployee';

class ViewEmployee extends Component {
    
    componentWillMount(){
       this.reload();
    } 

    reload() {
      this.setState({
        data : []
      })

    axios.get('http://localhost:5000/employee/all', 
    {
        headers: {
            Authorization: 'Bearer ' + localStorage.getItem("header")
        }
    })
    .then(res => {
        console.log("First pop", res.data.shift());
        this.setState({
            data : res.data
        })
    }) 
    }

    state = {
        searchText: '',
        data : [],
        visibleUpdate: false,
        visibleAdd: false,
        emp_id: undefined,
        updateEmployee: [],
        emp : [],
        visible: false 
    };
    
    info = (emp) => {
      this.setState({
        visible: true,
        emp: emp
      });
    };
  
    handleOk = e => {
      console.log(e);
      this.setState({
        visible: false,
      });
    };

      getColumnSearchProps = dataIndex => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
          <div style={{ padding: 8 }}>
            <Input
              ref={node => {
                this.searchInput = node;
              }}
              placeholder={`Search ${dataIndex}`}
              value={selectedKeys[0]}
              onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
              onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
              style={{ width: 188, marginBottom: 8, display: 'block' }}
            />
            <Button
              type="primary"
              onClick={() => this.handleSearch(selectedKeys, confirm)}
              icon="search"
              size="small"
              style={{ width: 90, marginRight: 8 }}
            >
              Search
            </Button>
            <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
              Reset
            </Button>
          </div>
        ),
        filterIcon: filtered => (
          <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
        ),
        onFilter: (value, record) =>
          record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase()),
        onFilterDropdownVisibleChange: visible => {
          if (visible) {
            setTimeout(() => this.searchInput.select());
          }
        },
        render: text => (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={[this.state.searchText]}
            autoEscape
            textToHighlight={text.toString()}
          />
        ),
      });
    
      handleSearch = (selectedKeys, confirm) => {
        confirm();
        this.setState({ searchText: selectedKeys[0] });
      };
    
      handleReset = clearFilters => {
        clearFilters();
        this.setState({ searchText: '' });
      };   
    
      showAddDrawer = () => {
        this.setState({
            visibleAdd: true
          });
    }

    onAddClose = () => {
        this.setState({
          visibleAdd: false
        });
        this.reload();
    };

      showUpdateDrawer = (emp) => {
    
        axios.get('http://localhost:5000/employee/'+ emp.emp_id, 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
            console.log("res", res.data);
            this.setState({
                visibleUpdate: true,
                updateEmployee : res.data
            })
        })
      };
    
    onUpdateClose = () => {
        this.setState({
          visibleUpdate: false,
          updateEmployee: []
        });
        window.location.reload();
    };

    handleDelete(emp,e) {
        e.preventDefault();
        console.log("sssssss", emp.emp_id)
        swal({
            text: "Sure to resign "+ emp.first_name + " " + emp.second_name + "?",
            buttons: {
                confirm: "Resign",
                cancel: true,
            }
        })
        .then(willSearch => {
            if (willSearch) {
                axios.get('http://localhost:5000/employee/delete/'+emp.emp_id, 
                {
                    headers: {
                        Authorization: 'Bearer ' + localStorage.getItem("header")
                    }
                })
                .then(res => {
                    swal("Yeah!",res.data,"success"); 
                    this.reload(); 
                }).catch( err => {
                    swal("Oops!","Something Went Wrong!!!","error");
                })
            }
        })
    }
    
    render() {
       
        const {emp} = this.state;      
        const columns = [
            {
              title: 'Id',
              dataIndex: 'emp_id'
            },
            {
              title: 'Name',
              dataIndex: 'first_name',
              ...this.getColumnSearchProps('first_name'),
            },
            {
              title: 'Email',
              dataIndex: 'email',
            },
            {
                title: 'Contact',
                dataIndex: 'contact',
            },
            {
              title: 'Role',
              dataIndex: 'role',
              render: role => (
               role.role_name
              ),
            },
            {
              title: 'Department',
              dataIndex: 'department',
              render: role => (
               role.department_name
              )
            },
            {
                title: 'Supervisor 01',
                dataIndex: 'supervisor1',
                ...this.getColumnSearchProps('supervisor1'),
            },
            {
              title: 'Status',
              dataIndex: 'status',
              render: tag => {
                let color;
                if (tag === 'Resign') {
                  color = 'red';
                }else {
                  color = 'geekblue';
                }
                return (
                  <Tag color={color} key={tag}>
                    {tag}
                  </Tag>
                );
              }
              // ...this.getColumnSearchProps('status'),
          },
            {
              dataIndex: '',
              key: 'z',
              width: '3%',
              render: (emp) => <Icon type="info-circle" onClick={this.info.bind(this, emp)}/>,
            },
            {
                dataIndex: '',
                key: 'x',
                width: '3%',
                render: (emp) => <Icon type="edit" onClick={this.showUpdateDrawer.bind(this, emp)}  theme="twoTone" />,
            },
            {
                dataIndex: '',
                key: 'y',
                width: '3%',
                render: (emp) => <Icon type="close-circle" onClick={this.handleDelete.bind(this, emp)}  theme="twoTone" twoToneColor='#EE204D'/>,
            },

          ];

        return (
            <div>
              <Card hoverable='true'>
                <Button style={{float:'right'}} type="primary" onClick={this.showAddDrawer} shape="circle" icon="user-add" />
                <br/><br/>
                <Table rowKey={record => record.emp_id} columns={columns} dataSource={this.state.data}  pagination={{ pageSize: 10 }} size="middle" />

                <Drawer
                title="Update Existing Employee"
                width={570}
                onClose={this.onUpdateClose}
                visible={this.state.visibleUpdate}
                >
                    <UpdateEmployee update={this.state.updateEmployee} close={this.onUpdateClose} />
                </Drawer>

                <Drawer
                title="Add New Employee"
                width={570}
                onClose={this.onAddClose}
                visible={this.state.visibleAdd}
                >
                    <AddEmployee close={this.onAddClose}/>
                </Drawer>
                </Card> 
                {emp.length!==0? <Modal
                  title="Full Employee Detail"
                  visible={this.state.visible}
                  onOk={this.handleOk}
                  onCancel={this.handleOk}
                  footer={null}
                >
                  <p>ID: <Tag color="volcano">00{emp.emp_id}</Tag>  Name: <Tag color="volcano">{emp.first_name} {emp.second_name}</Tag></p>
                  <p>Email: <Tag color="volcano">{emp.email}</Tag> Contact: <Tag color="volcano">{emp.contact}</Tag></p>
                  <p>Address: <Tag color="volcano">{emp.residence}</Tag></p>
                  <p>Role: <Tag color="volcano">{emp.role.role_name}</Tag>  Status: <Tag color="volcano">{emp.status}</Tag> Department: <Tag color="volcano">{emp.department.department_name}</Tag></p>
                  <p>Supervisor 01: <Tag color="volcano">{emp.supervisor1}</Tag>   Supervisor 02: <Tag color="volcano">{emp.supervisor2}</Tag></p>
                  <p>Join Date: <Tag color="volcano">{emp.join_date}</Tag>   Confirm Date: <Tag color="volcano">{emp.confirm_date}</Tag></p>
                  <p>Annual: <Tag color="volcano">{emp.annual}</Tag>   Casual: <Tag color="volcano">{emp.casual}</Tag> Medical: <Tag color="volcano">{emp.medical}</Tag></p>
                </Modal>: null}
            </div>
        )
    }
}    


export default ViewEmployee;
