import React from 'react';
import axios from 'axios';
import swal from 'sweetalert';
import moment from 'moment';
import 'antd/dist/antd.css';
import {
  Form,
  Input,
  Tooltip,
  Icon,
  Select,
  Button,
  DatePicker,
  Radio,
  Card,
  Row,
  Col
} from 'antd';

const { Option } = Select;

class UpdateEmployee extends React.Component {

  componentWillMount(){
    this.setState({
      data : this.props.update,
      role_id : this.props.update.role.role_id,
      department_id : this.props.update.department.department_id,
      supervisors : [],
      departments : [],
      roles : []
    })
    console.log("Update Employee's Id: ",this.props.update.emp_id);

    axios.get('http://localhost:5000/employee/supervisor', 
    {
        headers: {
            Authorization: 'Bearer ' + localStorage.getItem("header")
        }
    })
    .then(res => {
        console.log("Supervisors:  ", res.data);
        this.setState({
            supervisors : res.data
        })
    })
    
    axios.get('http://localhost:5000/department/all', 
    {
        headers: {
            Authorization: 'Bearer ' + localStorage.getItem("header")
        }
    })
    .then(res => {
        console.log("Departments:  ", res.data);
        this.setState({
            departments : res.data
        })
    }) 

    axios.get('http://localhost:5000/role/all', 
    {
        headers: {
            Authorization: 'Bearer ' + localStorage.getItem("header")
        }
    })
    .then(res => {
        console.log("Roles:  ", res.data);
        this.setState({
            roles : res.data
        })
    }) 
  }

  state = {
    data: [],
    supervisors : [],
    departments : [],
    roles : [],
    confirmDirty: false,
    autoCompleteResult: [],
    join_date: undefined,
    confirm_date: undefined,
    role_id: undefined,
    department_id: undefined,
    joinFromRes: "2019-01-01",
    confirmFromRes: "2019-12-31"
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {

        var passwordHash = require('password-hash');
        var hashedPassword = passwordHash.generate("123");

        const employee = {
          first_name: values.first_name || undefined,
          second_name: values.second_name || undefined,
          initials: values.initials || undefined,
          gender: values.gender || undefined,
          email: values.email || undefined,
          residence: values.residence || undefined,
          contact: values.contact || undefined,
          role: {role_id: values.role} || this.state.role.role_id,
          department: {department_id: values.department} || this.state.department.department_id,
          supervisor1: values.supervisor1 || undefined,
          supervisor2: values.supervisor2 || undefined,
          join_date: this.state.join_date || this.state.data.join_date,
          confirm_date: this.state.confirm_date || this.state.data.confirm_date,
          password: hashedPassword || undefined
        }

        console.log('Values of object: ', employee.role);

        axios.post('http://localhost:5000/employee/update/'+this.state.data.emp_id, employee, 
        {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem("header")
            }
        })
        .then(res => {
          swal("Yeah!",res.data,"success");
          console.log("res", res.data)
          this.props.close();
        })
        .catch(e => {
          swal("Oops!","Didn't Updated!!!","error");
          console.log("res", e)
        })
      
      
      }
    });
  };

  onChangeJoin = (date, dateString) => {
    console.log("Date String: ",dateString);
    console.log("Date: ", date);
    this.setState({ join_date : date });
  };

  onChangeConfirm = (date, dateString) => {
    console.log(dateString);
    this.setState({ confirm_date : dateString });
    console.log("state", this.state.joinFromRes);
  };

  render() {
    const { getFieldDecorator } = this.props.form;
    const { update } = this.props;

    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
    };
    const tailFormItemLayout = {
      wrapperCol: {
        xs: {
          span: 24,
          offset: 0,
        },
        sm: {
          span: 16,
          offset: 8,
        },
      },
    };

    return (
        <div>
        <Card bordered={false} style={{ height: '1000px'}}>  
        <Form {...formItemLayout} onSubmit={this.handleSubmit}>
        
        <Form.Item label="First Name">
          {getFieldDecorator('first_name', {
            rules: [{ required: true, message: 'Please input first name!' }],
            initialValue: this.state.data.first_name
          })(<Input />)}
        </Form.Item>
        <Form.Item label="Second Name">
          {getFieldDecorator('second_name', {
            rules: [{ required: true, message: 'Please input second name!' }],
            initialValue: this.state.data.second_name
          })(<Input />)}
        </Form.Item>
        <Form.Item label="Initials">
          {getFieldDecorator('initials', {
            rules: [{ required: true, message: 'Please input initials!' }],
            initialValue: this.state.data.initials
          })(<Input />)}
        </Form.Item>
        <Form.Item label="Gender">
          {getFieldDecorator('gender', {
            rules: [{ required: true, message: 'Please input initials!' }],
            initialValue: this.state.data.gender
          })(
          <Radio.Group name="radiogroup">
          <Radio value={1}>Male</Radio>
          <Radio value={2}>Female</Radio>
          </Radio.Group>
        )}
        </Form.Item>       
        <Form.Item label="E-mail">
          {getFieldDecorator('email', {
            rules: [
              {
                type: 'email',
                message: 'The input is not valid E-mail!',
              },
              {
                required: true,
                message: 'Please input your E-mail!',
              },
            ],
            initialValue: this.state.data.email
          })(<Input />)}
        </Form.Item>
        <Form.Item
          label={
            <span>
              Residence Address&nbsp;
              <Tooltip title="No/ Street/ City.">
                <Icon type="question-circle-o" />
              </Tooltip>
            </span>
          }
        >
          {getFieldDecorator('residence', {
            rules: [{ message: 'Please input your nickname!', whitespace: true }],
            initialValue: this.state.data.residence
          })(<Input />)}
        </Form.Item>
       
        <Form.Item label="Contact Number">
          {getFieldDecorator('contact', {
            rules: [{ required: true, message: 'Please input contact number!' }],
            initialValue: this.state.data.contact
          })(<Input addonBefore="+94" style={{ width: '100%' }} />)}
        </Form.Item>

        <Form.Item label="Role">
          {getFieldDecorator('role', {
            rules: [{ required: true, message: 'Please input role!' }],
            initialValue: this.state.role_id 
          })(
            <Select>
              {this.state.roles.map(item => (
                  <Option key={item.role_id}> {item.role_id} {item.role_name}</Option>
              ))}
            </Select>
            )}
        </Form.Item>  

        <Form.Item label="Department">
        {getFieldDecorator('department', {
            rules: [{ required: true, message: 'Please input your department!' }],
            initialValue: this.state.department_id 
          })(
          <Select>
            {this.state.departments.map(item => (
                  <Option key={item.department_id}>{item.department_id} {item.department_name}</Option>
            ))}
          </Select>
          )}
        </Form.Item>  

        <Form.Item label="Supervisor 01">
          {getFieldDecorator('supervisor1', {
            rules: [{ required: true, message: 'Please input first supervisor!' }],
            initialValue: this.state.data.supervisor1
          })(
            <Select>
            {this.state.supervisors.map(item => (
                  <Option key={item.emp_id}>{item.first_name}</Option>
            ))}
          </Select>
          )}
        </Form.Item>

        <Form.Item label="Supervisor 02">
          {getFieldDecorator('supervisor2', {
            rules: [{ message: 'Please input second supervisor!' }],
            initialValue: this.state.data.supervisor2
          })(
            <Select>
            {this.state.supervisors.map(item => (
                  <Option key={item.emp_id}>{item.first_name}</Option>
            ))}
          </Select>
          )}
        </Form.Item>

        <Form.Item label="Joined date">
          <DatePicker onChange={this.onChangeJoin} format="YYYY-MM-DD" defaultValue={moment(update.join_date, "YYYY-MM-DD")}/>
        </Form.Item>

        <Form.Item label="Confirm date">
          <DatePicker onChange={this.onChangeConfirm} format="YYYY-MM-DD" defaultValue={moment(update.confirm_date, "YYYY-MM-DD")}/>
        </Form.Item> 
        
        <br/><br/>  
        <Row>
        <Col span={17}>  
        <Form.Item {...tailFormItemLayout}>
          <Button type="primary" htmlType="submit" style={{width:'100px', float:'right'}}>
          <Icon type="check-circle" /> 
            Submit
          </Button>
        </Form.Item>
        </Col> 

        <Col span={7}> 
        <Form.Item {...tailFormItemLayout}>
          <Button type="danger" onClick={this.props.close} style={{width:'100px', float:'left'}}>
          <Icon type="close-circle" /> 
            Cancel
          </Button>
        </Form.Item>
        </Col> 
        </Row>
      </Form>
      </Card>
      </div>
    );
  }
}

const WrappedAddEmployee = Form.create({ name: 'register' })(UpdateEmployee);

export default WrappedAddEmployee;
